css:
@-moz-document url-prefix("http://sto-forum.perfectworld.com") {
body {
background-color: #0066B3 !important;}
.main-background {
background-image:none !important;
}
ul {
list-style-type: disc !important;
margin-left:10px !important;
}
ul li ul {
list-style-type: square !important;
}
ul li ul li ul {
list-style-type: circle !important;
}
ol li {
margin-left:10px !important;
}
.contentwrap {
background-image:none !important;
margin-top: -190px !important;
}
.forum-index li.even {
background: #0033CC !important;
}
.forum-index li.odd {
background: #00477D !important;
}
div.topic a.title {
color: #BFE3FE !important;
}
.bg-info-content a {
color: #80C8FE !important;
}
div.bg-body-content {
background: #00248E !important;
}

.txt-title-header div, .txt-title-header h3 {
color: #809FFE !important;
font: bold 18px Arial !important;
text-decoration: underline !important;
}
.ads {
width: 1px  !important;
height: 1px !important;
}
div.txt-title-header, div.bg-header-content, div.bg-footer-content, .tcat, #forumtools, 

#forumsearch, #threadtools, #threadsearch, #displaymodes, #imod, .thead, .alt1, .alt1Active, 

.alt2, .alt2Active  {
background: #00248E !important;
}
.PW-postbit-message {
color: #AAAAAA !important;
}
#posts, .smallfont {
color: #80C8FE !important;
}
#threadslist {
color: #4488FF !important;
}
.tcat, .thead, .fieldset, #collapseobj_threadreview, #collapseobj_usercp_subthreads, .topic {
color: #BFE3FE !important;
}
.time, .pagenav,  {
color: yellow !important;
}
}
