css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("powdertoy.co.uk") {

/* This beautiful CSS-File has been crafted with LESS (lesscss.org) and compiled by simpLESS (wearekiss.com/simpless) */
/*
 * The Powder Toy style re-write in LESS.
 * Uses background from jenn4 Image = http://img88.imageshack.us/img88/843/backgroundnu.jpg , http://www.facebook.com/jenn4-art.
 * (c)2012 jenn4, jenn4-art. credit from original source and all the hard work for boxmein.
 *
 * To stylers: Warning, code is extremely beautiful and well formatted, reading it may cause high satisfaction!
 * To-do: write CSS? Anything more

*/
/* Constants */
/* Variable names pretty self-explaining or whatnot */
/*
//simpless:!minify
*/
/* Newest main page stuff */
.main-menu {
  width: 100%;
  height: 34px;
  background-color: #383838 !important;
  background-repeat: repeat-x;
  background-image: linear-gradient(bottom, #2f2f2f 0%, #222222 100%) !important;
  background-image: -o-linear-gradient(bottom, #2f2f2f 0%, #222222 100%) !important;
  background-image: -ms-linear-gradient(bottom, #2f2f2f 0%, #222222 100%) !important;
  background-image: -moz-linear-gradient(bottom, #2f2f2f 0%, #222222 100%) !important;
  background-image: -webkit-linear-gradient(bottom, #2f2f2f 0%, #222222 100%) !important;
  background-image: -webkit-gradient(linear, left bottom, left top, color-stop(0%, #2f2f2f), color-stop(100%, #222222)) !important;
  border: 1px solid #222222 !important;
  -webkit-border-radius: 4px;
  -moz-border-radius: 4px;
  border-radius: 4px;
  box-shadow: 0px 3px 27px rgba(0, 0, 0, 0.08);
}
.form-actions {
  background-color: #1e1e1e !important;
  -webkit-box-shadow: 0px 1px 0px #222222 inset !important;
  -moz-box-shadow: 0px 1px 0px #222222 inset !important;
  -ms-box-shadow: 0px 1px 0px #222222 inset !important;
  -o-box-shadow: 0px 1px 0px #222222 inset !important;
  box-shadow: 0px 1px 0px #222222 inset !important;
}
.outside-header, .breadcrumb {
  background-image: linear-gradient(bottom, #2f2f2f 0%, #222222 100%) !important;
  background-image: -o-linear-gradient(bottom, #2f2f2f 0%, #222222 100%) !important;
  background-image: -ms-linear-gradient(bottom, #2f2f2f 0%, #222222 100%) !important;
  background-image: -moz-linear-gradient(bottom, #2f2f2f 0%, #222222 100%) !important;
  background-image: -webkit-linear-gradient(bottom, #2f2f2f 0%, #222222 100%) !important;
  background-image: -webkit-gradient(linear, left bottom, left top, color-stop(0%, #2f2f2f), color-stop(100%, #222222)) !important;
}
.active a {
  background-image: linear-gradient(bottom, #484848 0%, #3c3c3c 100%) !important;
  background-image: -o-linear-gradient(bottom, #484848 0%, #3c3c3c 100%) !important;
  background-image: -ms-linear-gradient(bottom, #484848 0%, #3c3c3c 100%) !important;
  background-image: -moz-linear-gradient(bottom, #484848 0%, #3c3c3c 100%) !important;
  background-image: -webkit-linear-gradient(bottom, #484848 0%, #3c3c3c 100%) !important;
  background-image: -webkit-gradient(linear, left bottom, left top, color-stop(0%, #484848), color-stop(100%, #3c3c3c)) !important;
}
.disabled a, .breadcrumb, .outside-header {
  border-color: #222222 !important;
  -webkit-box-shadow: 0px 1px 0px #222222 inset !important;
  -moz-box-shadow: 0px 1px 0px #222222 inset !important;
  -ms-box-shadow: 0px 1px 0px #222222 inset !important;
  -o-box-shadow: 0px 1px 0px #222222 inset !important;
  box-shadow: 0px 1px 0px #222222 inset !important;
}
.btn-info, .btn {
  background-image: linear-gradient(bottom, #484848 0%, #3c3c3c 100%) !important;
  background-image: -o-linear-gradient(bottom, #484848 0%, #3c3c3c 100%) !important;
  background-image: -ms-linear-gradient(bottom, #484848 0%, #3c3c3c 100%) !important;
  background-image: -moz-linear-gradient(bottom, #484848 0%, #3c3c3c 100%) !important;
  background-image: -webkit-linear-gradient(bottom, #484848 0%, #3c3c3c 100%) !important;
  background-image: -webkit-gradient(linear, left bottom, left top, color-stop(0%, #484848), color-stop(100%, #3c3c3c)) !important;
  background-color: #484848 !important;
}
.alert-warning {
  background-color: #222222 !important;
  border-color: #222222 !important;
}
.btn-info:hover, .btn:hover {
  background-image: linear-gradient(bottom, #484848 0%, #3c3c3c 100%) !important;
  background-image: -o-linear-gradient(bottom, #484848 0%, #3c3c3c 100%) !important;
  background-image: -ms-linear-gradient(bottom, #484848 0%, #3c3c3c 100%) !important;
  background-image: -moz-linear-gradient(bottom, #484848 0%, #3c3c3c 100%) !important;
  background-image: -webkit-linear-gradient(bottom, #484848 0%, #3c3c3c 100%) !important;
  background-image: -webkit-gradient(linear, left bottom, left top, color-stop(0%, #484848), color-stop(100%, #3c3c3c)) !important;
}
.btn-group > .dropdown-toggle {
  background-image: linear-gradient(bottom, #484848 0%, #3c3c3c 100%) !important;
  background-image: -o-linear-gradient(bottom, #484848 0%, #3c3c3c 100%) !important;
  background-image: -ms-linear-gradient(bottom, #484848 0%, #3c3c3c 100%) !important;
  background-image: -moz-linear-gradient(bottom, #484848 0%, #3c3c3c 100%) !important;
  background-image: -webkit-linear-gradient(bottom, #484848 0%, #3c3c3c 100%) !important;
  background-image: -webkit-gradient(linear, left bottom, left top, color-stop(0%, #484848), color-stop(100%, #3c3c3c)) !important;
  background-color: #484848 !important;
}
.dropdown-menu {
  background-color: #383838 !important;
}
.main-menu a {
  background-color: none !important;
  border-color: #222222 !important;
}
.container,
.pagination ul a,
.btn-info,
.btn-group > .dropdown-toggle,
.form-actions {
  border-color: #222222 !important;
}
.main-menu li > a:hover {
  background-image: linear-gradient(bottom, #484848 0%, #3c3c3c 100%) !important;
  background-image: -o-linear-gradient(bottom, #484848 0%, #3c3c3c 100%) !important;
  background-image: -ms-linear-gradient(bottom, #484848 0%, #3c3c3c 100%) !important;
  background-image: -moz-linear-gradient(bottom, #484848 0%, #3c3c3c 100%) !important;
  background-image: -webkit-linear-gradient(bottom, #484848 0%, #3c3c3c 100%) !important;
  background-image: -webkit-gradient(linear, left bottom, left top, color-stop(0%, #484848), color-stop(100%, #3c3c3c)) !important;
}
html, body {
  background-image: url('http://img88.imageshack.us/img88/843/backgroundnu.jpg') !important;
  background-attachment: fixed !important;
}
body {
  color: #cf1e0a !important;
}
a {
  color: #96979a !important;
  -webkit-text-shadow: 1px 1px 1px #000000 !important;
  -moz-text-shadow: 1px 1px 1px #000000 !important;
  -ms-text-shadow: 1px 1px 1px #000000 !important;
  -o-text-shadow: 1px 1px 1px #000000 !important;
  text-shadow: 1px 1px 1px #000000 !important;
}
input, button, textarea {
  background-image: none !important;
}
a:hover {
  /* omg, addition! */

  color: #b8b9bc !important;
}
a.Link {
  color: #CCC !important;
}
/* Mixins */
.border {
  border: 1px solid #222222 !important;
}
/* Vendor prefix stuff, repetitive code [Remember to call with .something ()  so it gets the values OK]*/
input, textarea, .Tag {
  background-color: #404040 !important;
  color: #FFF !important;
  resize: vertical;
  border: 1px solid #222222 !important;
}
.Conversation {
  border: 1px solid #222222 !important;
  margin-bottom: -1px;
}
.Pageheader *, .Pageheader {
  text-shadow: none !important;
}
#PageheaderSearch input {
  border-bottom: none !important;
}
a.Item {
  -webkit-text-shadow: 2px 2px 3px rgba(0, 0, 0, 0.7) !important;
  -moz-text-shadow: 2px 2px 3px rgba(0, 0, 0, 0.7) !important;
  -ms-text-shadow: 2px 2px 3px rgba(0, 0, 0, 0.7) !important;
  -o-text-shadow: 2px 2px 3px rgba(0, 0, 0, 0.7) !important;
  text-shadow: 2px 2px 3px rgba(0, 0, 0, 0.7) !important;
}
.ScoreMeh {
  color: #cf1e0a !important;
}
.ButtonLink,
.fSaveDetails,
.GroupInfo,
.Pageheader,
.Pageheader *,
.PostForm,
.ButtonLink {
  background-color: #383838 !important;
  color: #96979a;
  background-image: none !important;
}
.CUnreadCount {
  background-color: #8b0000 !important;
}
.Item, .Active, .TopContainer {
  -webkit-text-shadow: 2px 2px 3px rgba(0, 0, 0, 0.7) !important;
  -moz-text-shadow: 2px 2px 3px rgba(0, 0, 0, 0.7) !important;
  -ms-text-shadow: 2px 2px 3px rgba(0, 0, 0, 0.7) !important;
  -o-text-shadow: 2px 2px 3px rgba(0, 0, 0, 0.7) !important;
  text-shadow: 2px 2px 3px rgba(0, 0, 0, 0.7) !important;
}
/* Awesome code tag vs pre tag thread test (Yay, this is official!) */
code, pre {
  background-color: #222222 !important;
  border: 1px solid #cccccc;
  white-space: pre;
  /* No wrap, saves spaces to correct level */

  padding: 1px 2px;
  margin: 1px;
}
code {
  display: block;
}
pre {
  display: inline;
  color: #FFF !important;
}
.modal-header, .modal-body, .modal-footer {
  background-color: #383838 !important;
}
/* Copyright and footer messages */
.Copyright, .FooterExtra, .Header li a {
  -webkit-text-shadow: 1px 1px 1px #000000 !important;
  -moz-text-shadow: 1px 1px 1px #000000 !important;
  -ms-text-shadow: 1px 1px 1px #000000 !important;
  -o-text-shadow: 1px 1px 1px #000000 !important;
  text-shadow: 1px 1px 1px #000000 !important;
}
/* Various warning / error messages */
.Warning, .Error, .Information {
  background-color: #222222 !important;
  border: none !important;
  margin-bottom: 5px;
}
.ParentCategory {
  color: #DDD !important;
}
.Item, .Active {
  -webkit-text-shadow: 2px 0px 3px rgba(0, 0, 0, 0.7) !important;
  -moz-text-shadow: 2px 0px 3px rgba(0, 0, 0, 0.7) !important;
  -ms-text-shadow: 2px 0px 3px rgba(0, 0, 0, 0.7) !important;
  -o-text-shadow: 2px 0px 3px rgba(0, 0, 0, 0.7) !important;
  text-shadow: 2px 0px 3px rgba(0, 0, 0, 0.7) !important;
}
.TopContainer {
  color: #CCC;
  -webkit-text-shadow: 2px 2px 3px rgba(0, 0, 0, 0.7) !important;
  -moz-text-shadow: 2px 2px 3px rgba(0, 0, 0, 0.7) !important;
  -ms-text-shadow: 2px 2px 3px rgba(0, 0, 0, 0.7) !important;
  -o-text-shadow: 2px 2px 3px rgba(0, 0, 0, 0.7) !important;
  text-shadow: 2px 2px 3px rgba(0, 0, 0, 0.7) !important;
}
/* Make block quotes not take up half the post screen */
blockquote {
  margin: -30px 10px;
}
/* Those "Unread" and "New" notices in Recents and categories */
.Meta span em {
  box-shadow: 0px 0px 5px #FFF !important;
  color: #FFF !important;
}
.Meta {
  border-color: #222222 !important;
}
div {
  text-shadow: 4px 4px 4px #210704 !important;
}
/* New */
a.Link,
.Download,
input.EditPlain,
.Meta em,
.Page,
.SaveDetails,
.Subpage,
.View *,
.container.contents {
  background-color: #383838 !important;
}
/* In forums perhaps */
li {
  background-color: #272727 !important;
}
/* Top links, eg "Download", "Forum", ... */
#Header li {
  background: none !important;
}
h1, .download-buttons {
  color: #FFF !important;
  font-weight: normal;
}
.MessageList {
  border-radius: 3px;
}
/* Forum post colors:  */
.Mine {
  background-color: #211204 !important;
}
.Developer {
  background-color: #1c2d2e !important;
}
.Moderator {
  background-color: #1c2e1c !important;
}
.Administrator {
  background-color: #1c1d2e !important;
}
.Op {
  background-color: #2e1c1c !important;
}
/* TinyMCE colours and stuff */
a.NPage,
.ConUnread,
.mceLayout,
.Pagefooter,
.mceToolbar,
.mceToolbar *,
#AddReplyMessage_ifr {
  background-color: #555 !important;
}
.MessageList,
.Subpage,
.Page,
.Pageheader,
li,
.Item,
.Active,
input,
a.Link,
.Download,
.RecentContainer,
.PostForm,
.Meta em,
ul,
.mceToolbar,
.mceToolbar *,
.Pagefooter,
.View * {
  border-color: #222 !important;
}
.mceLayout, #AddReplyMessage_ifr html, #AddReplyMessage_ifr p {
  background-image: none !important;
}









}
