css:
@namespace url(http://www.w3.org/1999/xhtml);







@-moz-document url-prefix(www.kafic.net), domain(www.kafic.net)



{







#main {



background: #BAD6FF  !important;



}


#container, .windows-basic-medium, .windows-basic-small, .windows-medium, .panel-basic,

#forum, .forum-post-body, .forum-user-column, .forum_post_right_footer, .forum_post_left_footer,

h3, #forum table, .codetop, .htmltop, .quotemain, .windows-basic,

.windows-small

{

	color:#000000 !important;

	background-color:#D4E5FF !important;

	border-color:#BAD6FF !important;
}

.forum-sub th

{

	color:#000000 !important;

	

	border-color:#BAD6FF !important;

}

.forum-post-column

{

	background-color:#D4E5FF !important;

	color: #000000 !important;

	

	font-size:12px !important;

	font-weight:600 !important;

	color:#000000;

}

.quotetop, #comments .top_bar

{

	border-color:#BAD6FF !important;

	

	font-size:12px !important;

	font-weight:600 !important;

	color: #000000;

}

.top_bar small

{

	color:#ffffff !important;
}

.quotemain

{

	border-color:#BAD6FF !important;

	border-left-width: 1px !important;

	background-color:#FFFFFF!important;

}

h1.title, h2.title, h3.title, h4.title, h5.title, h5, h1.title a:link, h2.title a:link,

h3.title a:link, h4.title a:link, h5.title a:link, h1.title a:visited, h2.title a:visited,

h3.title a:visited, h4.title a:visited, h5.title a:visited, h2 a

{

	color:#000000 !important;

}

.forum-sub th, .forum-latest-topic a

{

	color: #666666 !important;
}

.border_menu .icon a:hover, .border_menu .icon.current_link a, .border_menu .icon.current_link a:link, .border_menu .icon.current_link a:visited

{

		background-color:#ffffff !important;

}

.ul_list a

{

	color:#000000 !important;

	background-color:#D4E5FF !important;

	border: 1px solid #ffffff !important;

}

.ul_list a:hover, .ul_list .current_link a

{

	color:#000000 !important;

	background-color:#ffffff !important;
}

.table_info a:link, .table_info a:visited

{

	color:#000000 !important;

}


#row2 {

width: 997px !important;

}

#row1 {

width: 997px !important;

}

#row3 {

width: 997px !important;

}

#container{

float: left !important;

}

#footer {

float: left !important;

}

#live_online {

float: left !important;


}



a:link.user_male, a:visited.user_male



{



	color:#3399FF !important;



}



a:link.user_female, a:visited.user_female



{



	color:#FF3333 !important;


}

#contacts_content {

background: #D4E5FF !important;
}

.bordit

{

	border:1px solid #BAD6FF  !important;

	padding:1px !important;
}

.border_menu div, .border_div, tr.border_div td

{

	border-bottom: 1px solid #ffffff  !important; 

}

.panel-basic .option

{

	background-color:#D4E5FF !important;

	border:1px #BAD6FF solid !important;

}

#blog_panel h2, #blog_panel h3, #blog_panel a, .panel-basic h4

{

	color:#000000 !important;

}

.forum-latest-topic h2

{

	font-size:14px !important;

	font-weight:600 !important;

}

#context_menu{

	margin: 0 !important; 

	border-left:1px solid #D4E5FF  !important;

	border-right:2px solid #BAD6FF !important;

	border-bottom:2px solid #BAD6FF !important;

	border-top:1px solid #D4E5FF  !important;

	background-color:#D4E5FF !important;

/*	padding:3px;	*/

}

#broadcast_container

{

	font-size: 12px !important;

	font-weight:600 !important;

	background-color:#BAD6FF !important;

	color: #ffffff !important;
}
.windows-basic h5 
{
background-color:#D4E5FF !important;




}

}
