#navAccount ul a:hover, #navAccount ul a:focus, #navAccount ul a:active, #navAccount .logoutButton:hover input, #navAccount .logoutButton input:active, #navAccount .logoutButton input:focus{background-color: #6d84b4!important;color:#fff!important;}
ul #navAccountInfo a:hover, ul #navAccountInfo a:focus, ul #navAccountInfo a:active{background:none!important;color:#3b59a4!important;}
.pas{background:#e9e9e9;border:1px solid #b3b3b3;border-right:0;border-radius:3px 0 0 3px;text-shadow:none;}
