css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bandcamp.com") {
	/*highlight current song*/
	tr.track_row_view.current_track { border: 1px solid currentColor ;}
	tr.track_row_view.current_track span[itemprop="name"] {font-size: larger;}
	
	/*style visited links*/
	.ipCellSet :visited:not(:hover),
	.trackTitle :visited:not(:hover),
	.track_row_view :visited:not(:hover),
	.item :visited:not(:hover),
	.collection-item-container :visited:not(:hover),
	a:visited > p.title
	{color: DarkViolet !important;  }
}
