css:
#content #history .journal:not(.has-notes) { height: 21px; overflow: hidden; }
#content #history .journal:not(.has-notes):hover { height: auto; overflow: none; }
#content #history .journal:not(.has-notes) .gravatar { display: none }
#content #history .journal:not(.has-notes) h4 { margin: 0; padding: 4px; font-size: 10px !important; }
#content #history .journal:not(.has-notes) { font-size: 10px; }
#content #history .journal:not(.has-notes) ul { padding-left: 0; margin-left: 0; }
#content #history .journal:not(.has-notes) li { display: inline-block; list-style-type: none; margin-left: 5px; padding-left: 5px; border-left: 1px solid #999; }
#content #history .journal ul.details { font-size: 9px; }
