css:
@-moz-document url-prefix("http://www.phpbbitalia.net") {
#phpbb-sub-header { box-shadow: 1px 1px 8px black; }
#wrap { margin-top: 0px !important; padding-top: 0px !important; border-radius: 0 0 6px 6px !important; box-shadow: 0px 1px 8px rgba(0,0,0,0.5)}
.navbar { padding-top: 20px !important; }
.navbar .corners-top { display: none !important; }
#phpbb { 
background: #aca8a1; 
background: -moz-linear-gradient(left,  #aca8a1 0%, #898989 100%); 
background: -webkit-gradient(linear, left top, right top, color-stop(0%,#aca8a1), color-stop(100%,#898989)); 
background: -webkit-linear-gradient(left,  #aca8a1 0%,#898989 100%); 
background: -o-linear-gradient(left,  #aca8a1 0%,#898989 100%); 
background: -ms-linear-gradient(left,  #aca8a1 0%,#898989 100%); 
background: linear-gradient(left,  #aca8a1 0%,#898989 100%); 
filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#aca8a1', endColorstr='#898989',GradientType=1 ); 
}

#phpbb-header-bar .active a,#phpbb-header-bar li:not(.phpbb-logo) a:hover  { position: relative; z-index: 10; }

#phpbb-header-bar li > a { 
  -webkit-transition: all 200ms linear;
  -moz-transition: all 200ms linear;
  -ms-transition: all 200ms linear;
  -o-transition: all 200ms linear;
  transition: all 200ms linear; 
}
}
