@-moz-document domain(facebook.com){
	#pagelet_bluebar {
		height: 80px !important; 
	}
	#blueBar, .canHaveFixedElements body.timelineLayout #blueBar {
		padding-top: 30px !important;
		background: #000000 !important;
		border-bottom: 0px !important;
		width: 100% !important;
	}
	#pageHead, .sidebarMode .fbx #pageHead, .sidebarMode #blueBar #pageHead,.fbx #pageHead, #blueBar #pageHead {
		background: #F1F1F1 !important;
		height: 45px !important;
		color: #000000 !important;
		left: 0px !important;
		width: auto !important;
		padding-top: 15px !important;
		padding-left: 20px !important;
		padding-right: 20px !important;
		
		margin-left: 0px !important;
		left: 0px;
	}
		#pageHead  #pageLogo {
			margin-top: 4px !important;
			margin-left: -5px !important;
		}
			#pageHead  #pageLogo a {
				background-color: #3B5998 !important;
				width: 180px !important;
				height: 30px !important;
			}
		#pageHead .uiTypeahead {
			border: 0px solid #C8C8C8 !important;
			background: #F1F1F1 !important;
			left: 22px !important;
			width: 800px !important;
		}
		#pageHead .uiTypeahead input {
			font: 16px arial,sans-serif !important;
			border: 1px solid #C8C8C8 !important;
			background-color: #ffffff !important;
			height: 25px !important;
			width: 508px !important;
		}
		#pageHead .uiTypeahead button {
			top: 1px !important;
			right: -60px !important;
			-moz-border-radius:4px !important;
			-webkit-border-radius:4px !important;
			border-radius:2px !important;
			border:1px solid #469df5 !important;
			display:inline-block !important;
			color:#ffffff !important;
			height: 30px !important;
			width: 70px !important;

			background:-webkit-gradient( linear, left top, left bottom, color-stop(0.05, #2992fc), color-stop(1, #3075ba) ) !important;
			background:-moz-linear-gradient( center top, #2992fc 5%, #3075ba 100% ) !important;
			filter:progid:DXImageTransform.Microsoft.gradient(startColorstr='#2992fc', endColorstr='#3075ba') !important;
			background-color:#2992fc !important;

			background-image: url(http://www.clker.com/cliparts/o/1/Y/9/T/a/lupe-in-white-md.png) !important;
			background-position: center center !important;
			background-repeat:no-repeat !important;
			background-size: 15px 15px !important;
		}
		
		#pageHead  .uiSearchInput {
			border-top: 0px solid #D9D9D9 !important;
		}
		
		#pageHead #jewelContainer {
			position: absolute !important;
			/*top: 20px !important;*/
			right: 50px !important;
		}
			#pageHead #jewelContainer .jewelButton:hover {
				background-color: #ffffff !important;
			}
			.sidebarMode #pageHead #jewelContainer {
				right: 280px !important;
			}
				.notifNegativeBase #fbMessagesFlyout {
					left: -250px !important;
				}
				.notifNegativeBase #fbMessagesFlyout .beeperNub {
					left: 252px !important;
				}
				
				.notifNegativeBase #fbNotificationsFlyout {
					left: -275px !important;
				}
				.notifNegativeBase #fbNotificationsFlyout .beeperNub {
					left: 277px !important;
				}
				
				.notifNegativeBase #fbRequestsFlyout {
					left: -225px !important;
				}
				.notifNegativeBase #fbRequestsFlyout .beeperNub {
					left: 227px !important;
				}
		
		#pageNav {
			position: absolute !important;
			top: 0px !important;
			left: 15px !important;

		}
		#pageNav a:hover {
			color: #F1F1F1 !important;
		}
			#userNavigation {
				left: -2px !important;
			}
			
	/* ... Content Spalte ... */
	body {
		background: #F1F1F1 !important;
	}
	
	#mainContainer {
		padding-top: 15px !important;
		border-right: 0px solid #cccccc !important;
		min-width: 1020px !important;
	}
		.sidebarMode #mainContainer {
			margin-right: 250px !important;
		}
	#content {
		min-width: 1020px !important;
		margin-right: 20px !important;
	}
		.sidebarMode #content {
			margin-right: 250px !important;
		}
	.fbx #globalContainer {
		background: #F1F1F1 !important;
		left: 0px !important;
		position: static !important;
		width: 100% !important;
		margin-left: 0px !important;
	}
	.sidebarMode .fbx #globalContainer {
		margin-left: 0px !important;
	}
	#contentCol {
		background-color: #FDFDFD !important;
		border: 1px solid #cccccc !important;
		-moz-border-radius:8px !important;
		-webkit-border-radius:8px !important;
		border-radius:8px !important;
		margin-left: 210px !important;
		position: relative !important;
	}
	/*
	#rightCol {
		position: absolute !important;
		right: 20px !important;
		float: none !important;
		clear: both !important;
	}
	*/
	.rightColumnWrapper {
		top: 90px !important;
	}
	/* ... Menü Left ... */
	#leftColContainer {
		padding-left: 15px !important;
		padding-right: 10px !important;
	}
		#leftColContainer .uiSideNav  li {
			padding-top: 3px !important;
			padding-bottom: 3px !important;
			border-bottom: 1px solid #ffffff !important;
		}
		.uiSideNav .item, .uiSideNav .subitem {
			border-bottom: none!important;
		}

	/* ... Sidebar (Chat, etc.) ... */
	#pagelet_sidebar {

	}
	
	.fbChatSidebar {
		width: 210px !important;
		/*
		top: 10px !important;
		bottom: 60px !important;
		right: 20px !important;
		*/
		border: 1px solid #cccccc !important;
		-moz-border-radius:2px !important;
		-webkit-border-radius:2px !important;
		border-radius:2px !important;
		z-index: 300 !important;
		margin-right: 20px !important;
	}
	.fbChatSidebarBody {
		margin-top: 43px;
		border-top: 1px solid #cccccc !important;
		background-color: #F9F9F9 !important;
		background-color: #ffffff !important;

	}
	.fbChatSidebarFooter {
		top: 8px !important;
		margin-left: 10px !important;
		margin-right: 10px !important;
		border: 1px solid #cccccc !important;
		background-color: #ffffff !important;
	}

	.fbChatSidebarFooter  input {
		/*position: fixed !important;
		top: 110px !important;
		right: 30px !important;
		width: 190px !important;*/
		border: 0px solid #C8C8C8 !important;
		background-color: #ffffff !important;
	}
	.uiSelectorBottomUp .uiSelectorMenuWrapper {
		top: 25px !important;
	}
	.uiScrollableAreaBody {
		min-width: 210px !important;

	}
	.fbChatOrderedList {
		padding-top: 0px !important;
	}
	.fbChatOrderedList .item {
		padding-top: 5px !important;
		padding-bottom: 5px !important;
		border-bottom: 1px solid #C8C8C8 !important;
	}

	
	.sidebarMode .fbDockWrapper {
		right: 260px !important;
	}

	.fbNubFlyoutTitlebar {
		background: #F1F1F1 !important;
		border-top: 1px solid #C8C8C8 !important;
		border-left: 1px solid #C8C8C8 !important;
		border-right: 1px solid #C8C8C8 !important;
	}
	.titlebarText {
		color: #000000 !important;
	}
	
	/* ... Post ... */
	.storyContent  {
		margin-top: 20px !important;
		margin-bottom: 20px !important;
	}
	.storyInnerContent {
		position: relative;
		border: 1px solid #cccccc !important;
		-moz-border-radius:4px !important;
		-webkit-border-radius:4px !important;
		border-radius:4px !important;
		padding-left: 10px !important;

	}
	.uiStreamFooter {
		display: block  !important;
		position: relative !important;
		border-top: 1px solid #cccccc !important;
		margin-bottom: 10px !important;
		padding-top: 10px !important;
		padding-left: 20px !important;
		padding-right: 20px !important;
		margin-left: -10px !important;
		margin-right: -10px !important;
	}
	.storyInnerContent .uiList {
		width: auto !important;
		border: 0px solid #CCC !important;
	}
	.storyInnerContent .uiUfi {
		width: auto !important;
		background: #F8F8F8 !important;
		border: 1px solid #cccccc !important;
		-moz-border-radius:4px !important;
		-webkit-border-radius:4px !important;
		border-radius:4px !important;
		margin-left: -8px !important;
		margin-right: -8px !important;
		float: left;
		clear: both;
	}
	.uiUfi .ufiItem {
		background: #F8F8F8 !important;
	}
	.storyInnerContent .photoRedesign {
		margin-left: 0px;
	}
	.storyInnerContent .photoRedesign img {
		min-width: 449px !important;
		height: auto !important;
		margin-left: -10px !important;
	}
	.uiStreamSubstory  .uiStreamFooter {
		margin-right: 0px !important;
	}
	.uiStreamSubstory  .uiUfi {
		margin-top: -5px !important;
		margin-right: 3px !important;
	}
	.uiStreamStory .uiStreamSubstory .photoRedesign {
		width: 100% !important;
	}
	.uiStreamStory .photoRedesignAspect::after, .uiStreamStory .photoRedesignCover::after, .uiStreamStory .photoRedesignLink .uiScaledImageContainer::after {
		border: 0px solid #cccccc !important;
	}
	.uiStreamStory .uiStreamSubstory .photoRedesignSquare .photoWrap {
		border: 0px solid #cccccc !important;
		min-width: 426px !important;
		margin-left: -10px !important;
		margin-right: -10px !important;
	}
	.uiStreamEdgeStoryLine {
		display: none !important;
	}
	
	.uiListVerticalItemBorder {
		border-top: 0px !important;
	}
	.uiStreamStory .mainWrapper {
		margin-right: 10px !important;
	}
	
	.hasLeftCol #headerArea  {
		width: auto !important;
		margin-top: 60px !important;
	}
	
	/* ... Timeline ... 
	.timelineLayout #contentCol {
		margin-left: 20px !important;
	}
	.timelineLayout #contentArea {
		left: 50% !important;
		margin-left: -425px !important;
	}*/
	.fbTimelineTopSection {
		margin-top: 5px !important;
	}
	.fbTimelineSection {
		border: 0px !important;
	}
	.timelineLayout .cover {
		padding-top: 57px !important;
	}
	.fbTimelineTopSectionBase i.topSectionBottomBorder {
		display: none !important;
	}
	.timelineLayout  #fbTimelineHeadline {
		position: absolute !important;
		top: 0px !important;
		border-bottom: 1px solid #cccccc !important;
		height: 60px !important;
	}
	.timelineLayout  .photoContainer {
		position: absolute !important;
		top: 210px !important;
		right: 300px !important;
	}
	.fbProfileCoverLarge #fbTimelineHeadline .profilePicThumbLarge .profilePic {
		width: 250px !important;
		height: 250px !important;
	}
	#fbProfileCover.fbProfileCoverLarge .fbTimelineProfilePicSelector {
		left: 90px !important;
		top: 0px !important;
	}

	#fbTimelineHeadline h2 {
		margin-top: 20px !important;
		margin-left: 20px !important;
		color: #222 !important;
		font: normal 13px arial,sans-serif !important;
		font-size: 21px !important;
		font-weight: normal !important;
	}
	.timelineLayout #rightCol {
		margin-right: 5px !important;
		width: 90px !important;
	}
	.fbTimelineScrubber {
		width: 90px !important;
		position: static !important;
	}
	.safari4 #globalContainer .fixed_elem.fbTimelineScrubber {
		left: 0px !important;
		margin-left: 0px !important;
	}
	.sidebarMode .safari4 #globalContainer .fixed_elem.fbTimelineScrubber {
		margin-left: 0px !important;
	}
	
	/* ... Footer ... */
	.hasLeftCol #pageFooter {
		margin-top: 10px !important;
		margin-left: 10px !important;
		margin-right: 20px !important;
		background: #F1F1F1 !important;
	}
	#pageFooter #contentCurve {
		display: none !important;
	}
	
	.sidebarMode .hasLeftCol #pageFooter {
		margin-right: 250px !important;
	}
	
	/* ... Nachrichten ... */
	.sidebarMode body.safari4 .fixedScrolling, .sidebarMode .safari4.fixedBody #leftCol, .sidebarMode .safari4.fixedBody .MessagingReadHeader, .sidebarMode .safari4.fixedBody .MessagingContentHeaderWrapper, .sidebarMode .safari4.fixedBody .fbCalendarHeader, .sidebarMode .safari4.fixedBody .fbTimelineMapHeader {
		position: static !important;
		padding: 0px !important;
		margin: 0px !important;
		padding-top: 20px !important;
		position: fixed !important;
		margin-top: -21px !important;
	}
	.fixedBody .MessagingContentWrapper {
		margin-top: 0px !important;
	}
	.uiBoxGray {
		border-top: 1px solid #cccccc !important;
		background: #ffffff !important;
		padding-top: 10px !important;
	}
	
	.fbEventHeaderBlock a div.fbEventHeadline  {
		top: 0px !important;
		color: #222 !important;
		font: normal 13px arial,sans-serif !important;
		font-size: 21px !important;
		font-weight: normal !important;
		margin-top: 20px !important;
		margin-left: -20px !important;
		margin-right: 20px !important;
		padding-left: 20px !important;
		width: 100% !important;
		border-bottom: 1px solid #cccccc !important;
	}
	.ticker .hasLeftCol #headerArea {
		color: #222 !important;
		font: normal 13px arial,sans-serif !important;
		font-size: 21px !important;
		font-weight: normal !important;
		width: 100% !important;
		margin-top: 0px !important;
	}
	
	/* ... Veranstaltungen ... */
	#pagelet_event_header {
		margin-right: 20px !important;
	}
	.fbEventHeader .fbEventHeadline {
		width: 100% !important;
		max-width: 100% !important;
		margin-right: 20px !important;
	}
	
	.fbEventHeader .fbEventButtons .uiButtonGroup {
		border: 0px solid #cccccc !important;
		margin-top: 1px !important;
		background-color: none !important;
		border: 0px solid #cccccc !important;
	}
	
	#pagelet_best_apps {
		margin-right: 20px !important;
	}
	
	/* ... Gruppen ... */
	#mainContainer .groupJumpLayout div#headerArea {
		width: 100% !important;
		margin-right: 20px !important;
		font-size: 11px !important;
	}
	.groupJumpLayout .groupsCoverPhoto{
		margin-right: 20px !important;
	}
	.groupsJumpBarTop {
		margin-right: 20px !important;
	}
	
	
	/* ... Elemente ausblenden ... */

	#pagelet_ego_pane_w, #pagelet_ticker, .fbSidebarGripper, #pagelet_ego_pane, .fbTimelineSideAds {
		display: none;
	}
}
