css:
@-moz-document domain("developer.android.com") {

* {
    border-color: #657b83 !important;
    outline-color: #657b83 !important;
    color: #839496 !important;
    text-shadow: none !important;
    -moz-border-radius: 0 !important;
    -webkit-border-radius: 0 !important;
    -ms-border-radius: 0 !important;
    border-radius: 0 !important;
    -moz-box-shadow: none !important;
    -webkit-box-shadow: none !important;
    -ms-box-shadow: none !important;
    box-shadow: none !important;
}

html,body, select>option:hover, #side-nav, #devdoc-nav, .ui-resizable-handle, tr:nth-child(odd)>td, tr:nth-child(odd)>th, .jd-details-title {
    background: #073642 !important;
}

#header, .button, .home-sections>ul>li, select, select>option, #body-content, div.selected, li.selected, div.selected>a, li.selected>a, th, td {
    background: #002b36 !important;
}
#body-content {
    border-top: solid 2px #073642 !important;
}

.jspVerticalBar, .jspHorizontalBar, pre {
    background: transparent !important;
}

.morehover>.top {
    background-image: url("data:image/png;base64,") !important;
}
.morehover>.mid {
    background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAQwAAAABCAYAAAArZZD5AAAAL0lEQVR42u3UsQkAMAzEQIMbd99nhfTefzNHhozxgltBQYmCcOL2mJmtfQL0H5EPtg1l+HfPev8AAAAASUVORK5CYII=") !important;
}
.logo>a {
    background: url("data:image/png;base64,") !important;
}
.logo>a>img {
    opacity: 0;
    -ms-opacity: 0;
}

#header-wrap, .slideshow-container, .content-footer, #nav-x>.wrap, th, td, table {
    border: none !important;
}

#devdoc-nav>.jspContainer>.jspPane {
    right: 0 !important;
}

::selection {
	background: #fdf6e3 !important;
	color: #839496 !important;
}
::-moz-selection {
	background: #fdf6e3 !important;
	color: #839496 !important;
}
::-webkit-selection {
	background: #fdf6e3 !important;
	color: #839496 !important;
}
:link {
    color: #268bd2 !important;
}
:visited {
    color: #6c71c4 !important;
}

p code, p tt {
    color: #6c71c4 !important;
}
pre {
    border-color: #6c71c4 !important;
}

}

@-moz-document url("http://developer.android.com"),url("http://developer.android.com/index.html"),
               url("https://developer.android.com"),url("https://developer.android.com/index.html"){
#body-content {
    background: #073642 !important;
}
}
