css:
@-moz-document domain("github.com") {

* {
    border-color: #657b83 !important;
    outline-color: #657b83 !important;
    color: #839496 !important;
    text-shadow: none !important;
    -moz-border-radius: 0 !important;
    -webkit-border-radius: 0 !important;
    -ms-border-radius: 0 !important;
    border-radius: 0 !important;
}

html,body, .tabs, .participation-graph, .minibutton, .repo_list, .tipsy, .url-box>ul>li, thead, .tree-browser tr.alt, #readme, .subnav-bar .subnav .selected, .subnav-bar .subnav a:hover, .upper_footer {
    background: #073642 !important;
}

#header, .highlight, .repo_list li, .tabs .selected, .file, .file .meta, .file .data, #repo_details, .tabs>li>a:hover, .url-box>ul>.selected, .commit, .frame, .bubble, .tree-browser, .selected, #readme article, #readme .name, .subnav-bar, .last-commit, .breadcrumb, .lower_footer {
    background: #002b36 !important;
}

#repo_details {
    margin-bottom: 0 !important;
    padding-bottom: 1em !important;
}
.subnav-bar {
    margin-top: 0 !important;
    padding-top: 1em !important;
}

.last-commit, .breadcrumb {
    margin-bottom: 0 !important;
    display: inline-block !important;
    padding: .5em .5em 0 .5em !important;
}
.commit-tease {
    margin-top: 0 !important;
}

#readme {
    padding: .5em 0 !important;
}
#readme .name {
    display: inline-block !important;
}

.http_clone_url>a, .public_clone_url>a {
    border-right: none !important;
}
.url-box, .participation {
    border-top: none !important;
}

p code, p tt {
    color: #6c71c4 !important;
}
pre {
    border-color: #6c71c4 !important;
}

.switcher {
    margin-left: 2px !important;
}

#repo_details, p tt, .participation-graph, .body, .repo_list li, .file, .filter-bar, .meta, .tabs>li, .tabs, .tabs>li>a, .minibutton span, .tree-browser, .tree-browser-wrapper, .tree-browser td, .tree-browser th, .commit, .commit-meta, #header, #readme .name, #readme article, #readme, .subnav-bar, .subnav>li>a, .counter, .social-count, .social-count, .counter, code, .lower_footer {
    border: none !important;
}

.minibutton, p tt, .switcher, input, .body, .repo-stats, .filter-bar, .participation, .minibutton span, .url-box>ul>li>a, .commit-meta, thead, th, .tree-browser td, .counter, .social-count, code, pre {
    background: transparent none !important;
}

.minibutton span:after, .tipsy:before, .tipsy:after, .social-count:before, .social-count:after {
    display: none !important;
}

::selection {
	background: #fdf6e3 !important;
	color: #839496 !important;
}
::-moz-selection {
	background: #fdf6e3 !important;
	color: #839496 !important;
}
::-webkit-selection {
	background: #fdf6e3 !important;
	color: #839496 !important;
}
:link {
    color: #268bd2 !important;
}
:visited {
    color: #6c71c4 !important;
}

.github-logo-4x {
    display: none !important;
}
.github-logo-4x-hover {
    display: block !important;
    opacity: 1 !important;
    -ms-opacity: 1 !important;
}
.filter_input {
    border: solid 1px !important;
}

}
