css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("drudgereport.com"), url-prefix("http://www.drudgereportarchives.com") {

         hr {
		border: none !important;
		border-top: 1px dotted #bbb !important;
		border-bottom: 1px dotted #fff !important;
		color: #F4F4F4 !important;
		background-color: #F4F4F4 !important;
		margin: 7px 42px !important;
        }

        a:hover {
		color: red !important;
		background-color: #000000 !important;
	}
	
	

	table {                
		margin-top: -15px !important;
	}

	font[size="-5"] {
                display: none !important;
	}

	font[size="+7"] {
                font-size: 30px !important;
	}

	td font[size="-5"] {
		display: none !important;
	}

table[width="97%"] {
                
                padding-top: 10px !important;
                padding-bottom: 10px !important;
        }


table[width="80%"] {
                
                padding-top: 10px !important;
                padding-bottom: 10px !important;
        }

img[src*="logo9.gif"] {
                height: 134px !important;
                width: 0px !important;
                padding-left: 625px !important;
                padding-right: 0px !important;
                padding-top: 0px !important;
                padding-bottom: 0px !important;
                background-image: url("data:image/jpeg;base64,%3D%3D") !important;
                background-position: center center !important;
                background-repeat: no-repeat !important;
                overflow: hidden !important;
        }


}
