css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("bkrs.info") {

body{
    background: -moz-linear-gradient(top, #333, #333, #333) #000 no-repeat !important;
    background-size:100%!important;
    background-repeat:no-repeat!important;
    background-attachment:fixed!important;
    font-family: "constantia"!important;}

a{color: #CCCCCC !important;}

a:hover, a:visited:hover, a:hover *, span[onclick]:hover, div[onclick]:hover, [role="link"]:hover, [role="link"]:hover *, [role="button"]:hover, [role="button"]:hover *, [role="menuitem"]:hover, [role="menuitem"]:hover *, .link:hover, .link:hover *, .sn-link:hover, .gac_b:hover *, .google-ac-b *, .yt-suggest-selected *, .yt-uix-button-menu-item:hover, .acl div.sug .sel *, .acl div.sug .sel, .ac_active, .ac_active *, .yui-ac-bd li:hover {
 color: #999999 !important; text-shadow: 0 0 10px #999967 !important;
text-decoration: none!important}



input *, input, select {color: #cccccc  !important}

textarea {-moz-appearance: none !important; background: rgba(0,0,0,.3) !important; -moz-border-radius: 3px !important; border: 1px solid #666666 !important; -moz-box-shadow: inset 0 0 8px #555555 !important}
textarea:focus {background: rgba(0,0,0,.5) !important}
textarea, textarea * {color: #555555 !important; text-shadow: 0 0 1px gray !important}
textarea:focus, textarea:focus > * {text-shadow: none !important; -moz-box-shadow: none !important}

body, html, div, h1, h2, h3, h4, h5, h6, table, tbody, thead, tr, td, th, ul, li, p, i, dd, dt, strong, code, iframe, embed, section, blockquote, label, form, pre, dl, img {color: #999 !important; text-shadow: none !important; background-color: transparent !important; border-color: #444 !important; -moz-border-top-colors: #444 !important; -moz-border-bottom-colors: #444 !important; -moz-border-left-colors: #444 !important; -moz-border-right-colors: #444 !important}
    


div#MarketGid5190 {display: none !important;}
div.sideblock2 {display: none !important;}}
