css:
/* Blueberry theme by NiGHTS */
/*Contact: Karamell@kinapuff.com*/
/*Last updated: 2012-06-30  Version: 0.5.5*/
@-moz-document domain("forums.gametrailers.com") {
/*Body/Background/Main*/
/*unofficial 0.6 changes */
.header_promo {display:none!important}
#copyright {visibility:hidden;padding-bottom: 20px!important}
#wrapper {margin-top: 48px!important}
.global_footer {display:none!important}
/*body {background-image: url(http://kinapuff.com/images/gametrailers/testback3.png), url(http://kinapuff.com/images/gametrailers/testback4.png)!important; background-position: 0% 0%, 100% 0%!important;background-repeat:repeat-y!important; background-color: #daebf5!important}*/
body {background:#001b2f url(http://kinapuff.com/images/gametrailers/bg.png)no-repeat scroll center center!important}



.icon_facebook {pointer-events: none;cursor: default;background-position: -999px -999px!important}
.icon_twitter {pointer-events: none;cursor: default;background-position: -999px -999px!important}
.icon_google_plus {pointer-events: none;cursor: default;background-position: -999px -999px!important}
.share_buttons {background: url(http://kinapuff.com/images/gametrailers/blueberry0.5.png) repeat-y!important}
.activity_bar {margin: -10px 0!important}
/*Forum wrapper section*/
.forums-wrapper {background: url(data:image/png;base64,)!important}
/*.forums*/
.forums .bg1 .bg2 {background: url(data:image/png;base64,);background-color: #f0f6fc!important}
.forums .bg1 {background: url(data:image/png;base64,);background-color: #FFFFFF!important}
.forums .bg2 {background: url(data:image/png;base64,);background-color: #FFFFFF!important}
.forums .bg2 .bg2 {background: url(data:image/png;base64,);background-color: #f0f6fc!important}
.row.bg2.announce {background-color: #FFFFFF!important}
.row.bg1.announce {background-color: #FFFFFF!important}
.row.bg1.sticky {background: url(data:image/png;base64,);background-color: #FFFFFF!important}
.row.bg2.sticky {background: url(data:image/png;base64,);background-color: #FFFFFF!important}
.row.bg2.sticky .topic, .row.bg2.sticky .bg2 {background: url(data:image/png;base64,);background-color: #f0f6fc!important}
.row.bg1.sticky .topic, .row.bg1.sticky .bg2 {background: url(data:image/png;base64,);background-color: #f0f6fc!important}
.icon{border-right: 1px solid #e0e0e0; background-position: center center!important;background-repeat:no-repeat!important}
.inner {background: #FFFFFF!important}
.panel {background: #FFFFFF!important}
/*Dashboard*/
#dashboard li a {border-top-left-radius: 2px; border-top-right-radius: 2px; border-bottom-left-radius: 2px; border-bottom-right-radius: 2px;font-size: 1em!important;background: url(data:image/png;base64,) repeat-x scroll 0 0 transparent!important}
#dashboard li a:hover, #dashboard li a:focus {background: url(data:image/png;base64,) repeat-x scroll 0 100% transparent!important}
/*Widget*/
.activityListContainer {height: 700px!important}
.flux4Widget .userBar .persistentTop .backgroundPanel, .flux4Widget .userBar .persistentBottom .backgroundPanel, .flux4Widget .userBar .expander {background: url(data:image/png;base64,) repeat-x!important}
/*Thread*/
.post {border-left: 1px solid;border-right: 1px solid; border-color: #cdcdcd!important}
.post-footer {display:none!important}
.post-wrapper.bg1 {border-top-left-radius: 4px; border-top-right-radius: 4px;margin-top: 2px!important;background: url(data:image/png;base64,) repeat-x #e9f0f8!important}
.post-wrapper.bg2 {border-top-left-radius: 4px; border-top-right-radius: 4px;margin-top: 2px!important;background: url(data:image/png;base64,) repeat-x #e9f0f8!important}
.post.forum, .user .post {background: url(data:image/png;base64,) repeat-x!important}
.subject a:link{color: #e5f6ff!important; font-size: 12px!important}
.subject a:visited {color: #e5f6ff!important}
.post-header {border: none!important}
.postbody .content img{max-width: 100%!important}
.content blockquote {border-top-left-radius: 2px; border-top-right-radius: 2px; border-bottom-left-radius: 2px; border-bottom-right-radius: 2px;background: #dce5ff!important;padding: 3px 3px 3px 3px!important; border: 1px solid #ababab!important}
.content blockquote blockquote {background: #d3dcf5!important}
.content blockquote cite {font-size: 13px!important; margin-left: 0px!important}
.signature blockquote  {border-top-left-radius: 2px; border-top-right-radius: 2px; border-bottom-left-radius: 2px; border-bottom-right-radius: 2px;background: #dce5ff!important;padding: 3px 3px 3px 3px!important; border: 1px solid #ababab!important}
.signature blockquote cite {font-size: 13px!important; margin-left: 0px!important}
.online img{padding:6px 6px 6px 6px!important}
.pm-icon a{background: url(http://forums.gametrailers.com/styles/gt/theme/images/icon_sendemail.gif) no-repeat scroll center!important}
.pm-icon{background: url(http://forums.gametrailers.com/styles/gt/theme/images/icon_sendemail.gif) no-repeat scroll center!important}
.profile-icons {padding-left: 4px!important}
.avatar {padding-top: 15px!important}
.post-wrapper {border-bottom: 2px solid #CDCDCD!important}
/*Text*/
#navigation #active-subsection a {color: #145a7e!important}
postlink:hover {color: #145a7e!important}
a:hover, a:focus {color: #145a7e!important}
#tabs a:hover span {color: #145a7e!important}
#dashboard li a:hover, #dashboard li a:focus {color: #efefef!important}
.content h2, .panel h2 {text-align:center!important}
.content-wrapper .notice {border-top-left-radius: 2px; border-top-right-radius: 2px; border-bottom-left-radius: 2px; border-bottom-right-radius: 2px;background-color: #d9d8e2!important; border-color: #cdcdcd!important}
/*Buttons*/
.edit-icon, .edit-icon a {background-image: url(data:image/png;base64,)!important}
.quote-icon, .quote-icon a {background-image: url(data:image/png;base64,)!important}

/*0.5 changes*/
.right-box, up{display:none!important}
.content-wrapper {margin-left: -8%!important}
.author-column, postprofile {max-width: 17%!important}
#qr_showeditor_div {display:none!important}
#qr_editor_div {display:block!important}
.content {min-height: 90px!important}
#wrapper, #dashboard {min-width: 1000px!important;width:90%!important}
.global_footer { border-left: 2px solid #c4e6ff;border-right:2px solid #c4e6ff;border-bottom:2px solid #c4e6ff;min-width: 1000px!important; width: 90%!important}
/*.header_promo {margin-top: -40px!important;width: 85%!important}*/
#wrapper {border-bottom-left-radius: 5px; border-bottom-right-radius: 5px;border-top-left-radius: 3px; border-top-right-radius: 3px;border: 2px solid #c4e6ff!important;background:url(data:image/png;base64,)!important}
}
