css:
@-moz-document domain('news.ycombinator.com'), url-prefix('http://news.ycombinator.com') {

@charset utf-8;

table
{
  background-color:#fdf6e3!important;
  width:100%!important;
}

tbody tr td
{
  background-color:#fdf6e3!important;
}

span.pagetop
{
  color:#93a1a1!important;
  padding-bottom:40px!important;
  font-family:Georgia, Times, serif!important;
  font-size:10pt!important;
  line-height:10px!important;
}

span.pagetop a:link,span.pagetop a:visited
{
  font-family:Georgia, Times, serif!important;
  font-size:10pt!important;
  color:#93a1a1!important;
  padding-left:5px!important;
  padding-right:5px!important;
  margin-bottom:10px!important;
  padding-bottom:4px!important;
  font-weight:normal!important;
  border-bottom:0 dotted #fff!important;
}

span.pagetop a:hover,span.pagetop a:active
{
  font-family:Georgia, Times, serif!important;
  font-size:10pt!important;
  color:#cb4b16!important;
  padding-left:5px!important;
  padding-right:5px!important;
  margin-bottom:10px!important;
  padding-bottom:4px!important;
  font-weight:normal!important;
  border-bottom:0 dotted #cb4b16!important;
}

body
{
  font-family:Georgia, Times, serif!important;
  font-size:12pt!important;
  color:#657b83!important;
}

td
{
  font-family:Georgia, Times, serif!important;
  font-size:12pt!important;
  color:#999!important;
  padding: 3px!important;
}

.admin td
{
  font-family:Georgia, Times, serif!important;
  font-size:8.5pt!important;
  color:#657b83!important;
}

td.subtext
{
  font-family:Georgia, Times, serif!important;
  font-size:12px!important;
  color:#999!important;
  border-bottom:1px dotted #ddd!important;
  padding-bottom: 5px !important;
}

form
{
  margin-bottom:0!important;
}

input
{
  font-family:Georgia, Times, serif!important;
  font-size:15px!important;
  color:#657b83!important;
  padding:8px!important;
  background-color:#fdf6e3!important;
  color:#333!important;
  width:auto!important;
  font-family:Georgia, Times, serif!important;
  font-size:15px!important;
  border:1px solid #ccc!important;
  -moz-border-radius:4px!important;
  -webkit-border-radius:4px!important;
  border-radius:4px!important;
}

input[type="text"]
{
  font-family:Georgia, Times, serif!important;
  font-size:15px!important;
  color:#657b83!important;
  padding:8px!important;
  background-color:#fdf6e3!important;
  color:#333!important;
  width:auto!important;
  font-family:Georgia, Times, serif!important;
  font-size:15px!important;
  border:1px solid #ccc!important;
  -moz-border-radius:4px!important;
  -webkit-border-radius:4px!important;
  border-radius:4px!important;
}

input[type="submit"]
{
  font-family:Georgia, Times, serif!important;
  color:#657b83!important;
  padding:8px!important;
  background-color:#cb4b16!important;
  color:#fff!important;
  width:auto!important;
  font-family:Georgia, Times, serif!important;
  font-size:13px!important;
  border:1px solid #cb4b16!important;
  -moz-border-radius:4px!important;
  -webkit-border-radius:4px!important;
  border-radius:4px!important;
  cursor:pointer!important;
}

textarea
{
  font-family:Georgia, Times, serif!important;
  font-size:15px!important;
  color:#657b83!important;
  column-span:200px!important;
  border:1px solid #ccc!important;
  -moz-border-radius:4px!important;
  -webkit-border-radius:4px!important;
  border-radius:4px!important;
  width:660px!important;
  height:100px!important;
}

a:link
{
  color:#cb4b16!important;
  text-decoration:none!important;
  border-bottom:1px dotted #fff!important;
}

a:hover,a:active
{
  color:#cb4b16!important;
  text-decoration:none!important;
  border-bottom:1px dotted #cb4b16!important;
}

a:visited
{
  color:#aaa!important;
  text-decoration:none!important;
  border-bottom:1px dotted #fff;
}

.default
{
  font-family:Georgia, Times, serif!important;
  font-size:12pt!important;
  color:#999!important;
  border-bottom:1px dotted #ccc!important;
  padding:0!important;
}

.admin
{
  font-family:Georgia, Times, serif!important;
  font-size:8.5pt!important;
  color:#657b83!important;
}

.title
{
  font-family:Georgia, Times, serif!important;
  font-size:12pt!important;
  color:#999!important;
  padding-top:5px!important;
  font-weight:normal!important;
}

.title a:link
{
  font-weight:normal!important;
  font-family:Georgia, Times, serif!important;
  font-size:14pt!important;
  color:#657b83!important;
  margin-right:6px;
}

.title a:hover,.title a:active
{
  border-bottom:1px dotted #cb4b16!important;
}

.title a:visited
{
  color:#aaa;
  border-bottom:1px dotted #fff;
}

.adtitle
{
  font-family:Georgia, Times, serif!important;
  font-size:9pt!important;
  color:#999!important;
}

.yclinks
{
  font-family:Georgia, Times, serif!important;
  font-size:8pt!important;
  color:#999!important;
  padding-top:15px!important;
}

.yclinks a:link,.yclinks a:visited
{
  color:#bbb!important;
  padding-left:5px!important;
  padding-right:5px!important;
  font-weight:normal!important;
  border-bottom:none!important;
}

.yclinks a:hover,.yclinks a:active
{
  color:#cb4b16!important;
  padding-left:5px!important;
  padding-right:5px!important;
  font-weight:normal!important;
  border-bottom:none!important;
}

.pagetop
{
  font-family:Georgia, Times, serif!important;
  font-size:12pt!important;
  color:#657b83!important;
}

.comhead
{
  font-family:Georgia, Times, serif!important;
  font-size:9pt!important;
  color:#888!important;
}

.comment
{
  color:#657b83!important;
  font-family:Georgia, Times, serif!important;
  font-size:15px!important;
  line-height:22px!important;
  padding-top:10px!important;
}

.dead
{
  font-family:Georgia, Times, serif!important;
  font-size:9pt!important;
  color:#ddd!important;
}

i
{
  font-style:italic!important;
}

u
{
  text-decoration:none!important;
  border-bottom:1px dotted #cb4b16!important;
  font-size:12px!important;
  margin-top:-10px!important;
}

.comment a:link,.comment a:visited
{
  color:#cb4b16!important;
  border-bottom:1px dotted #fff!important;
  font-weight:normal!important;
}

.comment a:hover,.comment a:active
{
  color:#cb4b16!important;
  border-bottom:1px dotted #cb4b16!important;
  font-weight:normal!important;
}

.dead a:link,.dead a:visited
{
  color:#93a1a1!important;
}

.pagetop a:visited
{
  color:#657b83!important;
}

.topsel a:link,.topsel a:visited
{
  color:#fff!important;
}

.subtext
{
  font-family:Georgia, Times, serif!important;
  font-size:8pt!important;
  color:#999!important;
  padding-bottom:5px!important;
  font-style:italic!important;
}

.subtext a:link
{
  padding-left:2px!important;
  padding-right:4px!important;
  color:#cb4b16!important;
  font-weight:normal!important;
  border-bottom:1px dotted #fff!important;
}

.subtext a:hover,.subtext a:active
{
  text-decoration:none!important;
  border-bottom:1px dotted #cb4b16!important;
}

.subtext a:visited
{
  padding-left:2px!important;
  padding-right:4px!important;
  color:#cb4b16!important;
  font-weight:normal!important;
  border-bottom:1px dotted #fff;
}

.comhead a:link
{
  padding-left:2px!important;
  padding-right:4px!important;
  color:#cb4b16!important;
  font-weight:normal!important;
  border-bottom:1px dotted #fff!important;
}

.comhead a:hover,.comhead a:active
{
  padding-left:2px!important;
  padding-right:4px!important;
  color:#cb4b16!important;
  font-weight:normal!important;
  text-decoration:none!important;
  border-bottom:1px dotted #cb4b16!important;
}

.default p
{
  margin-top:2px!important;
  margin-bottom:0!important;
  padding-bottom:5px;
  padding-top:15px!important;
  text-rendering:optimizeLegibility!important;
}

.pagebreak
{
  page-break-before:always!important;
}

pre
{
  overflow:auto!important;
  padding:2px!important;
  max-width:600px!important;
}

pre:hover
{
  overflow:auto!important;
}

body center > table > tbody > tr:first-child > td,body center > table > tbody > tr:first-child > td > table,body center > table > tbody > tr:first-child > td > table > tbody > tr > td
{
  background-color:#002b36!important;
}

body
{
  margin:0;
}

}
