  url-prefix('http://www.tumblr.com/reblog/'),
  url-prefix('http://www.tumblr.com/blog/'),
  url-prefix('http://www.tumblr.com/tumblelog/'),
  url-prefix('http://www.tumblr.com/tagged/'),
  url-prefix('http://www.tumblr.com/spotlight/'),
  url-prefix('http://www.tumblr.com/edit/') , url-prefix("https://www.tumblr.com/dashboard"), url("https://www.tumblr.com/new/blog"), url("https://www.tumblr.com/inbox"), url("https://www.tumblr.com/help"), url("https://www.tumblr.com/explore"), url("https://www.tumblr.com/following"), url("https://www.tumblr.com/likes"), url-prefix("https://www.tumblr.com/new/"), url-prefix("https://www.tumblr.com/reblog/"), url-prefix("https://www.tumblr.com/blog/"), url-prefix("https://www.tumblr.com/tumblelog/"), url-prefix("https://www.tumblr.com/tagged/"), url-prefix("https://www.tumblr.com/spotlight/"), url-prefix("https://www.tumblr.com/edit/") {
body {
  background:url("http://1.bp.blogspot.com/-rRvZSk4qsvY/Thy-bvwBHTI/AAAAAAAAAro/eIKJ_qAG-p8/s1600/Riddler+Wallpaper+Arkham+City.jpg") no-repeat fixed !important;
  background-color: rgba(44,71,98, 0.95) !important;
  background-position: 0% 0% !important;
  background-size: 100% 100%  !important;
}


a, a:visited{color:#666 !important}



a:hover{color:#333 !important}










img[src*="https://secure.assets.tumblr.com/images/post_icons_sprite.png"],
.new_post_label_icon {
opacity: 0.00 !important;  
}
#new_post{
  background-image: url(https://secure.assets.tumblr.com/images/post_icons_sprite.png) !important;
  background-position: top center !important; 
  background-repeat: no-repeat !important;
}



new_post {
  background-color: rgba(10, 10, 10, .70) !important;
}



#new_post .arrow {
  opacity: 1.0 !important;
  margin-left: 0px !important; 
}



#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
  background: rgba(1, 1, 1, .15) !important;
}
 


#posts LI[class*=" post"] {
  background: rgba(10, 10, 10, .93) !important;
}




}

.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  margin-top: 0px !important;
  border-bottom: solid 11px rgba(255,255,255,1.0) !important;
  border-right: 11px solid transparent !important;
  border-left: 11px solid transparent !important;
}

.selection_nipple:not([class="selection_nipple white"]) {
  border-bottom: solid 11px rgba(44,71,98, 1.0)  !important;
}



li.selected, li.recessed, a.active, div.post_filter_tabs a {
  background: none !important;
}



.post.photo_post .photo, .post.photo_post .video_thumbnail {
  -moz-box-shadow: 1px 1px 3px rgba(10, 10, 10, .70) !important;
  border-radius: 12px !important;
}
.content.is_media, .mask.top, .mask.bottom {
  background: none !important;
}



.mceContentBody {
  color: rgba(10, 10, 10, 0.75) !important;
  background: rgba(10,10,10, .75) !important;
}
