css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix('http://cantr.net'), url-prefix('http://www.cantr.net'), url-prefix('http://test.cantr.net'), domain('cantr.loc') {
    body {
      background-image:url('http://cantr.net/graphics/cantr/pictures/bg.jpg') !important;
      background-repeat: no-repeat !important;
      background-position:center top !important;
    }
    
    center table {
        background: rgba(0, 58, 0, 0.86) !important;
        box-shadow: 3px 3px 10px #004000, -3px -3px 10px #003A00, 3px -3px 10px #003000 !important;
    }
    
    .button_charmenu, .button_action {
        color: #4f4 !important;
    }
    
    .button_charmenuactive {
        background-color: #6b6 !important;
        color: #040 !important;
        box-shadow: 1px 1px 3px #6b6 !important;
    }
    
    .titlebar {
        background-color: #007000 !important;
    }
    
    .button_form {
        box-shadow: 1px 1px 3px #007700 !important;
        color: #4f4 !important;
    }
    
    a {
        color: #4d4 !important;
    }
    
    #footer_panel, center table table {
      background: none !important;
      box-shadow: none !important;
    }
    
    #character_menu, #player_menu {
        width: 260px !important;
        background: rgba(0, 58, 0, 0.86) !important;
        box-shadow: 3px 3px 10px #004000, -3px -3px 10px #003A00, 3px -3px 10px #003000 !important;
    }
    
    #player_menu {
        width:190px !Important;
    }
 
}
