@namespace url(http://www.w3.org/1999/xhtml);
/* AGENT_SHEET */
@-moz-document regexp(".*myanimelist\\.net/(?!animelist|mangalist).*") { 
 
body {
    background:rgb(1,1,1) !important;
    color:rgb(255,255,255) !important;
}
#myanimelist {
    width:1050px !important; 
}
#contentWrapper {
    background-color: rgb(15,15,15) !important;
}
#content {
    background:rgb(15,15,15) !important;
    border:none !important;
}
.content-container * {
    border-color: rgb(60,60,60) !important;
}
X#header { 
    margin-top:8px !important; 
    height:55px !important; 
    background:transparent url(http://i.imgur.com/Z1CILxt.png) no-repeat !important;
}    
#headerSmall  {
    background: transparent url(http://i.imgur.com/Z1CILxt.png) no-repeat !important;
}
#contentHome, .container, .borderClass {
    border-color:rgb(60,60,60) !important;
}
a {
    color:rgb(180,10,10) !important;
}
a:hover {
    color:rgb(220,0,0) !important;
}
h3 {
    color: rgb(180,10,10) !important;
}
#footer-block {
    background-color: rgb(0,0,0) !important;
}

    /* Nav ------------------------------------------------------ */
#menu {
    width:1048px !important;
    background-color:rgb(10,10,10) !important;
    border:1px solid rgb(60,60,60) !important;
}
#menu_left, #menu_left * {
    background-color:rgb(10,10,10) !important;
    
}
#menu li ul li {
    border-top:1px solid rgb(40,40,40) !important;
    box-shadow: -3px 3px 5px black !important;
}
#nav ul a:hover {
    background:rgb(30,30,30) !important;
}
#nav .edit a {
    border-left:1px solid rgb(30,30,30) !important;
}

#horiznav_nav  {
    border-bottom-color:rgb(180,25,25) !important;
}
#horiznav_nav ul li, #horiznav_nav ul li a {
    background:rgb(15,15,15) !important;
    
}
#horiznav_nav ul li a:hover {
    background-color:rgb(180,25,25) !important;
    color:rgb(255,255,255) !important;
}
#horiznav_nav ul li a.horiznav_active {
    background-color:rgb(180,25,25) !important;
    color:rgb(255,255,255) !important;
}
#header-menu .header-profile.link-bg {
    background-color: rgb(0,0,0) !important;
}
#header-menu .header-profile-button {
    box-shadow: none !important;
}
#header-menu .header-profile-dropdown {
    border-color: rgb(30,30,30) !important;
    box-shadow: -5px 5px 5px black !important;
}
#header-menu .header-profile-dropdown a {
    background-color: rgb(10,10,10) !important;
    border-color: rgb(30,30,30) !important;
}
#header-menu .header-profile-dropdown a:hover {
    background-color: rgb(180,10,10) !important;
    color: rgb(180,180,180) !important;
}
#header-menu .header-list-dropdown {
    border-color: rgb(30,30,30) !important;
    box-shadow: -5px 5px 5px black !important;
}
#header-menu .header-list-dropdown a {
    background-color: rgb(10,10,10) !important;
    border-color: rgb(30,30,30) !important;
}
#header-menu .header-list-dropdown a:hover {
    background-color: rgb(180,10,10) !important;
    color: rgb(180,180,180) !important;
} 
    /* Notifications --------------------------------------------------  */
.header-notification-button {
    background-color: rgb(0,0,0) !important;
}
.header-notification-dropdown .arrow_box {
    background-color: rgb(15,15,15) !important;
    border: solid 1px rgb(60,60,60) !important;
    box-shadow: -5px 5px 5px black !important;
}
.header-notification-dropdown-inner h3 {
    border-color: rgb(180,10,10) !important;
}
h3 .mark-all.disabled {
    
    opacity: 0.5 !important;
}
.header-notification-view-all a {
    background-color: rgb(18,18,18) !important;
    border-color: rgb(60,60,60) !important;
}
.notificaiton-category-nav li {
    border-color: rgb(60,60,60) !important;
}
.notificaiton-category-nav a.active {
    background-color: rgb(60,10,10) !important;
}
.notificaiton-category-nav a:hover {
    background-color: rgb(30,30,30) !important;
}
.notificaiton-category-nav a .counter {
    background-color: rgb(180,10,10) !important;
}
#nav li a span.count { /* my page */
    background-color: rgb(180,10,10) !important;
}
.header-notification-item.new {
    background-color: rgb(35,35,35) !important;
}
.header-notification-item:hover {
    background-color: rgb(60,20,20) !important;
}

    /* searchBar -------------------------------------------------- */

.incrementalSearchResultList {
    border-color: rgb(60,60,60) !important;
}
.incrementalSearchResultList .list .image,
.incrementalSearchResultList .list .thumb {
    border: none !important;
}
.incrementalSearchResultList .list {
    background-color: rgb(15,15,15) !important;
    border-color: rgb(60,60,60) !important;
}
.incrementalSearchResultList .focus {
    background-color: rgb(20,20,20) !important;
}
.incrementalSearchResultList .list .add-text,
.incrementalSearchResultList .list .info .text
 {
    color: rgb(180,10,10) !important;
}
#searchBar option:hover {
    background-color: rgb(10,10,10) !important;
    color: rgb(180,10,10) !important;
}
#searchBar .form-select-outer {
    border-radius: 2px 0px 0px 2px !important;
}
#topSearchText, #topSearchValue {
    color:rgb(255,255,255) !important;
    border-color:rgb(180,25,25) !important;
}
#topSearchValue *  {
    background:rgb(10,10,10) !important;
}
#searchBar.searchBar #topSearchButon {
    background-color: rgb(0,0,0) !important;
    border: none !important;
}
#topSearchButon {/*temp test*/
    display: none !important;
}

    /* Login ------------------------------------------------------ */
    
#dialog {
    border: 1px solid rgb(180,25,25) !important;
}
#dialog td {
    border: none !important;
}
#fancybox-outer {
    background-color: rgb(20,20,20) !important;
    border: 1px solid rgb(180,20,20) !important;
}
#fancybox-outer div[style*="font-size: 16px; font-weight: bold;"]  {
    color:rgb(180,10,10) !important;
}
.btn-form-submit {
    color: rgb(255,255,255) !important;
}
.btn-login {
    border-color: rgb(160, 10, 10) !important;
    color: rgb(200,10,10) !important;
}
.btn-login:hover {
    background-color: rgb(50,20,20) !important;
}
.btn-signup {
    background-color: rgb(180, 25, 25) !important;
    border-color: rgb(180, 25, 25) !important;
    color: rgb(255,255,255) !important;
}
.btn-signup:hover {
    background-color: rgb(120, 25, 25) !important;
    border-color: rgb(120, 25, 25) !important;
    color: rgb(220,220,220) !important;
}

    /* Profile ------------------------------------------------------ */
    

#profileRows a {
    border-color:rgb(60,60,60) !important;
}
#profileRows a:hover,
.profileRows a:hover {
    background-color:rgb(180,25,25) !important;
    color:rgb(255,255,255) !important;
}
.profileRows a.on {
    border-bottom-color: rgb(60,60,60) !important;
}
.friendHolder {
    background-color:rgb(25,25,25) !important;
}
div.borderClass~br {
    display:none !important;
}
div.bgNone, 
div.bgColor, 
div[Class="bgColor1"], 
div.bgColor2 {
    background-color:rgb(15,15,15) !important;
    border-top:1px solid rgb(180,25,25) !important;
    border-bottom:1px solid rgb(180,25,25) !important
}
.borderClass[width="60"] {
    border-right:2px solid !important;
    border-color:rgb(80,80,80) !important;
}
/*new---------------------*/
.user-function .icon-user-function {
    background-color: rgb(15,15,15) !important;
}
.user-status li  {
    background-color:  rgb(15,15,15) !important;
}
.user-button .btn-profile-submit {
    background-color: rgb(180,20,20) !important;
    color: rgb(200,200,200) !important;
}
.user-comments .comment {
    background-color: rgb(15,15,15) !important;
    border-bottom: solid 1px rgb(5,5,5) !important;
}
.page-common .btn-form-submit {
    background-color: rgb(180,20,20) !important;
}
.page-common .btn-form-submit:hover {
    
}
.user-statistics .stats-graph {
    height: 15px !important;
    border-radius: 3px !important;
}
.statistics-updates .data .graph {
    background-color: rgb(195,195,195) !important;
    border: none !important;
    padding: 0px !important;
    border-radius: 3px !important;
}

    /* Clubs -------------------------------------------------- */
    
.club-search-form-search .select {
    background-color: rgb(10,10,10) !important;
}
.link-blue-box {
    background-color: rgb(30,30,30) !important;
}
.club-search-form-search .select option {
