    color: rgb(180,180,180) !important;
    border: 1px rgb(180,180,180) solid !important;
}

    /* Messages ------------------------------------------------------ */
    
.message div {
    background-color: rgb(15,15,15) !important;
}

/* Shared */
td.borderClass[style="background-color: #f6f6f6;"] {
    background-color: rgb(5,5,5) !important;
}
span[style=" color: #0000FF;"] {
    color:rgb(20,50,220) !important;
}
    
    /* Anime-Manga ------------------------------------------------------ */

.anime-detail-header-stats .stats-block {
    background-color: rgb(15,15,15) !important;
}
.user-status-block {
    background-color: rgb(15,15,15) !important;
}
.btn-user-status-add-list {
    background-color: rgb(180,10,10) !important;
    color: rgb(200,200,200) !important;
    border: none !important;
}
.btn-user-status-add-list:hover {
    color: rgb(20,20,20) !important;
}
.updatesBar {
    background: linear-gradient(270deg, rgb(180,25,25) 0%, rgb(15,15,15) 100%) !important;
} 
.bg-color-blue1 {
    background-color: transparent !important;
}
.episode-list-header {
    background-color: transparent !important;
}
.episode_list td {
    background-color: transparent !important;
    color: rgb(180,180,180) !important;
}
.news-unit .text {
    color: rgb(255,255,255) !important;
}
.news-list .tags .tag {
    border-color:  rgb(60,60,60) !important;
}
.table-recently-updated td {
    background-color: rgb(15,15,15) !important;
}
     /* Season ------------------------------------------------------ */
        
.navi-seasonal {
    background-color: rgb(15,15,15) !important;
}
.horiznav_nav  {
    border-bottom-color: rgb(180,25,25) !important;
}
.horiznav_nav ul li, .horiznav_nav ul li a {
    background: rgb(15,15,15) !important;
}
.horiznav_nav ul li a:hover {
    background-color: rgb(180,25,25) !important;
    color: rgb(255,255,255) !important;
}
.horiznav_nav ul li a.on {
    background-color: rgb(180,25,25) !important;
