}    
.horiznav_nav ul li a.navtab {
    border-color: rgb(60,60,60) !important;
}
.horiznav-nav-seasonal {
    border-color: rgb(60,60,60) !important;
}
.btn-seasonal .btn-type {
    background-color: rgb(5,5,5) !important;
    color: rgb(255,255,255) !important;
}
.btn-type.on {
    background-color: rgb(30,30,30) !important;
    color: rgb(220,20,20) !important;
}
.seasonal-anime-list .anime-header {
    background-color: rgb(0,0,0) !important;
    color: rgb(200,20,20) !important;
    border-color: rgb(60,60,60) !important;
}
.seasonal-anime-list .seasonal-anime {
    border-color: rgb(80,80,80) !important;
}
.seasonal-anime-list .seasonal-anime div {
    background-color: rgb(15,15,15) !important;
    color: rgb(180,180,180) !important;
    border-color: rgb(60,60,60) !important;
}
.seasonal-anime .genre  {
    background-color: rgb(15,15,15) !important;
    border-color: rgb(60,60,60) !important;
    font-size: 11px !important;
    color: rgb(200,200,200) !important;
}
.btn-more-genre {
    background-color:  rgb(15,15,15) !important;
    border-color: rgb(60,60,60) !important;
}
.seasonal-anime .image .info {
    background-color: rgba(15,15,15,0.8) !important;
    color: rgb(180,180,180) !important;
    border-top-color: rgb(60,60,60) !important; 
}
.scormem .member::after,
.scormem .score::after {
    background-image: url(data:image/png;base64,) !important;
}
.anime-seasonal-byseason td {
    background-color: rgb(15,15,15) !important;
    border-bottom-color: rgb(60,60,60) !important;
}
        
    /* Streaming ------------------------------------------------------ */

.watch-anime-list .watch-anime {
    border-color: rgb(80,80,80) !important;
}
.watch-anime-list .watch-anime div {
    background-color: rgb(15,15,15) !important;
    color: rgb(180,180,180) !important;
    border-color: rgb(60,60,60) !important;
}

.watch-anime .genre  {
    background-color: rgb(15,15,15) !important;
    border-color: rgb(60,60,60) !important;
    font-size: 11px !important;
    color: rgb(200,200,200) !important;
}
.watch-anime .image .info {
    background-color: rgba(15,15,15,0.8) !important;
    color: rgb(180,180,180) !important;
    border-top-color: rgb(60,60,60) !important; 
}
        
    /* Forum ------------------------------------------------------ */
    
.forum_locheader {
    color:rgb(255,255,255) !important;
}
.btn-forum {
    border-color: rgb(60,60,60) !important;
}
.forum-topic-sort {
    background-color: rgb(15,15,15) !important;
}
.forum-table-header {
    background-color: rgb(0,0,0) !important;
}
.pw-button {
    display:none !important;
}
.btn-forum {
