}
.quotetext, .codetext {
    background:rgb(30,30,30) !important;
}
.bgbdrContainer {
    background:rgb(15,15,15) !important;
    border-color:rgb(60,60,60) !important;
}

    /* Button */
    
.inputButton {
    background-color:rgb(180,25,25) !important;
    border-color:rgb(180,25,25) !important;
    border-radius:4.5px !important;
}
.button_form, .button_edit, .button_add {
    background-color:rgb(25,25,25) !important;
    color:rgb(150,150,150) !important;
    border-color:rgb(180,25,25) !important;
}

    /* Other ------------------------------------------------------ */
    
.h1 {
    background-color:rgb(25,25,25) !important;
    color:rgb(180,25,25) !important;
    border:1px solid rgb(60,60,60) !important;
    border-top:none !important;
}
h2, .normal_header {
    color:rgb(180,25,25) !important;
    border-bottom:2px solid !important;
    border-color:rgb(100,100,100) !important;
}
.normal_header {
    color:rgb(210,210,210) !important;
    font-weight:bold !important;
}
#contentHome {
    background:rgb(15,15,15) !important;
}
.bgNone, .bgColor, .bgColor1, .bgColor2 {
    background-color:rgb(15,15,15) !important;
}
.picSurround img {
    border:none !important;
    background: transparent !important;
}
.dark_text {
    color:rgb(80,80,80) !important;
}
#rightcontentunder  {
    background:rgb(5,5,5)  !important;
    /*border-top:1px solid !important;*/
    border-color:rgb(60,60,60) !important;
}
.textarea, .inputtext {
    background-color:rgb(23,23,23) !important;
    border-color:rgb(80,80,80) !important;
    color:rgb(255,255,255) !important;
}
.goodresult {
    background-color:rgb(0,0,0) !important;
    border-color:rgb(0,200,0) !important;
}
.badresult {
    background-color:rgb(0,0,0) !important;
    border-color:rgb(200,0,0) !important;
}
.ranking-header {
    background-color:  rgb(0,0,0) !important;
}
.ranking-digest {
    background-color: rgb(15, 15, 15) !important;
    border-right: 1px rgb(60, 60, 60) solid !important;
}
.footer-ranking {
    background-color: rgb(0,0,0) !important; 
}
.social-login-block::after {
    background-color: rgb(80,80,80) !important;
}

    /* Extra ------------------------------------------------------ */
    
.ra1-pw-icon-baidu, .ra1-pw-icon-email {
    display:none !important; 
}
td, td div {
    border-color:rgb(60,60,60) !important;
}
.icon-social {
    opacity: 0.2 !important;
}
.icon-social:hover {
    opacity: 1.0 !important;
}


    /* IMG */  
 
       /* 338 */
    
