css:
@-moz-document domain("rdio.com") {
body, input, textarea, .button, .Catalog_Album .main_column .album_header .album_name, .ugc, .section_header, .catalog_header .catalog_name, .App_MainNav li a.playlist span {
    font-family: "Open Sans" !important;
}

.Album .album_title {
    font-size: 14px !important;
    letter-spacing: -1px !important;
}

.Catalog_Album .main_column .album_header .album_name {
    letter-spacing: -1px !important;
    font-weight: bold !important;
}

.Track .info .name a {
    font-size: 15px !important;
    font-weight: normal !important;
}

a {
    color: #63707a !important;
}

.App_Main {
    background-color: #fcfcfd !important;
}

.App_Main .App_MainNav {
    margin: 0 !important;
    background-color: #e1e6e9 !important;
}

.App_Main .app_main > .Scrollable {
    margin-left: 218px !important;
}

.App_MainNav li a,
.App_MainNav .section .Expander {
    padding-left: 20px !important;
    padding-right: 20px !important;
}

.App_MainNav li a {
    font-size: 13px !important;
    font-weight: normal !important;
}

.App_MainNav li.selected a {
    background: #1babf4 !important;
    background: -moz-linear-gradient(top,  #1babf4 0%, #2597d3 100%) !important;
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#1babf4), color-stop(100%,#2597d3)) !important;
    background: -webkit-linear-gradient(top,  #1babf4 0%,#2597d3 100%) !important;
    background: -o-linear-gradient(top,  #1babf4 0%,#2597d3 100%) !important;
    background: linear-gradient(to bottom,  #1babf4 0%,#2597d3 100%) !important;
    color: #fff !important;
    font-weight: 600 !important;
}

.App_MainNav .section .playlist_search .playlist_search_wrapper {
    padding-left: 10px !important;
}

.App_Main .footer {
    background-image: url(data:;base64,) !important;
}

.App_PlayerFooter .left_controls .play_pause {
   background-image: url(data:;base64,) !important;
}

.App_PlayerFooter .left_controls .next {
   background-image: url(data:;base64,) !important;
}

.Slider .track {
   background-color: #34444d !important;
}

.Slider .track .bar {
   background-color: #e9ebed !important;
}

.App_PlayerFooter .right_controls .volumeIcon {
    background-image: url(data:;base64,) !important;
}

.App_PlayerFooter .right_controls .time {
   color: #e9ebed !important;
}

.App_PlayerFooter .text_metadata .song_title {
   font-weight: bold !important;
   color: #000 !important;
}

.App_PlayerFooter .text_metadata,
.App_PlayerFooter .text_metadata .artist_title {
   color: #333 !important;
}


}
