css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cs-manager.com") {

body {	background: #000C17 !important;  }

#header {
	margin: 0 auto !important;}
#container, #footer_container,.post,#content {
	margin: 0 auto !important;}

a       {color: #D2691E !important;/*	
        font-style: normal !important; */
}

#header {
	background-color: #333B42 !important;
	background-image: url("http://img87.imageshack.us/img87/6618/newonerq3.png") !important;
	background-repeat: no-repeat !important;
	border-right: 1px solid #000000 !important;
	border-bottom: 1px solid #000000 !important;
	border-left: none;
	border-top: none;
}

#header img {	visibility: hidden !important;} 
#header a img {	visibility: hidden !important;} 

#container, #footer_container,.post,#content {
	color: #DAA520	!important;
	background: #444C54 !important;
	border-right: none;
	border-bottom: none; 
	border-left: none;
	border-top: none;
}

table.rank,table.login {
	border: 1px solid #000000 !important; 
}

table.rank,table.login,table.mail,table.info {
	background: #474E53 !important;
}

td.space,div.space {
	background: #333B42 !important;
	border:none !important;
}
a.player,td.player,td.top,td.tactic,a.tactic {
	background: #A9B2BA !important;
	color: #000000 !important;
}
a.tactic:hover {
	background: #C0C0C0 !important;
        color: #000000 !important;
}
td.position {
	background: #696969 !important;
        color: #FFFFFF !important;
}


td.head, h2, h3 {
	color: #CFD9CE !important;
	background-color: #262829 !important;
	background-image: url("http://img168.imageshack.us/img168/8937/grad3xa6.gif") !important;
	border-top: 0px solid #000000 !important;
	border-left: 0px solid #000000 !important;
	border-right: 0px solid #000000 !important;
	border-bottom: 00px solid #000000 !important;
}


li a {
	background: #333B42 !important;
	color: #DAA520 !important;
	font-style: normal !important;
  
}


ul#menu li a {
	border-bottom: none !important;
	border-right: none !important;
	border-left: none !important;
	border-top: none !important;
}


ul#tab li a {
	border: 1px solid #D3D3D3 !important;
}

li a:hover {
	background: #000000 !important;
	font-style: normal !important;
}


ul#tab li span {
	background: #000000 !important;
	color: #FFFFFF !important;
	font-style: normal !important;
	border-bottom: 0px solid #000000 !important;
}


li span {
	color: #FFE4B5 !important;
}


table.mail tr.head td, table.mail td.select, table.mail td.delete {
	background-color: #F0E68C !important;
        color: #CD853F !important;
}


#menu, #footer_left, #footer_right, div#container>div#info {
	background: #000C17 !important;
}


form#search {
	border-bottom: 1px solid #000000 !important;
	background: #272B2E !important;
}


input,select,textarea,radio {
	background: #D3D3D3 !important;
	border: 1px solid #000000 !important;
}
.buttonsmall, .button {
	background: #EFF3F6 !important;
	color: #000000 !important;
	border-top: 2px solid #C7C7C7 !important;
	border-left: 2px solid #C7C7C7 !important;
	border-right: 2px solid #C7C7C7 !important;
	border-bottom: 2px solid #C7C7C7 !important;
	font-size: 10px !important;
	padding: 0px !important;
}


td.value {
	background: #CFD7D9 !important;
        color: #000000 !important;
}
td.foot,td.info {
	background: #C5CDCF !important;
        
}


#training_map,#training_player,#skill,#trainer,#training_players {
	background: #C5CDCF !important;
}
div#training_type {
	background: #CFD7D9 !important;
}

/** LanCamp Training **/
tr.lan td.info {
	background: #ADBDDE !important;
}
tr.lan td.value {
	background: #ADBDDE !important;
}



tr.even {
	background: #D3D3D3 !important;
}
tr.odd {
	background: #A9A9A9 !important;
}
div.modify {
	background: #CFD7D9 !important;
}

#ok_msg {
	color: #59F559 !important;
	background-color: #005D00 !important;
	border-top: 2px solid #008B00 !important;
	border-left: 2px solid #008B00 !important;
	border-right: 2px solid #008B00 !important;
	border-bottom: 2px solid #008B00 !important;

}

#error_msg {
	color: #F55959 !important;
	background-color: #5D0000 !important;
	border-top: 2px solid #8B0000 !important;
	border-left: 2px solid #8B0000 !important;
	border-right: 2px solid #8B0000 !important;
	border-bottom: 2px solid #8B0000!important;

}

#help_tab {
	background: #669797 !important;
	color: #FFFFFF !important;
        font-weight: bolder !important
}

/** League table **/

table.table tr.promotion td, table.table tr.promotion_line td {
	color: #00B900 !important;

}

table.table tr.qualup td, table.table tr.qualup_line td {
	color: #000000 !important;
	color: #007400 !important;

}

table.table tr.qualdown td, table.table tr.qualdown_line td {
	color: #000000 !important;
	color: #740000 !important;

}

table.table tr.relegation td, table.table tr.relegation_line td {
	color: #B90000 !important;

}

table.calender td.week {
        background: #B9B9B9 !important;
        color: #900000 !important;

}

a.challenge {
	background: #E8E8E8 !important;

}

a.challenge:hover {
	background: #C8C8C8 !important;

}

div.challenge {
	background: #D3D3D3 !important;

}

div.disabled {
	background-color: #C8C8C8 !important;

}


}
