css:
@-moz-document domain("mitfahrgelegenheit.de") {
#footer, #content_right, .box_grey_top, .box_default_bottom, .box_grey_shadow_top, .box_grey_shadow_bottom, .box_default_top, #content_left div[style="padding: 10px; line-height: 1.5em"]
{
  display: none !important;
}

.header_ad_spacing 
{
  padding-top: 0px;
}

#content_left
{ 
  width: 100%;
  margin: 0 auto;
}

#content_left div.box_default, #content_left div.box_grey
{
  width: 100%;
  margin-bottom: 20px;
  zoom: 110% !important;
}

div.box_content_tab3d
{
  background: none;
  border: none !important;
}

#content_left div.box_grey, div.box_content_white
{
  border: 1px solid #c4c4c4 !important;
}

.box_default
{
  border: none !important;
}

#container
{
  margin: 0 auto;
  width: 95%;
}

#head
{
  margin: 0 auto;
  zoom: 120%
}

h2
{
  padding-bottom: 0;
}

.box_content
{
  zoom: 120%;
}

form input.submit_head
{
  background: #FFFFFF !important;
}

.button_grey, .button_new_grey, button.secondary, .button.secondary, form input.submit.secondary, input.button.secondary, form input.submit_return, form input.submit_grey_back, input.submit_grey_back, form input.submit_wide_grey, .ui-datepicker button, .button
{
  border-radius: 0px !important;
  -webkit-border-radius: 0px !important;
}

.tab .active, ul.tab_3d li.active, ul.tab_3d li.active a
{
  background-image: none !important;
  border: 0.7pt solid #c4c4c4 !important;
}

.tab_3d, .tab, ul.tab_3d li, ul.tab_3d li a
{
  background-image: none !important;
  
}
}
