css:
@namespace url(http://www.w3.org/1999/xhtml);

/*背景图片*/

@-moz-document url-prefix('http://tieba.baidu.com/f?kw='),

url-prefix("http://tieba.baidu.com/f?wd="),

url-prefix("http://tieba.baidu.com/f/good?kw="),

url-prefix("http://tieba.baidu.com/f/vote?kw="),

url-prefix('http://tieba.baidu.com/p/'),

url-prefix('http://tieba.baidu.com/f?ct='),

url-prefix('http://tieba.baidu.com/f?kz='),

url-prefix('http://tieba.baidu.com/i/'),

url-prefix('http://tieba.baidu.com/f/search'),

url-prefix('http://tieba.baidu.com/tb/index.html'),

url-prefix('http://tieba.baidu.com/index.html'),

url-prefix('http://tieba.baidu.com/tb/index/v2/toutiaohuigu.html'),

url-prefix('http://tieba.baidu.com/imall/mall'){

/** 去掉顶栏的广告行 **/
#frs_banner_ad{
    display: none !important; 
}

/** 修复贴吧内文右侧信息栏 **/

#container table[align="right"]{
   display: none !important;
}

/** 链接去下划线 START **/
a, a:link, a:visited, a:hover, a:active {
    text-decoration: none !important;     
}
/** 链接去下划线 START **/

/** 去广告 START **/
.l_banner, #rightAd, #frs_assocaited, #head img[src*="banner"], #header img[src*="banner"], #head a[style*="banner"], #header a[style*="banner"], a.editor_wap_link {
    display: none !important;
}
/** 去广告 END **/


/** 宽页面 START **/
body, body[class*="skin"] {
    padding: 0 35px !important;
    background: none;
    width: auto !important;
}

.tb-editor-wrapper, .fixedwidth, .l_core, .p_post, #head, #container, .frs_box, #content_leftList, #container #main, #container #main .category, .bg_wrap, .bg_wrap_main, #contet_wrap, .left_section, #head, #container, .d_content_has_lzl {
    width: auto !important;
    background-image: none !important;
}

.frs_content, .d_post_content_main {
    width: 100% !important;
    background-image: none !important;
}

.c_post_op {
    float: right !important;
    padding: 0 !important;
}

#container #main li[id*="thread_list"], #container .list_head, #content {
    width: 100% !important;
    padding: 0 !important;
    margin-right: 0 !important;
}
#container #main .list_content, #container .list_head .name {
    width: auto !important;
    float: left !important;
}
#container #main .list_downloads, #container #main .list_info, #container .list_head, #container .list_head .downloads, #container .list_head .info {
    margin: 0 !important;
    padding-right: 10px !important;
    float: right !important;
}
#container .list_head {
    clear: both !important;
}
#container .list_head .downloads {
    width: auto !important;
}
#container .list_head .info {
    width: 110px !important;
}

.right_section {
    display: none !important;
}

.l_thread, #post_list, .editor-quote, .editor-quote .quote_change, .editor-quote fieldset, .editor-quote legend {
    width: auto !important;
    word-break: break-word !important;
}

.d_quote, .d_quote * {
    width: auto !important;
    display: inline !important;
}

.editor-quote .quote_content {
    width: auto !important;
    padding-right: 16px !important;
    word-break: break-word !important;
}

#editor, #title1, #thread_header, #thread_footer, table#post_tb {
    padding-left: 0 !important;
    padding-right: 0 !important;
    margin-left: 0 !important;
    margin-right: 0 !important;
    width: 100% !important;
}

.th_list *:not(.th_rp_num, li) {
    width: auto !important;
}
.th_list li {
    width: 100% !important;
}
.th_list li *:not(.th_reply) {
    float: none !important;
    display: inline !important;
}
.th_list li .th_author, .th_list li .th_w1, .th_list li *[class*="reply"] {
    float: right !important;
}
.th_list li .th_rp_num {
    display: inline-block !important;
}
.th_list li .th_info {
    display: none !important;
}

.l_post, .l_container, .container {
    width: auto !important;
    margin: 0 !important;
    padding: 0 15px 0 0 !important;
}

.nav_r {
    margin-left: 5% !important;
}

#goTop {
    position: fixed;
    display: block;
    margin: 0;
    padding: 0;
    left: 0.5% !important;
    bottom: 200px !important;
    z-index: 600 !important;
}

.new_tiezi_tip, #edit_parent {
    margin: 0 !important;
}

.p_thread, .p_postlist, .d_content {
    width: auto !important;
    padding: 0 !important;
    margin: 0 !important;
}

.d_post_content {
    line-height: 150% !important;
}

div.wrap1, div.wrap2, [class*="skin"] div.wrap1, [class*="skin"] div.wrap2 {
    background-image: none !important;
    background-color: transparent;
}
/** 宽页面 END **/

/** 简化贴吧时的设置 START **/

/* 名称字体放大 */
.d_name, li.name, div.name {
   font-size: 16px !important;
}

/* .l_fans：粉丝 .l_badge：徽章 .post_icon, .author .icon：头像
 * .p_sharebar：分享按钮 .p_ding_down：顶贴按钮 .d_sign_split,img[onerror*="Page.sign"]：签名档 #sign_mod: 每日签到
 */
.post_icon, .author .icon, .l_fans, .l_badge, .l_concern, .p_sharebar, .p_ding_up, .p_ding_down, .d_sign_split, img[onerror*="Page.sign"], #sign_mod {
   display: none !important;
}

#aside, #j_aside {
   position: absolute !important;
   overflow: hidden !important;
   height: 22px !important;
   top: 80px !important;
   right: 45px !important;
   float: none !important;
   background: #FFF;
   box-shadow: -1px 1px 5px 1px #ABCDEF;
}

#aside:hover, #j_aside:hover {
   height: auto !important;
}

#aside .mod, #j_aside .mod {
    background-color: #FFF;
    margin-top: 0 !important;
    margin-bottom: 0 !important;
    border: none !important;
}

#balv_mod {
    padding-top: 4px !important;
}

#balv_mod:before {
    content: "展开本吧信息";
    text-align: center;
    color: #000;
    display: block !important;
    margin-bottom: 5px !important;
    padding-left: 5px !important;
}

.contet_wrap {
   margin-right: 0 !important;
}
/** 简化贴吧时的设置 END **/

}
