css:
/*Author ACIDized */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("2wapworld.com") {body
{
margin: 3px auto; 
max-width: 800px !important;
text-align: center; 

font-weight : normal;
font-family : Verdana, Arial, Helvetica, sans-serif;
  background-image : url('http://i47.tinypic.com/1ghhqs.jpg') !important;
  background-attachment : fixed;
  background-repeat : repeat;
color : #c0c0c0;

}
    a:active, a:visited, a:link, a.ads:active, a.ads:visited, a.ads:link {
        color : #dbc255;
        text-decoration: none;
        
    }
    a:hover {
          color : #fdd017;
          text-decoration: none !important;
          font-size: 150%;
          background-color: black;
    }
    img.online {
          border: 1px solid #00FF80;
        
    }
    img.offline {
          border: 1px solid #c0c0c0;
          
    }
    
   
    div.head {
	padding: 2px 8px 2px 8px;
	font-size: 11px;
	font-weight: bold;
	text-transform: uppercase;
	text-align: right;
	color: #EEEEEE;
  	background-image : none !important;
  	background-attachment : fixed;
  	background-repeat : repeat;
  	opacity:0.0
     }
    
    div.c {
     padding : 1px;
     text-align : center;
    }
    div.pm_box
    {
     background-color: #faf2c1;
     color: #000000;
     font-weight: bold;
     padding:7px 7px 7px 7px;
     border: 1px solid #d7cf9e;
    }
    div.popups { 
     background-color: #666666;
     color: #c0c0c0;
     border: 1px solid #d7cf9e; }
    div.shout_box
    {
	position:relative;
	padding:15px;
	color:#333;
	background:#000000 !important;
	-webkit-border-radius:10px;
	-moz-border-radius:10px;
	border-radius:10px;
	opacity:0.7;
}

    div.shout_sub
    {
     background-color: #000000 !important;
     color: white;
     padding:2px 2px 2px 2px;
    }
    div.postmenu {
     text-align : right;
    }
    div.post {position:relative;
	padding:15px;
	margin:20px 0 0.5em;
	color:#333;
	background:#eee;
	opacity: 0.9;
	-webkit-border-radius:10px;
	-moz-border-radius:10px;
	border-radius:10px;
}
.post:before {
	position:absolute;
	top:-10px;
	left:0;
	width:155px;
	height:15px;
    /* reduce the damage in FF3.0 */
    display:block; 
}


.post:after {
	content:"";
	position:absolute;
	top:-29px;
	left:50px;
	border:15px solid transparent;
	border-bottom-color:#eee;
    /* reduce the damage in FF3.0 */
    display:block; 
    width:0;
}

    div.quote{
  position: relative;
  background-color: black !important;
  margin: 0;
    text-align:center;
  -moz-border-radius:10px;
  -webkit-border-radius:10px;
  -webkit-box-shadow: 0px 0 3px rgba(0,0,0,0.25);
  -moz-box-shadow: 0px 0 3px rgba(0,0,0,0.25);
  box-shadow: 0px 0 3px rgba(0,0,0,0.25); 
}
div.darker {
     background-color: transparent !important;
    }

    div.form {
     background-color: #3b5998;
     color: #98a9ca;
     padding: 2px;
    }
    
    
    input {
	color: #000000;
	background-color: #ffffff;
	border-width: 1px;
	border-color: #333333;
	border-style: solid;
	}    
	input.ibutton {
      font-weight: bold;
      text-decoration: none;
      color: #ffffff;
      background-color: #5c74a3;
      border-top: 1px solid #7e91b6;
      border-left: 1px solid #152f5f;
      border-right: 1px solid #152f5f;
      border-bottom: 1px solid #152f5f;
      padding: 0px 1px 0px 1px;
    }
    hr {
        background-color: #242424;
        border: none;
        height: 1px;
        opacity:0.0;
    }
    
    textarea {
	color: #000000;
	background-color: #ffffff;
	border-width: 1px;
	border-color: #333333;
	border-style: solid;
	}
    

}
