css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix('https://www.tumblr.com')   {
* {
  border-radius: 0px !important;
}

img#content_tab_left {
  display: none !important;
}
#content_tab {
  padding-left:40px !important;
  padding-right:40px !important;
}


body {
  font-size: 12px !important;
  padding:0 20px !important;
}


#right_column .small_links,
#right_column #recommended_tumblelogs,
#right_column .radar {
  display:none;
}

#nav {
  font-size: 12px !important;
}
/*
#logo {
  height:34px !important;
  left:5px !important;
  top:24px !important;
  width:147px !important;
}
*/
a.new_post_notice {
  font-size: 12px !important;
  left: 155px !important;
}
#pagination {
  font-size:12px !important;
  padding-right:30px !important;
}


div#dashboard_controls {
 display:none;
}


img.image_thumbnail {

}
div[id^="highres_photo_"] {

  margin-bottom: 5px !important;
}
div[id^="highres_photo_"] img {
  max-height: 500px !important;
  width: auto !important;
}
div[id^="highres_photo_"] div {
  display: none !important;
}
div.caption {
  font-size: 12px !important;
}
div.caption * {
  display: inline !important;
  margin:0 2px 0 0 !important;
  padding: 0 !important;
}


ol#posts li.post.new_post{
  margin-bottom: 0px !important;
}
ol#posts li.post {

}


li.quote div.post_content .post_text_wrapper {
  visibility: hidden;
}
li.quote div.post_content .post_text_wrapper > * {
  visibility: visible;
  font-size: 12px !important;
}
li.quote div.post_content span.quote {
  font-size: 18px !important;
  font-weight: bold !important;
  
  line-height: 1.2 !important;
}
ol#posts li.post .quote_source {
  font-weight: normal !important;
}

li.quote div.post_content span.quote:before {
  opacity: 0.6 !important;
  font-family:Georgia,"Times New Roman",Serif !important;
  content: "\00201c"; !important;
  font-size: 40px !important;
  vertical-align: 5px;
}


pre{
  margin-top: 35px !important;
  background-color: #000020 !important;
  color: #00FF00 !important;
  font-family: 'Bitstream Vera Sans Mono', 'Courier New', monospace !important;
  font-size:12px !important;
  padding: 5px !important;
  line-height: 1.4 !important;

}


p.autopagerize_page_info,
p.autopagerize_page_info a.autopagerize_link {
 color: white !important;
}


ol.notes li.note a.block {
  display: none !important;
}

ol#posts ol.notes li.source_post {
  display:block !important;
}
}
