css:
@-moz-document domain("twitter.com") {
body {
  background: #fff !important;
  font-size:18px;
}
body * {
  font-family: sans-serif!important;
}

#page-container {
  letter-spacing: 0.1em;
  line-height: 1.5;
}
#page-container a {
  color: #099!important;
}
h1.fullname,
h2.fullname {
  display:none;
}
.wrapper.black {
  background: none !important;
}

.content {
  margin-left: 1.5em!important;
}
.content p {
  
  margin-bottom: 0em !important;
}
.stream-item-header {
  height:1.2em;
}
img.avatar {
  width:1.2em!important;
  height:1.2em!important;
}
.stream-item-header .avatar {
  margin-left:-1.5em!important;
}

.username b {
  display: none;

}
.username s {
  display: none;
}
.stream-item-footer {
  
}


div[data-component-term = "user_recommendations"],
div[data-component- = "trends"],
div[data-component- = "footer"],
div[data-component-term="trends"],
div[data-component-term="newest_list_members"],
div[data-component-term="similar_user_recommendations"] {
  display: none;
}


.stream-item-header .fullname {
  display: none!important;
}


.profile-header .username {
  color: white!important;
}

.username {
  font-size: 100%;
  font-weight: bold!important;
  color: black!important;
}

.content-main {
  float: left!important;
}

.dashboard {
  float: right!important;
}

#suggested-users {
  clear: none;
}

.profile-card.profile-header {
  float: left!important;
}

.profile-card.profile-header .profile-header-inner {
  height: 130px;
}

a.profile-picture {
  width:1.2em !important;
  height:1.2em !important;
}

.profile-header-inner-overlay {
  height: 0!important;
}
}
