css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document url-prefix('www.tumblr.com/'),  url-prefix('https://www.tumblr.com/'),  url('https://www.tumblr.com/login'),


  url('http://www.tumblr.com/dashboard'),


  url('http://www.tumblr.com/new/blog'),



  url('http://www.tumblr.com/inbox'),

  url('http://www.tumblr.com/help'),



  url('https://www.tumblr.com/preferences'),

  url('http://www.tumblr.com/lookup'),


  url('http://www.tumblr.com/explore'),


  url('http://www.tumblr.com/following'),


  url('http://www.tumblr.com/likes'),


  url-prefix('http://www.tumblr.com/new/'),



  url-prefix('http://www.tumblr.com/tumblelog/'),
  url-prefix('http://www.tumblr.com/reblog/'),

  url-prefix('http://www.tumblr.com/blog/'),

  url-prefix('http://www.tumblr.com/tagged/'),


  url-prefix('http://www.tumblr.com/spotlight/'),


  url-prefix('http://www.tumblr.com/edit/') , url("https://www.tumblr.com/dashboard"), url("https://www.tumblr.com/new/blog"), url("https://www.tumblr.com/inbox"), url("https://www.tumblr.com/help"), url("https://www.tumblr.com/lookup"), url("https://www.tumblr.com/explore"), url("https://www.tumblr.com/following"), url("https://www.tumblr.com/likes"), url-prefix("https://www.tumblr.com/new/"), url-prefix("https://www.tumblr.com/tumblelog/"), url-prefix("https://www.tumblr.com/reblog/"), url-prefix("https://www.tumblr.com/blog/"), url-prefix("https://www.tumblr.com/tagged/"), url-prefix("https://www.tumblr.com/spotlight/"), url-prefix("https://www.tumblr.com/edit/") {
body{

background: url('http://static.tumblr.com/usldvzy/95Dm6mhbe/picture21.png') bottom left #202020 no-repeat fixed !important;

}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

background-color: #222 !important;}

#left_column .dashboard_options_form {
background: url('http://assets.tumblr.com/images/white_20.png') #000000;}

ol#posts li.notification{background-color: #000000 !important;
border-bottom: 1px solid #2F465C !important;
color: #A8BCCB !important;}

#right_column a#dashboard_switch_blog_menu_current:hover {background: transparent;}

#right_column .radar .content{background:transparent!important}

#right_column .radar .content .mask{background:transparent!important;}

.selection_nipple:not([class="selection_nipple white"]) {
  border-bottom: solid 11px rgba(30,30,30, 1.0)  !important;}

#nav .nav_item.active{background-color:#353738;}

#left_column #posts .post .permalink{background-color:#2A2D30;}

#nav .nav_item.active, #nav .nav_item .nav_menu, 
#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel{
background-color:#454748;}

#footer, #footer a {
color:#dddddd !important;
background-image:none !important;}


.post.same_user_as_last:hover, .post.text_post:hover, 
.post.video:hover, .post.audio:hover, .post.photo:hover, 
.post.link:hover, .post.quote:hover, .post.conversation:hover, 
.post.is_reblog:hover, .post.submission:hover, .post.regular:hover, 
.post.question_post:hover, .post.note:hover {
  border-top-right-radius: 24px !important; 
  opacity: 1.00 !important;
}
.permalink {
  background: none !important;
  width: 16px !important;
  height: 16px !important;
  background: rgba(20,20,20, .85) !important;
  border-bottom-left-radius: 10px !important;
  border-top-right-radius: 24px !important;
  margin-top: 6px !important;
  margin-right: 6px !important;
}
.permalink:hover {background: rgba(127,139,152, 1.0 ) !important;}

.new_post_label{

font-size:0px !important;

}



.new_post_label_icon{background-image:none !important}

/* Code repaired by admin. See http://forum.userstyles.org/discussion/37591/attempting-to-mass-fix-broken-tumblr-dash-icon-styles */
.new_post {
	background: #ffffff url('http://static.tumblr.com/usldvzy/GL4mblmku/picture1sss__1_a.png') center center no-repeat !important;
}






#logo {height: 0 !important;



	width: 0 !important;



	padding-left: 240px !important;



	padding-top: 70px !important;



	background: url(http://static.tumblr.com/usldvzy/GWbm6m36h/picture5d.png) no-repeat !important;}


#popover_blogs .tab_notice:after{display:none}div.l-header .tab.iconic > a {background-image:url('http://media.tumblr.com/tumblr_mbliwb7U171qhk6l1.png') !important;}
}
