@-moz-document url("http://www.facebook.com/"), url("http://www.facebook.com/find-friends/browser/"), url-prefix("http://www.facebook.com/") {

.uiMetaComposerMessageBox .composerTypeahead .wrap {
padding: 5px !important;
border: 0;
-webkit-box-shadow: 0px 2px 5px 0px lightGrey;
}

.timelineLayout #contentCol {
background: #E7EBF2;
padding-top: 0;
}

.fbx #globalContainer #content {
min-height: inherit;
background: #E7EBF2;
border-bottom-right-radius: 5px;
border-bottom-left-radius: 5px;
}

-----------------------------------------------------------------------

.uiStreamStory .uiStreamAttachments .photoRedesignAspect, .uiStreamStory .uiStreamAttachments .photoRedesignCover, .uiStreamStory .uiStreamAttachments .photoRedesignLink {
zoom: 1;
box-shadow: 0px 2px 5px 0px lightGrey;
-moz-box-shadow: 0px 2px 5px 0px #d3d3d3;
-webkit-box-shadow: 0px 2px 5px 0px lightGrey;
background: #F2F2F2;
border: 1px solid #C1C1C1;
margin-bottom: 10px;
padding: 16px 8px 8px;
position: relative;
word-wrap: break-word;
}

.fbDockWrapper.f3584211452444923838 .fbDockChatTab.highlight .fbNubButton, .fbDockWrapper.f3584211452444923838 .fbMercuryChatTab.highlightTab .fbNubButton, .fbDockWrapper.f3584211452444923838 .fbDockChatTab.highlight:hover .fbNubButton, .fbDockWrapper.f3584211452444923838 .fbMercuryChatTab.highlightTab:hover .fbNubButton {
background-color: #F4F4F4;
background-image: none;
filter: none;
-ms-filter: none;
border-color: #999;
}

.permalinkBody #contentArea {
border: 1px solid 
#C4CDE0;
width: 624px;
background-color: 
white;
padding: 0 3px 1px;
width: 882px;
}

.permalinkBody #rightCol {
width: 243px;
display: none;
}

.home #leftCol {
min-height: 600px;
background: 
white;
border-bottom-left-radius: 5px;
}

.ego_section {
margin-bottom: 15px;
display: none;
}

body {
background: white url(http://i.imgur.com/bUMYZ.jpg) top center repeat fixed;
}

#headNav {
border-top-left-radius: 5px;
border-top-right-radius: 5px;
}

.uiProfilePhotoLarge {
border-radius: 3px;
}

.UIImageBlock_Image .img, img.UIImageBlock_Image, i.UIImageBlock_Image {
border-radius: 5px;
}

.uiStreamStory .largePhoto img {
border-radius: 5px;
}

.uiPhotoThumb {
border-radius: 5px;
}

.ego_column {
display: none;
}

.uiImageBlockImage, .uiImageBlockImage .img {
display: block;
border-radius: 5px;
}

#pagelet_rhc_ticker {
box-shadow: 0px 2px 5px 0px #e0e0e0;
-moz-box-shadow: 0px 2px 5px 0px #e0e0e0;
-webkit-box-shadow: 0px 2px 5px 0px #e0e0e0;
}

.uiPhotoThumb {
border-radius: 5px;
box-shadow: 0px 2px 5px 0px #e0e0e0;
-moz-box-shadow: 0px 2px 5px 0px #e0e0e0;
-webkit-box-shadow: 0px 2px 5px 0px #e0e0e0;
}

.uiVideoThumb {
border: 0;
display: inline-block;
overflow: hidden;
position: relative;
box-shadow: 0px 2px 5px 0px #e0e0e0;
-moz-box-shadow: 0px 2px 5px 0px #e0e0e0;
-webkit-box-shadow: 0px 2px 5px 0px #e0e0e0;
}

div.uiAttachmentPhotoBox .uiMediaThumb, div.uiAttachmentPhotoBox div.horizontalSplitContainer {
display: inline-block;
float: left;
box-shadow: 0px 2px 5px 0px #e0e0e0;
-moz-box-shadow: 0px 2px 5px 0px #e0e0e0;
-webkit-box-shadow: 0px 2px 5px 0px #e0e0e0;
border-radius: 5px;
}

.uiSideNav .selectedItem .item, .uiSideNav .selectedItem .item:hover, .uiSideNav ul .selectedItem .subitem, .uiSideNav ul .selectedItem .subitem:hover {
background-color: #D8DFEA;
font-weight: bold;
border-top-left-radius: 5px;
border-bottom-left-radius: 5px;
}

.uiMediaThumbMedium i {
height: 85px;
width: 114px;
border-radius: 5px;
}

.uiButton, .uiButtonSuppressed:active, .uiButtonSuppressed:focus, .uiButtonSuppressed:hover {
border-radius: 5px;
}

.profile-picture img {
display: block;
margin: auto;
max-width: 180px;
border-radius: 5px;
}

#pageNav a {
color: white;
display: block;
display: inline-block;
font-weight: bold;
height: 22px;
padding: 8px 10px 0 10px;
text-decoration: none;
-webkit-transition: all 0.5s ease;
-moz-transition: all 0.5s ease;
-o-transition: all 0.5s ease;
}

.uiSideNav .item, .uiSideNav .subitem {
margin-left: 20px;
margin-right: -20px;
-webkit-user-select: none;
-webkit-transition: all 0.5s ease;
-moz-transition: all 0.5s ease;
-o-transition: all 0.5s ease;
}

.fbFeedTicker .tickerStoryClickable {
cursor: pointer;
-webkit-transition: all 0.5s ease;
-moz-transition: all 0.5s ease;
-o-transition: all 0.5s ease;
}

.uiSideNav .sideNavItem .uiCloseButton:focus, .uiSideNav .sideNavItem:hover .uiCloseButton, .uiSideNav .sideNavItem .openToggler .uiCloseButton {
opacity: 1;
-webkit-transition: all 0.5s ease;
-moz-transition: all 0.5s ease;
-o-transition: all 0.5s ease;
}

.uiPhotoThumb {
border-radius: 5px;
-webkit-transition: all 0.5s ease;
-moz-transition: all 0.5s ease;
-o-transition: all 0.5s ease;
}

.fbReminders .fbRemindersStory {
padding: 3px 5px;
-webkit-transition: all 0.5s ease;
-moz-transition: all 0.5s ease;
-o-transition: all 0.5s ease;
border-bottom-left-radius: 5px;
border-top-left-radius: 5px;
}

.fbChatMessageGroup .profileLink .profilePhoto {
display: block;
height: 32px;
width: 32px;
border-radius: 3px;
}

.fbJewelFlyout .jewelFooter a:hover, .fbJewelFlyout .jewelFooter a:active, .fbJewelFlyout .jewelFooter a:focus {
-webkit-transition: all 0.5s ease;
}

.permalinkBody #globalContainer {
margin-top: 40px;
padding: 0 40px 0;
width: 889px;
min-width: 889px;
background: white;
border-radius: 5px;
}

#globalContainer {
margin: 0 auto;
position: relative;
zoom: 1;
background: white;
width: 69%;
border-radius: 5px;
}
}
