css:
@-moz-document url("http://www.youtube.com/"), url-prefix("http://www.youtube.com/"), domain("http://www.youtube.com/") {
.ad-div.mastad {
font-size: 0;
margin-bottom: 15px;
display: none;
}

.promoted-videos-disclaimer {
position: absolute;
bottom: 2px;
right: 5px;
display: none;
}

#masthead-user-image img {
height: 32px;
width: auto;
vertical-align: middle;
}

#homepage-sidebar-ads .pyv-large-thumb {
float: right;
padding-bottom: 10px;
display: none;
}

#homepage-sidebar-ads {
min-height: 250px;
display: none;
}

#search-pva {
position: relative;
border: none;
margin-top: 0;
padding: 15px 0 10px 10px;
display: none;
}

.ytg-4col {
width: 949px;
}

.watch #watch-channel-brand-div {
left: -5px;
display: none;
}

#search-base-div .result-item, #search-base-div .sidebar-ads {
margin-bottom: 5px;
overflow: hidden;
display: none;
}

.secondary-pane .channel-module.has-visible-edge {
padding: 0 7px;
display: none;
}
}
