css:
@-moz-document url-prefix('http://www.tumblr.com'), url-prefix('https://www.tumblr.com')   {
	
body {
height: 100%!important;
background: black url(http://static.tumblr.com/uiqhh9x/CVGm6nqa9/bg.jpg) no-repeat center center fixed!important;
font: normal 14px/1.4 "Helvetica Neue", "HelveticaNeue", Helvetica, Arial, sans-serif!important;
color: #000000!important;
margin: 0!important;
padding: 0!important;
}

#posts .post {
-webkit-border-radius: 0px!important;
-moz-border-radius: 0px!important;
border-radius: 0px!important;
}

#auto_pagination_loader {
background: rgba(0, 5, 0, 0.52)!important;
border-top: solid 1px rgba(255, 255, 255, .13)!important;
display: block!important;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: rgba(0, 5, 0, 0.27)!important;
padding: 20px!important;
-webkit-border-radius: 0px!important;
-moz-border-radius: 0px!important;
border-radius: 0px!important;
}

 #right_column .controls_section {
border: 1px solid rgba(0, 0, 0, 0.27)!important;
-webkit-border-radius: 0px!important;
-moz-border-radius: 0px!important;
border-radius: 0px!important;
}
#right_column::after {
content: ' '!important;
background: transparent!important;-
}

#posts .notification {
color: rgba(255, 255, 255, .45)
border-left: solid 1px black!important;
border-right: solid 1px black!important;
border-bottom: solid 1px black!important;
position: relative!important;
background-color: rgba(0, 0, 0, 0.27)!important;
}

#posts .notification.alt {
background-color: 
rgba(255, 255, 255, 0.09)!important;
}

#right_column {
	background: transparent!important;
}

div.l-header .selection_nipple {
border-left: 12px solid transparent!important;
border-right: 12px solid transparent!important;
border-bottom: 12px solid rgba(255, 255, 255, 0.3)!important;
}

#posts .notification .nipple {
width: 0!important;
height: 0!important;
border-top: 6px solid transparent!important;
border-bottom: 6px solid transparent!important;
border-right: 6px solid rgba(255, 255, 255, 0.62)!important;
position: absolute!important;
left: -5px!important;
top: 10px!important;
}

#right_column .controls_section li a {
color: rgba(255, 255, 255, .80)!important;
}

#posts .notification.first_notification {
border-top: solid 1px rgba(255, 255, 255, 0.37)!important;
}

#posts .notification a:not(.avatar_frame) {
color: rgba(255, 255, 255, 0.71)!important;
font-weight: bold!important;
text-decoration: none!important;
border-bottom: 1px solid rgba(255, 255, 255, 0.4)!important;
-webkit-background-clip: padding-box!important;
}

.send_fan_mail {
	background: transparent!important!important;
}

#right_column .controls_section li:first-child {
-webkit-box-shadow: inset 0 1px 0 rgba(255, 255, 255, .09)!important;
-moz-box-shadow: inset 0 1px 0 rgba(255,255,255,.09)!important;
box-shadow: inset 0 1px 0 rgba(255, 255, 255, .09)!important;
-webkit-border-top-left-radius: 0px!important;
-webkit-border-top-right-radius: 0px!important;
-moz-border-radius-topleft: 0px!important;
-moz-border-radius-topright: 0px!important;
border-top-left-radius: 0px!important;
border-top-right-radius: 0px!important;
border-top-width: 0!important;
}

#right_column .controls_section li:hover {
background-color: rgba(255, 255, 255, .17)!important;
}

#right_column .controls_section li a:hover{
color: rgba(255, 255, 255, .85)!important;
}

#posts .post .footer_links .tags .tag {
margin-right: 7px!important;
color: #cccccc!important;
}

#posts .post .footer_links .tags a.tag {
text-decoration: none!important;
color: #cccccc!important;
}

#posts .post .post_question {
background: #EBEBEB!important;
}

#posts .post .permalink {
background: transparent!important;
}

}
