/*
This style is intended to provide integration of 4chan x into 4chan and 
designed for Mozilla Firefox 10.x or higher, including the Aurora and 
Nightly channels.
*/
@-moz-document domain(boards.4chan.org), domain(rs.4chan.org) {
	body::after {
		position: fixed !important;
		/*[[background]]*/
		bottom: 279px;
		/* Note: The above position is overriden in all slideout QRs, best to use "Custom OneeChan Mascot" and edit the value in that */
		left: 0;
		-moz-transform: scaleX(-1);
		-webkit-transform: scaleX(-1);
		/*[[ChangeAlign]]*/
		right: 0;
		left: auto;
		-moz-transform: scaleX(1);
		-webkit-transform: scaleX(1);/**/
	}
	body {
		padding: 16px 0 0 !important;
		/*[[Margin]]*/
	}
	@media only screen and (max-width: 1100px) {
		body {
			padding-top: 31px !important;
		}
	}
	@media only screen and (max-width:689px) {
		body {
			padding-top: 46px !important;
		}
	}
	@media only screen and (max-width:553px) {
		body {
			padding-top: 61px !important;
		}
	}
	.globalMessage {
		bottom: auto !important;
		left: 2px !important;
		left: auto !important; /**/
		padding: 10px 5px 10px 5px !important;
		position: fixed !important;
		right: 2px !important; /*[[ChangeAlign]]*/
		right: auto !important;
		top: -1000px !important;
	}

	.globalMessage b { font-weight: 100 !important; }

	.globalMessage:hover::before {
		cursor: pointer;
		opacity: 1;
	}
}
@-moz-document domain('4chan.org') {
/* Cleanup */
	#absbot,
	#ft li.fill,
	#logo,
	#postForm .refreshBtn,
	#postForm > tbody > tr > td:first-child,
	#postForm > tbody > tr:nth-of-type(7),
	#postPassword + span,
	#qr.auto:not(:hover) #recaptcha_reload_btn,
	#recaptcha_switch_audio_btn,
	#recaptcha_whatsthis_btn,
	#settingsBox[style*="display: none;"],
	.board > hr:last-of-type,
	.closed,
	.deleteform br,
	.error:empty,
	.hidden_thread > .summary,
	.navLinksBot,
	.next,
	.pages td:nth-of-type(2n-1),
	.postingMode,
	.prev,
	.qrHeader,
	.replyContainer > .hide_reply_button.stub ~ .reply,
	.replymode,
	.rules,
	.sideArrows:not(.hide_reply_button),
	.stylechanger,
	.warnicon,
	.warning:empty,
	.yui-menu-shadow,
	a[href*="jlist"],
	body > .postingMode ~ #delform hr,
	body > br,
	body > hr,
	div.reply[hidden],
	form table tbody > tr:nth-of-type(2) td[align='right'],
	form[name="post"] h1,
	html body > span[style='left: 5px; position: absolute;']:nth-of-type(0),
	table[style="text-align:center;width:100%;height:300px;"],
	td[style^='padding: ']:not([style='padding: 10px 7px 7px 7px;']):not([style='padding: 10px 7px 7px;']),
	#imgControls label:first-of-type input,
	.autoPagerS,
	#options hr,
	.inline .report_button,
	.inline input,
	.entry:not(.focused) > .subMenu,
	#autohide,
	#qr.autohide select,
	#qr.autohide .close {
		display: none !important;
	}
	html, body, input, select, textarea, .boardTitle {
/* Defaults of all elements */
		font-family: /*[[Font]]*/ !important;
	}
	div.post > blockquote .prettyprint span {
		font-family: monospace !important;
	}
	#credits a,
	.abbr a,
	.backlink:not(.filtered),
	.chanlinkify,
	.file a,
	.pages,
	.pages a,
	.quotejs,
	.quotelink:not(.filtered),
	.quotelink:not(.filtered),
	.useremail,
	a.deadlink,
	a[href*="//dis"],
	a[href*=res],
	div.post > blockquote .chanlinkify.YTLT-link.YTLT-text,
	div.postContainer span.postNum > .replylink {
		text-decoration: /*[[UnderlineLinks]]*/ !important;
	}
	a {
		outline: 0 !important;
	}
	#qp div.post .postertrip,
	#qp div.post .subject,
	.capcode,
	.container::before,
	.dateTime,
	.file,
	.fileInfo,
	.fileText,
	.name,
	.postNum,
	.postertrip,
	.rules,
	.subject,
	.subjectm
	.summary,
	a,
	blockquote,
	div.post > blockquote .chanlinkify.YTLT-link.YTLT-text,
	div.reply,
	fieldset,
	textarea,
	time + span {
		font-size: /*[[FontSize]]*/ !important;
	}
	#boardNavDesktop,
	#boardNavDesktop a,
	#boardNavDesktopFoot a,
	#count,
	#imageType,
	#imageType option
	#imgControls,
	#navtopr a[href="javascript:;"],
	#postcount,
	#stats,
	#timer,
	#updater,
	.pages a,
	.pages strong,
	body:not([class]) a[href="javascript:void(0);"],
	input,
	label {
		font-size: 12px !important;
		text-decoration: none !important;
	}
	.filtered {
		text-decoration: line-through !important;
	}
/* YouTube Link Title */
	div.post > blockquote .chanlinkify.YTLT-link.YTLT-na {
		text-decoration: line-through !important;
	}
	div.post > blockquote .chanlinkify.YTLT-link.YTLT-text {
		font-style: normal !important;
	}
	/*[[CustomScrollbars]]*/scrollbarbutton { display: none !important; }
	scrollbar, scrollcorner { -moz-appearance: none !important; width: 10px !important; height: 10px !important; }
	scrollbar[orient='vertical'] { border-left: 0; }
	scrollbar[orient='horizontal'] { border-top: 0; }
	thumb { -moz-appearance: none !important; border: 1px solid; }/**/
/* Z-INDEXES */
	#options.reply.dialog {
		z-index: 999 !important;
	}
	#autoPagerBorderPaging,
	#boardNavDesktopFoot:hover,
	#ihover,
	#menu.reply.dialog,
	#navlinks,
	#overlay,
	#qp,
	#updater:hover,
	.deleteform:hover,
	.exPopup,
	html .subMenu {
		z-index: 99 !important;
	}
	#boardNavDesktop,
	#qr,
	body > form #imgControls {
		z-index: 98 !important;
	}
	.fileText ~ a > img + img {
		z-index: 96 !important;
	}
	#boardNavMobile,
	#imageType,
	#imgControls label:first-of-type,
	#imgControls label:first-of-type::after,
	#stats,
	#updater {
		z-index: 10 !important;
	}
	#settingsBox {
		z-index: 9 !important;
	}
	.fileThumb,
	.deleteform:hover input[type='checkbox'] {
		z-index: 7 !important;
	}
	#boardNavDesktopFoot::after,
	#navtopr,
	.deleteform::before,
	.qrMessage,
	body > a[style='cursor: pointer; float: right;'],
	div.navLinks a:first-of-type::after {
		z-index: 6 !important;
	}
	#stats,
	#watcher,
	#watcher::before {
		z-index: 4 !important;
	}
	#postForm label::after,
	#postForm label input,
	body::after {
		z-index: 3 !important;
	}
	#recaptcha_reload_btn,
	.boardBanner,
	.globalMessage::before,
	.replyhider a {
		z-index: 1;
	}
	#postForm td,
	div.reply,
	div.reply.highlight {
		z-index: 0;
	}
	#settingsWindowLink::after,
	body:not([class]) a[href="javascript:void(0);"]::after {
		z-index: -1 !important;
	}
/* ICON POSITIONS */
/* 4chan X Options / 4chan Options */
	#settingsWindowLink::after, body:not([class]) a[href="javascript:void(0);"]::after {
		content: url('data:image/png;base64,');
		position: fixed !important;
		left: 230px !important;
		right: auto !important;
	/*[[ChangeAlign]]*/
		left: auto !important;
		right: 17px !important;/**/
		opacity: 0.3;
	}
/* 4sight */
	body > a[style='cursor: pointer; float: right;']::after {
		content: url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA8AAAAPCAQAAACR313BAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAH9JREFUGNONkFEZgCAMhIlABCIQgQhE8O2vQQMiEMEIi7AIRjACPqGC4ueetu/udrcZ869wBMI7ZFkREpmN+IRXlpOo+HGt3KZA6eFA6mYZ4dzlkNFbcWefW467XonGYMnU3qrFKwjCQqKi2PmD5JOARansw/0PQpkbeMpUfdUBLYs3tDb03tIAAAAASUVORK5CYII=');
		font-size: 12px !important;
		position: fixed !important;
		left: 38px;
		/*[[ChangeAlign]]*/
		right: 0px !important;
		left: auto !important;/**/
		opacity: 0.3;
	}
/* Back */
	div.navLinks a:first-of-type::after {
		content: url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA8AAAAPCAQAAACR313BAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAGNJREFUGNNjYKAaiC2IXY9LyiH2fuz/2P3YpBRi9wOl/mORjhWIbYBKgeB7oEIIbIgVAEnfR5JEhf2Yuu8DeQ2x/UBTgCYh7J6PajdEC6rL9+ORBgsGgO3DJY2kMAHkegbaAgCK4libswvDKwAAAABJRU5ErkJggg==');
		position: fixed !important;
		left: 2px !important;
		right: auto !important;
		/*[[ChangeAlign]]*/
		left: auto !important;
		right: 230px !important;/**/
		cursor: pointer !important;
		-moz-transform: scale(.8);
		-webkit-transform: scale(.8);
		opacity: 0.4;
		bottom: 1px !important;
		top: auto !important;
	}
/* Delete Form */
	.deleteform::before {
		content: url('data:image/png;base64,') !important;
		visibility: visible !important;
		position: fixed !important;
		left: 20px !important;
		right: auto !important;
		/*[[ChangeAlign]]*/
		right: 210px !important;
		left: auto !important;/**/
		-moz-transform: scale(.9);
		-webkit-transform: scale(.9);
		opacity: 0.4;
		top: auto !important;
		bottom: 2px !important;
	}
/* Expand Images */
	#imgControls label:first-of-type::after {
		content: url('data:image/png;base64,');
		opacity: 0.2 !important;
		position: relative !important;
		top: 4px !important;
	}
/* Global Message */
	.globalMessage::before {
		content: url('data:image/png;base64,');
		height: 9px !important;
		position: fixed !important;
		left: 165px !important;
		right: auto !important;
		/*[[ChangeAlign]]*/
		right: 70px !important;
		left: auto !important;/**/
		min-width: 30px !important;
		max-width: 30px !important;
		padding-bottom: 5px !important;
		opacity: 0.4;
	}
/* Navigation / Bot */
	#boardNavDesktopFoot::after {
		border: none !important;
		position: fixed !important;
		right: auto !important;
		left: 212px !important;
		/*[[ChangeAlign]]*/
		left: auto !important;
		right: 37px !important;/**/
		content: url('data:image/png;base64,');
		opacity: 0.4;
	}
/* Watcher */
	#watcher::before {
		content: url('data:image/png;base64,');
		height: 9px !important;
		font-size: 12px !important;
		position: fixed !important;
		left: 193px !important;
		right: auto !important;
		/*[[ChangeAlign]]*/
		right: 42px !important;
		left: auto !important;/**/
		min-width: 30px !important;
		max-width: 30px !important;
		opacity: 0.4;
	}
/* END OF ICON POSITIONS */
	a.yuimenuitemlabel:hover,
	.sideArrows a:hover {
		background-color: transparent !important;
	}
	#settingsBox {
		position: fixed !important;
		left: 5px !important;
		/*[[ChangeAlign]]*/
		left: auto !important;
		right: 5px !important;/**/
		width: 234px !important;
	}
	#boardNavMobile {
		background: none !important;
		border: none !important;
		font-size: 12px !important;
		padding: 0px !important;
		padding-top: 1px !important;
		padding-left: 2px !important;
		width: 320px !important;
		pointer-events: none;
	}
	.pageJump {
		position: fixed !important;
		top: -1000px !important;
		pointer-events: all;
	}
	.extButton img {
		margin-top: -4px !important;
	}
	#boardNavMobile select {
		font-size: 11px !important;
		pointer-events: all;
	}
	.qrMessage {
		position: fixed !important;
		left: 3px !important;
		/*[[ChangeAlign]]*/
		left: auto !important;
		right: 3px !important;/**/
		bottom: 250px !important;
		font-size: 11px !important;
		font-weight: 100 !important;
		background: none !important;
		border: none !important;
		width: 248px !important;
	}
	.boardBanner {
		position: fixed !important;
		left: 2px !important;
		right: auto !important;
		/*[[ChangeAlign]]*/
		left: auto !important;
		right: 2px !important;/**/
		top: 19px !important;
		width: 248px !important;
		margin: 0 !important;
		margin-top: -6px !important;
	 }
	.boardBanner img {
		width: 248px !important;
		height: 83px !important;
	}
	/*[[boardlogo]]*/
	.boardTitle {
		font-size: 30px !important;
		font-weight: 400 !important;
	}
	.boardSubtitle {
		font-size: 13px !important;

	}
/* 4watch */
	body > span > div {
		position: fixed !important;
		top: auto !important;
		bottom: 440px !important;
		left: 0 !important;
		right: auto !important;
		/*[[ChangeAlign]]*/
		left: auto !important;
		right: 0 !important;/**/
		width: 248px;
	}
	hr {
		padding: 0 !important;
		height: 0;
		width: 100% !important;
		clear: both;
		border: none !important;
	}
/* Front Page */
	.bd,
	.bd ul,
	img,
	.pages,
	.pages *,
	#qr,
	div[id^='qr'],
	table.reply[style^='clear: both'],
	.boxcontent > hr,
	h3 {
		border: none !important;
	}
	.boxcontent input {
		height: 18px !important;
		vertical-align: bottom;
		margin-right: 1px !important;
	}
	a.yuimenuitemlabel {
		padding: 0 20px !important;
	}
/* Navigation */
	#boardNavDesktop, /* Top Navigation */
	.pages /* Bottom Navigation */ {
		text-align: center !important;
		font-size: 0 !important;
		color: transparent !important;
		width: auto !important;
	}
	#boardNavDesktop{
		/* cripple the positioning of the top navigation */
		position: static !important;
		width: auto !important;
		padding-right: 0px !important;
		margin-right: 0px !important;
	}
	/*[[StickyNavigation]]*/
	#boardNavDesktopFoot:hover::after {
		opacity: 1;
		cursor: pointer;
	}
	#boardNavDesktopFoot {
		visibility: visible !important;
		position: fixed !important;
		position: fixed !important;
		top: -1000px !important;
		right: auto !important;
		left: 2px !important;
		/*[[ChangeAlign]]*/
		right: 2px !important;
		left: auto !important;/**/
		bottom: auto !important;
		width: 226px !important;
		color: transparent !important;
		padding: 3px 10px 35px 10px !important;
		border-width: 0 1px 1px 0 !important;
		word-spacing: -2px !important;
		height: 50px !important;
		text-align: center !important;
		word-spacing: -3px !important;
	}
	.fileThumb {
		position: relative !important;
	}
	.pages td:nth-of-type(1) {
		font-size: 75% !important;
		text-transform: uppercase;
	}
	.pages td {
		color: transparent !important;
	}
	#boardNavDesktop a,
	.pages a,
	.pages strong,
	.pages input {
		-moz-appearance: none;
		-webkit-appearance: none;
		display: inline-block;
		font-size: 12px !important;
		text-align: center;
		border: none !important;
		text-align: center;
		margin: 0 1px 0 2px;
	}
	.pages {
		word-spacing: 10px !important;
	}
/* moot's announcements */
	h3, h2, h1, .globalMessage {
		font-size: 12px !important;
	}
	h3, .globalMessage {
		font-weight: 200 !important;
	}
	.pages input {
		margin-bottom: 2px !important;
	}
	.pages strong,
	.pages input,
	a,
	.new {
		-moz-transition: background-color .1s linear;
		-webkit-transition: background-color .1s linear;
	}
/* Post Form */
/* Override OS-specific UI */
	#ft li,
	#ft ul,
	#options input:not([type='checkbox']):not([type='radio']),
	#updater input:not([type='checkbox']):not([type='radio']),
	.box-outer,
	.boxbar,
	.deleteform input[value=Delete],
	.recaptcha_image_cell > center > #recaptcha_image,
	[name='recaptcha_response_field'],
	.top-box,
	h2,
	input:not([type='checkbox']):not([type='radio']),
	input:not([type='radio']),
	input[type='file'] > input[type='button'],
	input[type='submit'],
	select,
	textarea {
		-moz-appearance: none;
	}
	#postForm > table > tbody > tr > td {
		padding: 0 !important;
	}
/* Unfuxor the Captcha layout */
	#recaptcha_widget_div tr, #recaptcha_widget_div td, #recaptcha_widget_div center, #recaptcha_widget_div #recaptcha_table, #recaptcha_widget_div #recaptcha_area, #recaptcha_widget_div #recaptcha_image {
		margin: 0 !important;
		padding: 0 !important;
		height: auto !important;
	}
	#recaptcha_table #recaptcha_image {
		border: none !important;
	}
	#postForm #recaptcha_widget_div {
		height: 69px;
	}
/* Collapse the postarea table for better control over the height and width */
	#postForm {
		border-collapse: collapse;
	}
/* Formatting for all postarea elements */
	.recaptchatable #recaptcha_response_field,
	.deleteform input[type='password'],
	input,
	input.field,
	input[type='submit'],
	input[type='file'] > input[type='button'],
	input[type='file'] > input[type='text'],
	textarea {
		border-width: 1px !important;
		border-style: solid !important;
		padding: 1px !important;
		height: 20px;
	}
	#postForm input:not([type='checkbox']):not([type='radio']),
	#qr .move .field,
	#postForm textarea,
	#postForm #recaptcha_widget_div input,
	#postForm #recaptcha_widget_div #recaptcha_image img,
	#qr input[type='submit'],
	input[type='file'],
	#qr textarea,
	#qr .field {
		margin: 1px 0 0 !important;
		vertical-align: bottom;
	}
/* Width and height of all postarea elements (excluding some captcha elements) */
	#recaptcha_response_field,
	#postForm textarea,
	textarea.field,
	#postForm input,
	#postForm .field,
	#recaptcha_widget_div input,
	#qr .move .field,
	#qr .field[type='password'],
	.ys_playerContainer audio,
	#qr input[title="Verification"],
	#postForm > table,
	#recaptcha_image,
	#qr div,
	input[type='file'] {
		width: 248px !important;
	}
/* File and Subject inputs */
	#qr input[type='file'] > input[type='text'],
	#postForm input[type='file'] > input[type='text'] {
		width: 185px !important;
	}
	input[type='file'] > input[type='button'],
	#postForm input[type='file'] > input[type='button'],
	#qr input[type='file'] > input[type='text'],
	#postForm input[type='file'] > input[type='text'] {
		margin-top: 0 !important;
	}
/* Buttons */
	input[type='submit'], /* Any lingering buttons */
	input[value="Report"],
	input[type='file'] > input[type='button'],
	#postForm input[type='file'] > input[type='button'] /* Browse Button (Fx*) */ {
		cursor: default !important;
		height: 20px !important;
		padding: 0 !important;
		font-size: 12px !important;
	}
	#postForm [type='submit'],
	#qr input[type='submit'] {
		width: 100% !important;
		float: left !important;
		clear: both !important;
	}
	#qr input[type='file'] > input[type='button'],
	#postForm input[type='file'] > input[type='button'] {
		width: 62px !important;
		margin-right: 1px !important;
	}
/* Force the file input's CONTAINER to behave as if it were non-existent, e.g. occupies no more space than its contents */
	#qr input[type='file'],
	#postForm input[type='file'] {
		height: auto !important;
		border: none 0px !important;
		padding: 0 !important;
		float: left !important;
	}
/* Positioning of various postarea checkboxes */
	#postForm label {
		position: absolute;
	}
	#postForm input[name='sub'] + input + label {
		font-size: 12px !important;
		top: auto !important;
		bottom: 0px;
		right: 20px;
		vertical-align: bottom;
	}
	#qr input[name='email'] + label {
		bottom: 2px;
		right: 4px;
	}
	#qr input[name='sub'] + input + label {
		font-size: 12px !important;
		top: auto !important;
		right: 70px;
		margin-top: 1px;
	}
/* Image Hover and Image Expansion */
	#ihover {
		max-width:85% !important;
		max-height:85% !important;
	}
	#qp {
		min-width: 500px !important;
	}
	.fileText ~ a > img + img {
		position: relative !important;
		top: 0px !important;
