css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain('www.tumblr.com'), url-prefix('www.tumblr.com/'),  url-prefix('https://www.tumblr.com/'),  url('https://www.tumblr.com/login'),


  url('http://www.tumblr.com/dashboard'),


  url('http://www.tumblr.com/new/blog'),



  url('http://www.tumblr.com/inbox'),

  url('http://www.tumblr.com/help'),



  url('https://www.tumblr.com/preferences'),

  url('http://www.tumblr.com/lookup'),


  url('http://www.tumblr.com/explore'),


  url('http://www.tumblr.com/following'),


  url('http://www.tumblr.com/likes'),


  url-prefix('http://www.tumblr.com/new/'),



  url-prefix('http://www.tumblr.com/tumblelog/'),


  url-prefix('http://www.tumblr.com/tagged/'),


  url-prefix('http://www.tumblr.com/spotlight/'),


  url-prefix('http://www.tumblr.com/edit/') , url("https://www.tumblr.com/dashboard"), url("https://www.tumblr.com/new/blog"), url("https://www.tumblr.com/inbox"), url("https://www.tumblr.com/help"), url("https://www.tumblr.com/lookup"), url("https://www.tumblr.com/explore"), url("https://www.tumblr.com/following"), url("https://www.tumblr.com/likes"), url-prefix("https://www.tumblr.com/new/"), url-prefix("https://www.tumblr.com/tumblelog/"), url-prefix("https://www.tumblr.com/tagged/"), url-prefix("https://www.tumblr.com/spotlight/"), url-prefix("https://www.tumblr.com/edit/") {
body{
font-family: Trebuchet MS, Georgia, Times New Roman, Serif !important;

background-image: url('http://static.tumblr.com/usldvzy/EHYm6nynh/copy__2__of_picture40.png');
background-color: #ffffff !important;
background-repeat: repeat-x !important;
background-attachment: fixed !important;
background-position: bottom left !important;
background-size:100% auto  !important;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

background-color: #668ba5 !important;}

#left_column .dashboard_options_form {
background: url('http://static.tumblr.com/usldvzy/o3Pm6nzf6/white_20z.png') #000000;}

ol#posts li.notification{background-color: #527b99 !important;
border-bottom: 1px dashed #7a9eb6 !important;
color: #A8BCCB !important;}


#right_column a#dashboard_switch_blog_menu_current:hover {background: transparent;}

#right_column .radar .content{background:transparent!important;}

#right_column .radar .content .mask{background:transparent!important;}

.selection_nipple:not([class="selection_nipple white"]) {
border-bottom: solid 11px rgba(86,117,139, 1.0)  !important;}

#nav .nav_item.active{background-color:#527b99;}

#left_column #posts .post .permalink{background-color:#56758B;}
#left_column #posts .post .sub_avatar_inner {












-moz-border-radius: 38px !important; -webkit-border-radius: 60px !important; width: 32px !important; height: 32px !important; margin: 0px !important;












}
#nav .nav_item.active, #nav .nav_item .nav_menu, 
#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel{
background-color:#56758B;}

#footer, #footer a {
color:#dddddd !important;
background-image:none !important;}

div.l-header, div.l-header a {
color:#527b99 !important;
background-image:none !important;text-transform: uppercase;}

.post.same_user_as_last:hover, .post.text_post:hover, 
.post.video:hover, .post.audio:hover, .post.photo:hover, 
.post.link:hover, .post.quote:hover, .post.conversation:hover, 
.post.is_reblog:hover, .post.submission:hover, .post.regular:hover, 
.post.question_post:hover, .post.note:hover {
  border-top-right-radius: 24px !important; 
  opacity: 1.00 !important;
}
.permalink {
  background: none !important;
  width: 16px !important;
  height: 16px !important;
  background: rgba(20,20,20, .85) !important;
  border-bottom-left-radius: 10px !important;
  border-top-right-radius: 24px !important;
  margin-top: 6px !important;
  margin-right: 6px !important;
}
 .permalink:hover {background: rgba(86,117,139, 1.0 ) !important;}


}
