@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain("scienceblogs.de")
{
	
/* ===================================== 
   Flash Animation
   Animation by Daniel Eden
   https://daneden.github.io/animate.css/
   ===================================== */
	@-webkit-keyframes flash {
		from, 50%, to { opacity: 1; }
		25%, 75% { opacity: 0.5; }
	}
	
	@-moz-keyframes flash {
		from, 50%, to { opacity: 1; }
		25%, 75% { opacity: 0.5; }
	}
	
	@keyframes flash {
		from, 50%, to { opacity: 1; }
		25%, 75% { opacity: 0.5; }
	}
	
	
	
/* ===================================== 
   Pulse Animation
   Animation by Daniel Eden
   https://daneden.github.io/animate.css/
   ===================================== */
   
	@-webkit-keyframes pulse
	{
		from { transform: scale3d(1, 1, 1); }
		50% { transform: scale3d(1.5, 1.5, 1.5); }
		to { transform: scale3d(1, 1, 1); }
	}
	
	@-moz-keyframes pulse
	{
		from { transform: scale3d(1, 1, 1); }
		50% { transform: scale3d(1.5, 1.5, 1.5); }
		to { transform: scale3d(1, 1, 1); }
	}
	
	@keyframes pulse
	{
		from { transform: scale3d(1, 1, 1); }
		50% { transform: scale3d(1.5, 1.5, 1.5); }
		to { transform: scale3d(1, 1, 1); }
	}
	
/* ===================================== 
   LightSpeedIn Animation
   Animation by Daniel Eden
   https://daneden.github.io/animate.css/
   ===================================== */
   
	@keyframes lightSpeedIn
	{
		from {
			-webkit-transform: translate3d(100%, 0, 0) skewX(-30deg);
			transform: translate3d(100%, 0, 0) skewX(-30deg);
			opacity: 0;
		}

		60% {
			-webkit-transform: skewX(20deg);
			transform: skewX(20deg);
			opacity: 1;
		}

		80% {
			-webkit-transform: skewX(-5deg);
			transform: skewX(-5deg);
			opacity: 1;
		}

		to {
			-webkit-transform: none;
			transform: none;
			opacity: 1;
		}
	}
	
	
	
/* ===================================== 
   Regular HTML-Tags
   ===================================== */
	
	body { color: #FFFFFF !important; }
	
	a { color: #EEEEFF !important; }
	
	p { color: #ADADAD !important; }
	
	p > a
	{
		color: #ADADAD !important;
		font-weight: bold !important;
	}
	
	div
	{
		background-color: black !important;
		color: #ADADAD !important;
	}
	
	
	
/* ===================================== 
   Page Top Partner Navigation
   ===================================== */
	
	/* Page partner nav active background (wissen.de, ...) */
	div[id="partner-nav-wrapper"] > div[class="center-content"] > ul > li[class="active"]
	{
		
		background-color: #AEAEAE !important;
		-webkit-border-radius: 10px 0px 10px 10px;
		-moz-border-radius: 0px 0px 10px 10x;
		border-radius: 0px 0px 10px 10px;
	}
	
	/* Page partner nav inactive link (wissen.de, ...) */
	div[id="partner-nav-wrapper"] > div[class="center-content"] > ul > li > a
	{
		color: #CECECE !important;
	}
	
	/* Page partner nav active link (wissen.de, ...) */
	div[id="partner-nav-wrapper"] > div[class="center-content"] > ul > li[class="active"] > a
	{
		color: #FFFFFF !important;
	}
	
	
	
/* ===================================== 
   Headerbox Top Navigation
   ===================================== */
	
	/* Page headerbox top background */
	div[class*="blog-nav-wrapper"]
	{
		background-color: #DFDFDF !important;
		z-index: 0 !important;
	}
	
	/* Facebook/Twitter icons background */
	div[class="social-nav-wrapper"]
	{
		background-color: #DFDFDF !important;
	}
	
	/* Page headerbox bottom background */
	div[class="topic-nav-wrapper"]
	{
		background-color: #cccccc !important;
		border-color: #FFFFFFF !important;
		border-width: 1px !important;
		border-bottom: 1px solid #FFFFFFF !important;
		width: 100% !important;
	}
	
	/* Page headerbox top navigation (Blogs, Blogger, eMagazines, Jobs) */
	ul[class="nav"]
	{
		top: -2px !important;
	}
	
	/* RSS & Atom */
	a[class*="imr"][class*="imr-rss"],
	a[class*="imr"][class*="imr-atom"]
	{
		color: #333333  !important;
		text-shadow: 2px 2px #CDCDCD;
	}
	
	
	
/* ===================================== 
   Headerbox Bottom Navigation
   ===================================== */
	
	/* Navigation bottom (Naturwissenschaften, ...) */
	li[id^="nav-item"]
	{
		-webkit-border-radius: 14px 14px 0px 0px;
		-moz-border-radius: 14px 14px 0px 0px;
		border-radius: 14px 14px 0px 0px;
		background-color: #FFFFFF !important;
	  margin-left: 2px;
		margin-top: 5px;
	}
	
	/* Navigation bottom link */
	li[id^="nav-item"] > a
	{
		color: #000 !important;
		padding:0px !important;
	}


	
/* ===================================== 
   Headerbox Searchform
   ===================================== */
	
	/* Page headerbox top search form */
	li[class^="contains-input"] form
	{
		color: #FFFFFF !important;
		background-color: #DFDFDF!important;
		margin-left: 20px !important;
		border: 1px inset #FFF !important;
	  top: 5px !important;	
	}
	
	/* Page headerbox top search box animation reset */
	li[class^="contains-input"]
	{
		opacity: 0.3 !important;
		-webkit-transition: opacity 0.5s !important;
		-moz-transition:  opacity 0.5s !important;
		-o-transition: opacity 0.5s !important;
	}
	
	/* Page headerbox top search box animation trigger */
	li[class^="contains-input"]:hover
	{
		opacity: 1 !important;
	}
	
	/* Page headerbox top search box caption (Suchen) */
	li[class^="contains-input"]  > form  > input
	{
		text-align: right !important;
		font-size: 8pt;
	}
	
	
	
/* ===================================== 
   Logo swagness
   ===================================== */
	
	/* Shameless plug logo animation */ 
	a[id="logo"]
	{
    animation-name: lightSpeedIn !important;
    animation-duration: 5s !important;
		animation-iteration-count: 1 !important;
	}
	
	
	
/* ===================================== 
   Wissen.de Searchbutton
   Right page nav "Partner von wissen.de"
   ===================================== */
	
	span[class="logo"]
	{
		background-color: black !important;
		-webkit-border-radius: 12px 12px 9px 9px;
		-moz-border-radius: 12px 12px 9px 9px;
		border-radius: 12px 12px 9px 9px;
	}
	
	
	
/* ===================================== 
   Social
   ===================================== */
	
	div[id="text-4"] span
	{
		background-color: #AAAAAA !important;
		-webkit-border-radius: 0px 12px 9px 9px;
		-moz-border-radius: 0px 12px 9px 9px;
		border-radius: 1px 10px 10px 10px;
		padding: 0.5px;
	}
	
	
/* ===================================== 
   Tag-cloud
   ===================================== */
	
	/* Tag cloud: cheap bling bling */
	div[id="cf_tag_cloud-2"]
	{
    
		padding: 10px;
		border: none !important;
	}
	
	/* Tag cloud: Link */
	a[class^="tag-link"]
	{
		display: inline-block !important;
		color:#DFDFDF !important;
		transition-property: opacity;
    transition-duration: 1s;
	}
	
	a[class^="tag-link"]:hover
	{
		-webkit-animation: flash 2s linear infinite !important;
    -moz-animation: flash 2s linear infinite !important;
    animation: pulse 2s linear infinite !important;
	}
	
	/* Tag cloud: clear bottom border*/
	div[id*="text-5"]
	{
		border: none !important;
	}

	
	
/* ===================================== 
   Article-list
   ===================================== */
	/* Article teaser 1st headline */
	a[class="blog-link"]
	{
		color: #DEDEDE !important;
		letter-spacing:3px !important;
		font-variant:  unicase !important;
		font-size:0.7em !important;
	}
	
	/* Article teaser 2nd headline */
	a[rel="bookmark"]
	{
		color: #CDCDCD !important;
	  font-variant: all-petite-caps;
	}
	
	/* Article teaser open link (weiterlesen) */
	a[class="read-more"]
	{
		color: #FFFFFF !important;
		font-variant: all-petite-caps !important;
		text-decoration: underline !important;
	}
	
	li[class^="menu-item"],[class^="blogs"]
	{
		padding-left: 4px !important;
		padding-right: 4px !important;
		margin-right: -2px !important;
		padding: 0px 2px 0px 2px;
		background-color: #000 !important;
		height: 30px !important;
		-webkit-border-radius: 0px 0px 15px 15px;
		-moz-border-radius: 0px 0px 15x 15px;
		border-radius: 0px 0px 15px 15px;
	}
	
	li[class^="menu-item"] > a ,[class^="blogs"]  > a
	{
	 color: #FFF !important;
	}	
	
	
	
/* ===================================== 
   Page Bottom Navigation
   ===================================== */
	
	/* Page bottom link (Naturwissenschaften, Medizin, ...) */
	ul[class=" topic-menu"] > li[id*="nav-item"] > a
	{
		color: white !important;
	}
	
	/* Page bottom link - Clear background and border */
	ul[class=" topic-menu"] > li[id*="nav-item"]
	{
		border: none !important;
		background-color: black !important;
		-webkit-border-radius: 0px 0px 0px 0px;
		-moz-border-radius: 0px 0px 0px 0px;
		border-radius: 55px 0px 0px 0px;
	}
	
	
