css:
.button {
background-image: -webkit-linear-gradient(#f5f7fa 60%, #e5eaed 100%);
background-image: -moz-linear-gradient(#f5f7fa 60%, #e5eaed 100%);
}
.Album .metadata {
line-height: 17px;
}
.Artist .stats, .Album .release_date, .Album .artist_title, .Album .metadata, .Track .metadata, .Person .info {
font-size: 11px !important;
}
.Scrollable.allowMask > .scroller > * {
padding-top: 63px;
}
.Slider .track {
height: 8px !important;
background-color: #fff !important;
padding: 1px;
border: 1px solid #d2d4d6;
}
.Slider .track .bar {
left: 0;
height: 100%;
background-color: #89949B;
}
.App_PlayerFooter .right_controls .volume {
padding: 13px 0;
}
.Expander, .Profile_Queue_Station .header .title span, .Profile_Queue_List .header, .App_PeopleSidebar .people_tabs li {
font-size: 11px;
}
.App_Header, .footer {
background: #EDF0F2 !important;
box-shadow: none;
-moz-box-shadow: none;
}
.App_Header {
height: 44px !important;
border-bottom: 1px solid #d2d4d6;
}
.App_Header .logo.rdio {
background-position: 0 center;
height: 100%;
}
.App_Header .user_nav {
margin: 5px 15px 0 0;
}
.footer {
border-top: 1px solid #d2d4d6;
}
.App_Header .search_input, .App_Header input {
background-color: #F2F4F6 !important;
} 
.App_Header .Search_Box .search_box_container .search_input {
box-shadow: none;
-moz-box-shadow: none;
border: 1px solid #caccce;
border-top: 1px solid #c0c2c4;
}
.App_Header .search_container {
padding-top: 10px;
}
.App_MainNav .section {
padding: 0 0 14px 0;
}
.App_Main .App_MainNav {
    margin: 0 !important;
    background-color: #F8FAFA !important; /* f1f4f7 */
    border-right: 1px solid #f1f4f7;
    xxborder-right: 1px solid #e1e6e9;
}

.App_Main .app_main > .Scrollable {
    margin-left: 218px !important;
}
.App_MainNav li a {
line-height: 14px;
}
.App_MainNav li a,
.App_MainNav .section .Expander {
    padding-left: 20px !important;
    padding-right: 20px !important;
}

.App_MainNav li a {
    font-size: 12px !important;
padding: 2px 5px;
}
.App_MainNav .hover {
background: transparent !important;
}

.App_MainNav li.selected a {
    background: #1babf4 !important;
    background: -moz-linear-gradient(top,  #1babf4 0%, #2597d3 100%) !important;
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#1babf4), color-stop(100%,#2597d3)) !important;
    background: -webkit-linear-gradient(top,  #1babf4 0%,#2597d3 100%) !important;
    background: -o-linear-gradient(top,  #1babf4 0%,#2597d3 100%) !important;
    background: linear-gradient(to bottom,  #1babf4 0%,#2597d3 100%) !important;
    color: #fff !important;
    font-weight: 600 !important;
}
.App_MainNav li span {
font-size: 11px !important;
white-space: nowrap;
}
.App_MainNav li.selected span {
color:#fff !important;
}
.App_MainNav .section .playlist_search .playlist_search_wrapper {
    padding-left: 10px !important;
}
.App_PlayerFooter .album_link {
top: 0;
left: 0;
}
.App_PlayerFooter .album_link img {
width: 91px;
height: 91px;
}
.App_PlayerFooter .text_metadata {
left: 110px;
}
.App_PlayerFooter .left_controls {
left: 99px;
}
.App_PlayerFooter .progress {
left: 185px;
margin-top: 31px;
}
