@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mail.google.com") {
	/* Hide status messages */
	tbody.vC tr.vm td.vn span.vG {display:none !important;}
	tbody.vC tr.vl td.vn span.vG {display:none !important;}

	/* Hide invisibility alert */
	div[role="alert"].ul {display: none !important;}

	/* Hide bottom tablist */
	div[role="tablist"][gh="gt"] {display: none !important;}
}
