css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("4chan.org") {
    body {
        background: #2C2C2C !important;
		color: #eee !important;
    }
    
    hr {
        background-color: #000 !important;
        color: #000 !important;
	}
	
	.globalMessage {
	   display: none !important;
    }
    
    div#absbot {
        display: none !important;
    }
	
	hr {
        border-right: medium none !important;
        border-width: 1px medium medium !important;
        border-style: solid none none !important;
        border-color: rgb(183, 197, 217) -moz-use-text-color -moz-use-text-color !important;
        -moz-border-top-colors: none;
        -moz-border-right-colors: none;
        -moz-border-bottom-colors: none;
        -moz-border-left-colors: none;
        -moz-border-image: none;
        height: 0px;
    }
    
    a {
		color: #efefef !important;
	}
	
	#boardNavDesktop, #boardNavDesktopFoot, #boardNavMobile {
		color: #ABABAB !important;
	}
	
	#recent-images li {
		background-color: transparent !important;
	}
	
	.new {
		background: rgba(255,255,255,.4) !important;
		border-radius: 5px;
	}
	
	#updater:hover, div#thread_filter.autohide:hover, .box-outer, #preview-tooltip-nws, #preview-tooltip-ws, #disclaimer-dialog, .yuimenu .bd, .reply, #backwash_tooltip {
		background-color: #333 !important;
		border: 1px solid #555 !important;
		color: #eee !important;
		border-radius: 8px;
	}
	
	.pagelist {
	    color: #ABABAB !important;
		background: #444 !important;
		border: 1px solid #555 !important;
	}
	
	.pagelist > div a, .pagelist > div strong {
		color: #eee !important;
	}
	
	.post.reply.highlight {
		background-color: #4B4B4B !important;
		border: 1px solid #4B4B4B !important;
		color: #eee !important;
	}
	
	select {
		background: #444 !important;
		color: #aaa !important;
		border: 1px solid gray;
		-moz-appearance: none;
		-webkit-appearance: none;
		font-size: 9pt !important;
	}

	input[type="checkbox"] {
		-moz-appearance: none !important;
		-webkit-appearance: none !important;
		background: rgba(0,0,0,.4) !important;
		border: 1px solid rgba(255,255,255,.3) !important;
		height: 10px !important;
		width: 10px !important;
		border-radius: 2px !important;
	}

	.reply input[type="checkbox"] {
		border: 1px solid rgba(255,255,255,.3) !important;
	}

	input[type="checkbox"]:checked {
		background: rgba(255,255,255,.4) !important;
	}
	
	div[style="width: 100%; font-size: 4px; background-color: rgba(224,64,0,1);"] {
		display: none !important;
	}
	
	pre, textarea, input:not([type="button"]):not([type="checkbox"]):not([type="submit"]):not([type="reset"]), select, input[type="file"] > input[type="text"] {
		-moz-appearance: none;
		-webkit-appearance: none;
		font-size: 9pt ! important;
		color: #fff !important;
		background: #333 !important;
		border-color: gray !important;
		border: 1px solid;
	}
}
