css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {



Body{



color:#565656 !important;



font-size:11px !important;



background-image:none !important;



}



*{font-family: Georgia, Times New Roman, Serif !important; font-style:italic !important}







a, a:visited{color:#666 !important}



a:hover{color:#333 !important}







#dashboard_index, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts, #dashboard_post_queue,#dashboard_likes, #dashboard_followers, #dashboard_following, #dashboard_members, #main_tag_beta, #dashboard_upload_static_file, #main_about, #main_doc_page, #main_themes, #main_meetups, #main_jobs, #main_directory_page, #dashboard_settings, #dashboard_goodies, #dashboard_tumblelog_settings, #dashboard_inbox  {



background-image:url('http://i52.tinypic.com/6ft7v8.png') !important;



background-repeat:repeat !important;



background-attachment:fixed !important;



background-color:#513660 !important;



background-position:top right



}







#dashboard_index div.l-content,#dashboard_submissions div.l-content,#dashboard_drafts div.l-content,#dashboard_post_queue div.l-content,#dashboard_likes div.l-content,#main_tag_beta #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {



background: rgba(0,0,0,0) !important;



}







#dashboard_edit_post{



background-color: #fff !important



}







#left_column #posts .post .permalink {



background-color:#ccc !important;



-moz-border-radius: 0px 11px 0px 0px !important;



-webkit-border-radius: 0px 11px 0px 0px !important;



}







#left_column #posts .post .post_avatar,



#left_column #posts .post .sub_avatar .sub_avatar_inner {



background-color: #fbfbfb !important;



border-bottom:0px !important;



}







#left_column #posts .post{



border-bottom:0px #1f0b1b solid !important



}







#nav .nav_item.active,



#nav .nav_item .nav_menu,



#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {



background-color:#fff !important;



background-image:none !important;



border:0px !important;



background: rgba(256,256,256,0.4) !important;



font-family: Georgia !important;



font-style: italic !important;



}







#nav .nav_item {



color:#fff !important;



font-family: Georgia !important;

font-size:16px !important;

font-style: italic !important;

text-shadow: 1px 1px 2px #444;

font-weight:bold !important



}







#nav .nav_item:hover {



color:#eee !important;



}







#nav .nav_item .nav_menu {



border-bottom:0px !important



}







#nav .nav_item .nav_menu a {



color:#ccc !important;

text-shadow: none;

border-top:0px !important;

margin-top:0px !important;

}







#nav .nav_item .nav_menu a:hover {



color:#a895b3 !important;



background: rgba(256,256,256,0.7) !important;



}







#footer{



color:#666 !important;



background: rgba(256,256,256,0.8) !important;



background-image:none !important;



text-shadow:none !important;



font-family: Georgia !important;



font-style: italic !important;



}



#footer a{color:#777 !important;}







.no_posts_found, h1.dashboard_header, h1.dashboard_header a, h1.dashboard_header span {



color:#ccc !important



}







#right_column .dashboard_nav_item {



border-bottom:0px solid rgba(0,0,0,0.5) !important;



background-image:none  !important



}



                    #right_column .dashboard_nav_item a,



                    #right_column .dashboard_nav_item ul.dashboard_subpages,



                    #right_column .dashboard_nav_item ul.dashboard_subpages li a {



                        color:#fff !important;



}







#right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {



                        color:#fff !important ;



                    }







#right_column .dashboard_nav_item .dashboard_nav_title{color:#fff !important}



#right_column .dashboard_nav_item .dashboard_nav_title a{color:#fff !important}







#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {



                        background: none !important;



                    }







#show_radar a, #hide_radar a,



                        #hide_recommended_blogs a, #show_recommended_blogs a {



                            color:#fff !important;



background: none !important;



                        }



#dashboard_controls_radar_media{background: none !important;}



#dashboard_controls_suggested_blogs{background-color: rgba(256,256,256,0.4) !important; border:0px !important}



#suggested_blog_avatar_1,#suggested_blog_avatar_2,#suggested_blog_avatar_3{border:0px !important}



.dashboard_controls_suggested_blog{border:0px !important}







#left_column #posts .notification {



                        background-color:#fbfbfb !important;



                        color:#666 !important;



                        border-bottom-color: #eee !important



                    }







#left_column #posts .notification .block {



                            color:#333 !important;



                        }







#left_column #posts .notification.alt {



                        background-color: #f7f7f7 !important;



                    }



#left_column #posts .notification.last_notification,



                    #left_column #posts .notification.single_notification {



                        border-bottom:0px !important;



                    }



#left_column #posts .notification a {



                        color:#565656 !important;



                        border-bottom:0px !important;



                    }



#left_column #posts .notification blockquote {



                        border-left-color:#eee !important;



                    }







#left_column #posts .notification .avatar {



                        border-color:transparent !important;



                        background-color:#fbfbfb !important;



opacity:0.5 !important;



                    }







#left_column #posts .notification .avatar:hover{



opacity:1 !important;



border-bottom:0px !important;



                    }







#dashboard_switch_blog_menu{



background-color: rgba(256,256,256,0.8) !important;



text-shadow:none !important;



border:0px !important;



font-family: Georgia !important;



font-style: italic !important;



}



#dashboard_switch_blog_menu_header, #dashboard_switch_blog_arrow_and_divider,.dashboard_switch_blog_menu_item,#dashboard_switch_blog_menu_footer {



text-shadow:none !important;



border:0px !important;



color:#555 !important;



font-family: Georgia !important;



font-style: italic !important;



}



.dashboard_switch_blog_menu_item a{



border:0px !important;



color:#ccc !important;



}



.dashboard_switch_blog_menu_item:hover{



background-color: rgba(256,256,256,0.7) !important;



color:#555 !important;



}



#dashboard_switch_blog_menu_footer{



background-color: rgba(256,256,256,0.6) !important;



}







#new_post_notice_container{



color:#fff !important;



background-image: none !important;



}



#new_post_notice_container a{



color:#fff !important;



}







#dashboard_switch_blog_menu_current,



#dashboard_switch_blog_arrow_and_divider,



#dashboard_switch_blog_arrow_current



{



background-image:none !important



}



.notification_type_icon{



display:none !important



}











h1,h2,h3,h4,h5,h6{color:#eee !important}







.new_post_label{



text-transform: lowercase !important;



font-family:Georgia !important;



font-size: 11px !important;



font-style:italic !important;



margin-top:8px !important;

letter-spacing:4px !important;

word-spacing: -10px !important;



}







/* Code repaired by admin. See http://forum.userstyles.org/discussion/37591/attempting-to-mass-fix-broken-tumblr-dash-icon-styles */
.new_post {
	background: #ffffff url('http://static.tumblr.com/ettmxkg/bmlll5muk/new_post_icons_no_labels_floral.jpg') center center no-repeat !important;
}
.new_post_label_icon {
	background-image: none !important;
}















.sub_avatar{width:32px !important; height:32px !important; background-color:transparent !important}







#right_column .dashboard_nav_item ul.dashboard_subpages li a {



color:#fff !important;



text-shadow: none !important;



margin:0px !important;



}







#right_column .dashboard_nav_item ul.dashboard_subpages li a:hover{



color:#cdcdcd !important;



text-shadow: none !important;



}



#left_column #posts .post .post_avatar{-moz-border-radius: 38px !important; -webkit-border-radius: 60px !important; width: 64px !important; height: 64px !important; margin: 0px !important;}



#left_column #posts .post .sub_avatar_inner {



-moz-border-radius: 38px !important; -webkit-border-radius: 60px !important; width: 32px !important; height: 32px !important; margin: 0px !important;



}



#dashboard_switch_blog_menu_current{color:#fff !important;



font-family: Georgia !important;

text-shadow: 1px 1px 2px #444 !important;

font-style: italic !important;

text-shadow:none !important



}















#right_column .dashboard_nav_item .dashboard_nav_title,



                    #right_column .dashboard_nav_item .dashboard_nav_title a,



                    #right_column .dashboard_nav_item a,



                    #right_column .dashboard_nav_item ul.dashboard_subpages,



                    #right_column .dashboard_nav_item ul.dashboard_subpages li a {



                        color:#fff !important;





font-family: Georgia !important;



font-style: italic !important;



}



#right_column .dashboard_nav_item ul.dashboard_subpages li{margin-bottom:10px !important;}



#right_column .dashboard_nav_item ul.dashboard_subpages li a {



background-color:rgba(256,256,256,0.7) !important;



color:#555 !important;



-moz-border-radius: 10px !important;



-webkit-border-radius: 10px !important;



margin:0px !important;



padding:3px  !important



}



#right_column .dashboard_nav_item ul.dashboard_subpages li a:hover{



background-color:rgba(256,256,256,0.8) !important;



color:#222 !important;



-moz-border-radius: 10px !important;



-webkit-border-radius: 10px !important;



padding:4px  !important



}







#logo {







height: 0 !important;







	width: 0 !important;







	







	padding-left: 210px !important;







	padding-top: 49px !important;







	background: url(http://static.tumblr.com/ettmxkg/XuIlkohnq/logo_italic.png) no-repeat !important;















}



.selection_nipple{

display:none !important;

}





.tab a{

color:#fff !important;

}



.items li a {color:#444 !important}

.last_item a{color:#444 !important}



.selected a{text-shadow:none !important; color:#fff !important}

.recessed a{text-shadow-color:none !important; color:#fefefe !important}



.right_column_section li{background:rgba(256,256,256,0.4) !important; border:0px !important}

.right_column_section li a{color:#555 !important}

.right_column_section li a:hover{background:rgba(256,256,256,0.6) !important}

.notification, .avatar_frame, .nipple{border:0px !important}

.permalink{background-image:none !important; height:15px !important; width:15px !important; margin-top:4px !important; margin-right:5px !important;-webkit-border-radius:5px 5px 5px 5px !important;-moz-border-radius:5px 5px 5px 5px  !important}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color:rgba(256,256,256,0) !important}

.dashboard_options_form, .dashboard_options_form_disclaimer{ border:0px !important; color:#fff !important; font-weight:bold !important; text-shadow:1px 1px 1px #444 !important}

}
