css:
@-moz-document domain("raspberrypi.org") {
body {
    background: #000000;
    color: #FFFFFF;
}

h1 {
    color: #FFFF00;
}

a {
    color: #3366FF;
}

ul.topiclist li { background: #333333; }
ul.topics { background: #333333; }
ul.topiclist li.header { 
    background: #666666; 
    color: #000000; 
    font-size: 120%; 
}
.post:hover { background: #333333; color: #CCCCCC; }
.postbody { color: #CCCCCC; }

#main {
    background: #666666;
}

#branding {
    background: #333333;
}

#primary {
    background: #333333;
}

#author-info {
    background: #000000;
    color: #CCCCCC;
}


}
