css:
@-moz-document domain('http://www.tumblr.com/'), url-prefix('http://www.tumblr.com/'){



Body{
background: url('http://static.zerochan.net/Self.Made.full.1182880.jpg') center right # 007129 repeat fixed !important;  }




#dashboard_index, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts, #dashboard_post_queue,#dashboard_likes, #dashboard_followers, #dashboard_following, #dashboard_members, #main_tag_beta, #dashboard_upload_static_file, #main_about, #main_doc_page, #main_themes, #main_meetups, #main_jobs, #main_directory_page, #dashboard_settings, #dashboard_goodies {

background-image:url('http://static.zerochan.net/Self.Made.full.1182880.jpg') !important;

background-repeat:repeat !important;

background-attachment:fixed !important;

background-color:# 97a999 !important;

background-position:top

}



#dashboard_index div.l-content,#dashboard_submissions div.l-content,#dashboard_drafts div.l-content,#dashboard_post_queue div.l-content,#dashboard_likes div.l-content,#main_tag_beta #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

background: rgba(0,0,0,0.2) !important;

}



#dashboard_edit_post{

background-color: #99a997 !important

}

/*===============================Load More Posts Banner================================================*/ 
 
#auto_pagination_loader 
	{background:rgba(0,113,41, 0.00)!important;} 
div.return_to_top_icon{	background: none !important; 
	width: 0px !important; 
	height: 0px !important; 
	margin-top: 0px !important; 
	opacity: .5 !important; 
	border-bottom: solid 30px white !important; 
	border-right: 15px solid transparent !important; 
	border-left: 15px solid transparent !important; }	 
/*=======================================Selection Nipple ================================================*/ 
 
.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {  
	background: none !important; 
	width: 0px !important; 
	height: 0px !important; 
	margin-top: 0px !important; 
	border-bottom: solid 11px white !important; 
	border-right: 11px solid transparent !important; 
	border-left: 11px solid transparent !important; } 
.selection_nipple:not([class="selection_nipple white"]) { 
	border-bottom: solid 11px #005a20 !important;}	






#left_column #posts .post .permalink {

opacity: 0 !important;

}



#left_column #posts .post .post_avatar,

#left_column #posts .post .sub_avatar .sub_avatar_inner {

background-color: #fbfbfb !important;

border-bottom-color: #788274 !important;

}



#right_column .dashboard_nav_item.active .icon.dude {

background-position: -0px 0px;

}



#right_column .dashboard_nav_item.active .icon.arrow {

background-position: -0px -0px;

display: inline;

height: 9px;

left: -5px;

position: absolute;

top: 12px;

width: 5px;

}



#right_column .dashboard_nav_item.active .icon.gear {

opacity: 0;	

}

#right_column .dashboard_nav_item.active .icon.gear {

background-position: -0px -0px;

}



#left_column #posts .post{

border-bottom:3px #6d7d6c solid !important

}



#nav .nav_item.active,

#nav .nav_item.short active,

#nav .nav_item .nav_menu,



#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {

background-color:# f4c317 !important;

background-image:none !important;

border:0px !important;

background: rgba(0,0,0,0.3) !important;

}







#nav .nav_item {

color:#0a0a0a !important;

}



#nav .nav_item:hover {

color:#fff !important;

}



#nav .nav_item .nav_menu {

border-bottom:0px !important

}



#nav .nav_item .nav_menu a {

color:# ffc320 !important;

border-top:0px !important;

}



#nav .nav_item .nav_menu a:hover {

color:#fff !important;

background: rgba(0,0,0,0.8) !important;

}



#footer{

color:#444 !important;

background: rgba(0,0,0,0.7) !important;

background-image:none !important;

text-shadow:none !important

}



.no_posts_found, h1.dashboard_header, h1.dashboard_header a, h1.dashboard_header span {

color:#ccc !important

}







#right_column .dashboard_nav_item {

border-bottom:none !important;

background-image:none  !important

}



                    #right_column .dashboard_nav_item a,

                    #right_column .dashboard_nav_item ul.dashboard_subpages,

                    #right_column .dashboard_nav_item ul.dashboard_subpages li a {

                        color:# 929292 !important;

}



#right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {

                        color:#929292 !important ;

                    }



#right_column .dashboard_nav_item .dashboard_nav_title{color:#929292 !important}

#right_column .dashboard_nav_item .dashboard_nav_title a{color:#929292 !important}



#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {

                        background: none !important;

                    }



#show_radar a, #hide_radar a,

                        #hide_recommended_blogs a, #show_recommended_blogs a {

                            color:#929292 !important;

background: none !important;

                        }

#dashboard_controls_radar_media{background: none !important;}

#dashboard_controls_suggested_blogs{background-color: rgba(0,0,0,0.5) !important; border:0px !important}

#suggested_blog_avatar_1,#suggested_blog_avatar_2,#suggested_blog_avatar_3{border:0px !important}

.dashboard_controls_suggested_blog{border:0px !important}





#left_column #posts .notification {

                        background-color:#fff !important;

                        color:#444 !important;

                        border-bottom-color: #eee !important;

opacity: 0.5 !important;

                    }



#left_column #posts .notification .block {

                            color:# 63b161 !important;

                        }



#left_column #posts .notification.alt {

                        background-color: #f8f8f8 !important;

                    }

#left_column #posts .notification.last_notification,

                    #left_column #posts .notification.single_notification {

                        border-bottom-color:#a2b2a7 !important;

                    }

#left_column #posts .notification a {

                        color:#565656 !important;

                        border-bottom:0px !important;

                    }

#left_column #posts .notification blockquote {

                        border-left-color:#eee !important;

                    }



#left_column #posts .notification .avatar {

                        border-color:transparent !important;

                        background-color:#fbfbfb !important;

opacity:0.5 !important;

                    }



#left_column #posts .notification .avatar:hover{

opacity:1 !important;

                    }



#dashboard_switch_blog_menu{

background-color: #fff !important;

text-shadow:none !important;

border:0px !important;

opacity: 0.7 !important;

}

#dashboard_switch_blog_menu_header, #dashboard_switch_blog_arrow_and_divider,.dashboard_switch_blog_menu_item,#dashboard_switch_blog_menu_footer {

text-shadow:none !important;

border:0px !important

}



.dashboard_switch_blog_menu_item:hover{

background-color: #444 !important;

opacity: 0.8 !important;

}

#dashboard_switch_blog_menu_footer{

background-color: #fff !important;

opacity: 0.7 !important;

}



#new_post_notice_container{

color:#051114 !important;

background-image: none !important;

}

#new_post_notice_container a{

color:#051114 !important;

}



#dashboard_switch_blog_menu_current,

#dashboard_switch_blog_arrow_and_divider,

#dashboard_switch_blog_arrow_current

{

background-image:none !important

}

.notification_type_icon{

display:none !important

}





h1,h2,h3,h4,h5,h6{color:#555555 !important}



.new_post_label{

text-transform: lowercase !important;

font-color: #fff;

font-family:alkjsdf !important;

font-size: 00px !important;

font-style: color:#fff !important;

margin-top:0px !important;

}

.arrow{display:none !important}
.sub_avatar{width:32px !important; height:32px !important; background-color:transparent !important}
.new_post_label_icon{background-image:none !important}
#new_post{
background: url('http://static.tumblr.com/ettmxkg/D11lvhock/new_post_icons_no_labels_vintage2.png') center top no-repeat;
background-color:#fff !important;
}
img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{
    width:0;
    height:70px;
    padding-right:250px;
    opacity: 0;
}




ol#posts li.post.new_post {

background: #fff url('http://i294.photobucket.com/albums/mm100/xscm13x/egoistlinks.jpg') center center no-repeat;

}




img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{

    width:0;

    height:70px;

    padding-right:250px;

    opacity: 0;

}



.sub_avatar{width:32px !important; height:32px !important; background-color:transparent !important}



#right_column .dashboard_nav_item ul.dashboard_subpages li a {

color:#929292 !important;

text-shadow: none !important;

margin:0px !important;

}



#right_column .dashboard_nav_item ul.dashboard_subpages li a:hover{

color:#fff !important;

text-shadow: none !important;

}

#logo {

height: 0 !important;

width: 0 !important;



padding-left: 218px !important;

padding-top: 49px !important;

background: url(http://i294.photobucket.com/albums/mm100/xscm13x/egoisttitle.jpg) no-repeat !important



}

/*===========================================Right Column Radar Image============================================*/ 
 
div.gradient, 
div.dashboard_nav_item,  
a.permalink 
	{background-color:# 007129 !important; 
	opacity:.9;  
	background-image:none !important; 
	border:0px !important} 
.content.is_media,  
.mask.top,  
.mask.bottom { 
	background: none !important; } 
.post.photo_post .photo,  
.post.photo_post .video_thumbnail { 
	border-radius: 16px !important;} 
.post.photo_post .photo,  
.post.photo_post .video_thumbnail  
	{-moz-box-shadow: 1px 1px 3px #000000 !important;} 
 
}
