@-moz-document url-prefix(http://www.boinc.be/vbulletin),url-prefix(http://boinc.be/vbulletin) {

body 
{
	width: 1000px !important;
	padding-left: 125px !important;
}
a:link, body_alink
{
	color: #204667 !important;
	text-decoration: none !important;
}
a:visited, body_avisited
{
	color: #204667 !important;
	text-decoration: none !important;
}
a:hover, a:active, body_ahover
{
	color: #204667 !important;
	text-decoration: underline !important;
}
.page
{

}
td, th, p, li
{
	font: 9pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	color: #000000;
}
.tborder
{
	background: #150e05;
	color: #000000;
	border: 0px solid #392206;
}
.tcat
{
	color: #FFFFFF !important;
	font-family: verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	font-weight: bold !important;
	padding: 4px !important;
	padding-left: 6px !important;
}
.tcat a:link, .tcat_alink
{
	color: #FFFFFF !important;
	text-decoration: none !important;
}
.tcat a:visited, .tcat_avisited
{
	color: #FFFFFF !important;
	text-decoration: none !important;
}
.tcat a:hover, .tcat a:active, .tcat_ahover
{
	color: #FFFFFF !important;
	text-decoration: underline !important;
}
.thead
{
	color: #FFFFFF !important;
	font: normal 9px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
.thead a:link, .thead_alink
{
	font: normal 9px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	color: #FFFFFF !important;
}
.thead a:visited, .thead_avisited
{
	font: normal 9px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	color: #FFFFFF !important;
}
.thead a:hover, .thead a:active, .thead_ahover
{
	font: normal 9px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	color: #FFFFFF !important;
}
.tfoot
{
	font: normal 10px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	color: #FFFFFF !important;
	padding: 4px !important;
}
.tfoot a:link, .tfoot_alink
{
	font: normal 10px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	color: #FFFFFF !important;
	text-decoration: none !important;
}
.tfoot a:visited, .tfoot_avisited
{
	font: normal 10px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	color: #FFFFFF !important;
	text-decoration: none !important;
}
.tfoot a:hover, .tfoot a:active, .tfoot_ahover
{
	font: normal 10px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	color: #FFFFFF !important;
	text-decoration: underline !important;
}
.alt1, .alt1Active
{
	background: #ffffff !important;
	color: #eeeec6;
}
.alt2, .alt2Active
{
	background: #ebedee !important;

	color: #eeeec6;
}
td.inlinemod
{
	background: #FFFFCC;
	color: #000000;
}
.wysiwyg
{
	background: #191107;
	color: #eeeec6;
	font: 10pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}
textarea, .bginput
{
	font: 10pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}
.button
{
	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}
select
{
	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}
option, optgroup
{
	font-size: 11px;
	font-family: verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}
.smallfont
{
	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}
.time
{
	color: #FF9000;
}
.navbar
{
	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}
.highlight
{
	color: #FF0000;
	font-weight: bold;
}
.fjsel
{
	background: #3E5C92;
	color: #E0E0F6;
}
.fjdpth0
{
	background: #F7F7F7;
	color: #000000;
}
.panel
{
	background: #120c05;
	color: #eeeec6;
	padding: 10px;
	border: 1px solid #422807;
}
.panelsurround
{
	background: #311e06;
	color: #000000;
}
legend
{
	color: #ffffff !important;
	font: 11px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}
.vbmenu_control
{
	color: #ffffff !important;
	font: bold 10px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	padding: 3px 6px 3px 6px;
	white-space: nowrap;
}
.vbmenu_control a:link, .vbmenu_control_alink
{
	color: #ffffff !important;
	text-decoration: none !important;
}
.vbmenu_control a:visited, .vbmenu_control_avisited
{
	color: #ffffff !important;
	text-decoration: none !important;
}
.vbmenu_control a:hover, .vbmenu_control a:active, .vbmenu_control_ahover
{
	color: #ffffff !important;
	text-decoration: underline !important;
}
.vbmenu_popup
{
	background: #FFFFFF;
	color: #000000;
	border: 1px solid #0B198C;
}
.vbmenu_option
{
	background: #191107;
	color: #FF9000;
	font: normal 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
	white-space: nowrap;
	cursor: pointer;
}
.vbmenu_option a:link, .vbmenu_option_alink
{
	color: #FFFFFF;
	text-decoration: none;
}
.vbmenu_option a:visited, .vbmenu_option_avisited
{
	color: #FFFFFF;
	text-decoration: none;
}
.vbmenu_option a:hover, .vbmenu_option a:active, .vbmenu_option_ahover
{
	color: #FFFFFF;
	text-decoration: none;
}
.vbmenu_hilite
{
	background: #FF9000;
	color: #FFFFFF;
	font: normal 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
	white-space: nowrap;
	cursor: pointer;
}
.vbmenu_hilite a:link, .vbmenu_hilite_alink
{
	color: #FFFFFF;
	text-decoration: underline;
}
.vbmenu_hilite a:visited, .vbmenu_hilite_avisited
{
	color: #FFFFFF;
	text-decoration: underline;
}
.vbmenu_hilite a:hover, .vbmenu_hilite a:active, .vbmenu_hilite_ahover
{
	color: #FFFFFF;
	text-decoration: underline;
}
/* ***** styling for 'big' usernames on postbit etc. ***** */
.bigusername { font-size: 14pt; }

/* ***** small padding on 'thead' elements ***** */
td.thead, th.thead, div.thead { padding: 4px; }

/* ***** basic styles for multi-page nav elements */
.pagenav a { text-decoration: none; }
.pagenav td { padding: 2px 4px 2px 4px; }

/* ***** de-emphasized text */
.shade, a.shade:link, a.shade:visited { color: #777777; text-decoration: none; }
a.shade:active, a.shade:hover { color: #FF4400; text-decoration: underline; }
.tcat .shade, .thead .shade, .tfoot .shade { color: #DDDDDD; }

/* ***** define margin and font-size for elements inside panels ***** */
.fieldset { margin-bottom: 6px; }
.fieldset, .fieldset td, .fieldset p, .fieldset li { font-size: 11px; }

/* ***** don't change the following ***** */
form { margin: 0px; }
label { cursor: default; }
.normal { font-weight: normal; }
.inlineimg { vertical-align: middle; }
.underline { text-decoration: underline; }

}
