css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://wikiwiki.jp/diablo3blizz/") {
    * {
        font-family     : "DejaVu Sans", "Osaka","NfMotoyaCedar W1 KP", "MS UI Gothic", sans-serif !important;
    }
    body, #backframe, #naviframe, td, th, div {
        background-color: #000f34 !important;
        border-color    : #103f4f !important;
        color           : #deebef  !important;
    }
    #topframe, #naviframe, #footframe, #naviframe a, #footframe a {
        border:0 !important;
    }
    #title, h1, h2, h3, h4, h5, table {
        background-color: #001f34 !important;
        border-color    : #234f5f !important;
        color           : cyan !important;
    }
    .title2 {
        color: orange !important;
    }
    div#topicpath {color: white !important;}
    div#lastmodified {
        color: darkcyan !important;
    }
    hr {
        background-color: #106f7f !important;
        color           : #106f7f !important;
    }
    pre {
        background-color: #001f34 !important;
        border-color    : #001f34 !important;
    }
    a {
        border-color: darkslategray !important;
    }
    a, span.wikicolor[style="color:orange"] {
        color: paleturquoise !important;
    }
    span.noexists {
        background-color: #003f4f !important;
    }
    span.wikicolor[style="color:#B08040"] {color: skyblue !important;}
    span.wikicolor[style="color:crimson"] {color: tomato !important;}
    span.wikicolor[style="color:blueviolet"] {color: plum !important;}
    span.wikicolor[style="color:forestgreen"] {color: lime !important;}
    span.wikicolor[style="color:#663300"]{color: orange !important;} /* NOW（カウンター） */

}
