css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#TabsToolbar .tabbrowser-tab:first-child{
background:-moz-linear-gradient(transparent, rgba(0,0,0,0.38) 30%),
                    -moz-linear-gradient(transparent, rgba(0,0,0,0.38) 30%) !important;
background-position:0px 0px,100% 0px  !important;
background-size:1px 100% !important;
background-repeat: no-repeat !important;
}
#TabsToolbar .tabbrowser-tab:first-child:last-of-type[selected="true"]{
-moz-appearance: none !important;
background:     -moz-linear-gradient(rgba(255,255,255,0.1), rgba(255,255,255,0.9)),
                        -moz-linear-gradient(transparent, rgba(0,0,0,0.38) 40%),
                       -moz-linear-gradient(transparent, rgba(0,0,0,0.38) 40%) !important;
background-position: 1px 0px,100% 0px,0px 0 !important;
background-size: -moz-calc(100% - 2px) 100%, 1px 100%, 1px 100% !important;
background-repeat: no-repeat !important;
}



.tabbrowser-tab[pending="true"]{
opacity:0.3;
}
.tabbrowser-tab{
-moz-border-image:none !important;
border:none !Important;
background:-moz-linear-gradient(transparent, rgba(0,0,0,0.38) 30%) !important;
background-position:100% 0px !important;
background-size:1px 100% !important;
background-repeat: no-repeat !important;
height:27px !important;
line-height:27px !important;
padding:0 5px !important;
margin:0 !important;
}

.tabbrowser-tab:hover{
-moz-appearance: none !important;
background:   -moz-linear-gradient(rgba(255,255,255,0.05), rgba(255,255,255,0.6)),
              -moz-linear-gradient(transparent, rgba(0,0,0,0.38) 30%) !important;
background-position: 0px 0px,100% 0px !important;
background-size: -moz-calc(100% - 1px) 100%, 1px 100% !important;
background-repeat: no-repeat !important;
}

.tabbrowser-tab[selected="true"]{
-moz-appearance: none !important;
background:     -moz-linear-gradient(rgba(255,255,255,0.1), rgba(255,255,255,0.9)),
                        -moz-linear-gradient(transparent, rgba(0,0,0,0.38) 40%) !important;
background-position: 0px 0px,100% 0px !important;
background-size: -moz-calc(100% - 1px) 100%, 1px 100% !important;
background-repeat: no-repeat !important;
}

#TabsToolbar .tabbrowser-tab:first-child:hover{
background:-moz-linear-gradient(transparent, rgba(0,0,0,0.38) 30%),
                    -moz-linear-gradient(transparent, rgba(0,0,0,0.38) 30%),
                     -moz-linear-gradient(rgba(255,255,255,0), rgba(255,255,255,0.6)) !important;
background-position:0px 0px,100% 0px,1px 0px !important;
background-size:1px 100%,1px 100%,-moz-calc(100% - 2px) 100% !important;
background-repeat: no-repeat !important;
}

#TabsToolbar .tabbrowser-tab:first-child[selected="true"]{
background:-moz-linear-gradient(transparent, rgba(0,0,0,0.38) 30%),
                    -moz-linear-gradient(transparent, rgba(0,0,0,0.38) 30%),
                     -moz-linear-gradient(rgba(255,255,255,0), rgba(255,255,255,0.9)) !important;
background-position:0px 0px,100% 0px,1px 0px !important;
background-size:1px 100%,1px 100%,-moz-calc(100% - 2px) 100% !important;
background-repeat: no-repeat !important;
}



#TabsToolbar .tabbrowser-tab:last-of-type:hover{
-moz-appearance: none !important;
background:   -moz-linear-gradient(rgba(255,255,255,0.05), rgba(255,255,255,0.6)),
                    -moz-linear-gradient(transparent, rgba(0,0,0,0.38) 30%) !important;
background-position: 0px 0px,100% 0px !important;
background-size: -moz-calc(100% - 1px) 100%, 1px 100% !important;
background-repeat: no-repeat !important;
}

#TabsToolbar .tabbrowser-tab:last-of-type[selected="true"]{
-moz-appearance: none !important;
background:     -moz-linear-gradient(rgba(255,255,255,0.1), rgba(255,255,255,0.9)),
                        -moz-linear-gradient(transparent, rgba(0,0,0,0.38) 40%) !important;
background-position: 0px 0px,100% 0px !important;
background-size: -moz-calc(100% - 1px) 100%, 1px 100% !important;
background-repeat: no-repeat !important;
}

.alltabs-item[restoring]:not([busy]),
.tabbrowser-tab[restoring]:not([busy]) {
opacity:1 !important;
}
.alltabs-item[restoring]:not([busy]) .tab-text,
.tabbrowser-tab[restoring]:not([busy]) .tab-text{
color:#999 !important;
}
.alltabs-item[restoring]:not([busy]) .tab-icon-image,
.tabbrowser-tab[restoring]:not([busy]) .tab-icon-image{
opacity:0.5 !important;
}

.tab-text.tab-label{
margin-bottom:-1px !Important;
color:white !Important;
text-shadow:0 0 4px black,0 0 6px #222 !Important;
}

.tabbrowser-tab[pending="true"] .tab-text.tab-label{
text-shadow:0 0 3px black,0 0 3px black,0 0 3px black,0 0 4px black,0 0 4px black,0 0 5px black !Important;
}

.tabs-newtab-button{
-moz-appearance: none !important;
border:none !important;
-moz-border-image:none !Important;
padding:0 !important;
background:-moz-linear-gradient(transparent, rgba(0,0,0,0.38) 30%) !important;
background-position:100% 0px !important;
background-size:1px 100% !important;
background-repeat: no-repeat !important;
height:27px !important;
line-height:27px !important;
padding:0 5px !important;
}
.tabs-newtab-button:hover{
-moz-appearance: none !important;
background:   -moz-linear-gradient(rgba(255,255,255,0.05), rgba(255,255,255,0.6)),
                      -moz-linear-gradient(transparent, rgba(0,0,0,0.38) 30%) !important;
background-position: 0px 0px, 100% 0px !important;
background-size: -moz-calc(100% - 1px) 100%, 1px 100% !important;
background-repeat: no-repeat !important;
border:none !Important;
}

.tabs-newtab-button > .toolbarbutton-icon{
margin-bottom:-4px !important;
}


.tab-throbber,
.tab-icon-image {
  -moz-margin-start: 1px !important;
  -moz-margin-end: 4px !important;
  margin-top: 1px !important;
  width: 16px !important;
  height: 16px !important;
  list-style-image: url("data:image/png;base64,") !important;
}

.tab-throbber {
  list-style-image: url("data:image/png;base64,") !important;
}

.tab-throbber[progress] {
  list-style-image: url("data:image/png;base64,") !important;
}


/*mutirow*/

#TabsToolbar{
background:transparent !important;
}
