css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("google.it") , domain("google.com") , domain("google.de") , domain("google.es") {
#gbx3 {
    background: url("http://oi46.tinypic.com/2nr0w1y.jpg") repeat scroll 0 0 transparent !important;
    border-color: #848484 !important;
    box-shadow: 0 1px 5px #BFBFBF;
    min-width: 980px;
    opacity: 0.3 !important;

}
.jhp input[type="submit"], .gssb_c input, .gac_bt input {
    -moz-user-select: none;
    background-color: #F5F5F5;
    background: url("http://oi46.tinypic.com/2nr0w1y.jpg") repeat scroll 0 0 transparent !important;
    border: 1px solid rgba(0, 0, 0, 0.1);
    border-radius: 5px 5px 5px 5px;
    box-shadow: 0 4px 8px #DEDEDE;
    color: #666666;
    cursor: default;
    font-family: arial,sans-serif;
    font-size: 11px;
    font-weight: bold;
    height: 29px;
    line-height: 27px;
    margin: 11px 6px;
    min-width: 54px;
    opacity: 0.9 !important;
    padding: 0 13px !important;
    text-align: center;

}
.gsib_a {
    box-shadow: 0 0 4px #DEDEDE;
    padding: 4px 6px 0;
    width: 100%;
    
}

#footer > div {
    border-top: 1px solid #EBEBEB;
    bottom: 0;
    box-shadow: 0 -8px 14px #DEDEDE;
    padding-top: 3px;
    position: absolute;
    width: 100%;
}

#ftby > div, #fll > div, #footer a {
    display: inline-block;
    margin-top: 3px;
}

#gbz .gbz0l .gbts, #gbz .gbzt-hvr, #gbz .gbzt:focus, #gbz .gbgt-hvr, #gbz .gbgt:focus {
    border-bottom: 1px solid #CC3535;
    color: #CC3535 !important;
}
#gbgs4, .gbgs {
    background: url("http://oi46.tinypic.com/2nr0w1y.jpg") repeat scroll 0 0 transparent !important;
    border: 1px solid #C6C6C6;
    border-radius: 12px 2px 12px 2px;
    box-shadow: 0 4px 8px #DEDEDE;
    display: block;
}
.gbmc {
    background: none repeat scroll 0 0 #FFFFFF;
    box-shadow: 0 0 32px #DEDEDE;
    color: #DEDEDE;
    opacity: 0.5;
    padding: 10px 0;
    position: relative;
    z-index: 2;
}
.gbqfb {
    background-color: #4D90FE;
    background-image: -moz-linear-gradient(center top , #4D90FE, #4787ED);
    border: 2px solid #3079ED;
    border-radius: 8px 8px 8px 8px;
    box-shadow: 0 4px 8px #9EC3FF;
    color: #FFFFFF !important;
}
element.style {
    border-bottom: 1px solid #DEDEDE;
    box-shadow: 0 2px 19px #DEDEDE;
    height: 57px;
}
.osl a, .gl a, #tsf a, a.mblink, a.gl, a.fl, .slk a, .bc a, .flt, a.flt u, .oslk a, #tads .ac a, #tadsb .ac a, #rhs .ac a, .blg a, #appbar a {
    background: url("http://oi46.tinypic.com/2nr0w1y.jpg") repeat scroll 0 0 transparent;
    border: 1px solid #DEDEDE;
    border-radius: 8px 8px 8px 8px;
    box-shadow: 0 4px 8px #DEDEDE;
    padding: 1px 4px;
    text-decoration: none;
}
.gl, #foot a, .nobr {
    background: none repeat scroll 0 0 transparent;
    border: 0 none;
    box-shadow: 0 0 0 #FFFFFF;
    white-space: nowrap;
}



}
