@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("youtube.com") {
/** by M3n747 <m3n747@o2.pl> **/

body, .branded-page-v2-col-container.clearfix {
    background      	: #FFFFFF !important;
    }
.branded-page-v2-primary-col, .primary-col, #content {
    width               : 800px !important;
    }
#feed, #content {
    margin-left         : 130px !important;
    }
.feed-item-thumb.watched img, .video-thumb.ux-thumb.yt-thumb-default-185 {
    width               : 120px !important;
    height              : 72px !important;
    }
#watch-like, .like-button-renderer-like-button {
    color               : #555555 !important;
    border-style        : solid !important;
    border-width        : 1px !important;
    border-color        : #555555 !important;
    background          : linear-gradient(#CCFFCC 0%, #99FF99 50%, #66FF66 100%) !important;
    width               : 84px !important;
    }
#watch-dislike, .like-button-renderer-dislike-button {
    border-style        : solid !important;
    border-width        : 1px !important;
    border-color        : #555555 !important;
    background          : linear-gradient(#FFCCCC 0%, #FF9999 50%, #FF6666 100%) !important;
    width               : 55px !important;
    }
.start.comment-action-vote-up {
    background          : #D3FFE3 !important;
    }
.end.comment-action-vote-down {
    background          : #FFD6D6 !important;
    }
.yt-uix-subscription-button {
    background          : linear-gradient(#FEFEFE 0%, #EFEFEF 50%, #E3E3E3 100%) !important;
    color               : #333333 !important;
    }
.watched-badge {
    background-color    : transparent !important;
    color               : #00FF99 !important;    
    text-shadow         : 2px 2px #000000 !important;
    }
#masthead-positioner-height-offset {
    height              : 50px !important;
    }
#watch7-main {
    margin-left         : -130px !important;
    }
#channels-browse-content-grid {
    width               : 1200px !important;
    margin-left         : -45px !important;
    }
#watch7-sidebar {
    position            : absolute !important;
    top                 : -120px !important;
    left                : 5px !important;
    }


/* Styles YousableTubeFix buttons (if you have it installed, of course - and you should) */
#ytf-main-resize-button, #ytf-main-download-button, #ytf-main-configure-button {
    border              : #CCCCCC 1px solid !important;
    font-weight         : bold !important;
    background          : linear-gradient(#FEFEFE 0%, #EFEFEF 50%, #E3E3E3 100%) !important;
    }
#ytf-main-resize-button span, #ytf-main-download-button span,
#ytf-main-configure-button span {
    color               : #444444 !important;
    }


/* Moves video description up to compensate for YousableTubeFix buttons */
/* If you don't have YTF, you can remove it or comment it out */
div.yt-uix-button-panel {
    position            : relative !important;
    bottom              : 9px !important;
    }


/* Youtube Center download button - remove or comment out if you don't use it*/
.start.yt-uix-button.yt-uix-tooltip.yt-uix-button-text,
.end.yt-uix-button.yt-uix-tooltip.yt-uix-button-text {
    color               : #555555 !important;
    border-style        : solid !important;
    border-width        : 1px !important;
    border-color        : #999999 !important;
    background          : linear-gradient(#FFF8DE 0%, #EEF0A8 50%, #FFF8DE 100%) !important;
    }


/* Removes crap */
#yt-uix-clickcard-card1, #yt-hitchhiker-feedback, #footer-hh-container, #guide,
.branded-page-related-channels-title, .branded-page-related-channels-list, .separator,
.yt-alert, #initial-feed-promo, .branded-page-v2-primary-col-header-container,
.guide-section.guide-static-section.guide-section-no-counts, .secondary-col,
#footer-container, .branded-page-v2-secondary-col, #ticker, #upsell-promo,
#masthead-appbar, #appbar-guide-button-container, #inproduct-guide-modal,
.iph-bracket-up.iph-bracket-6053434.iph-bracket-__start__, .feed-header.clearfix {
    display             : none !important;
    }
}
