css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("reddit.com") {

a.author {
    font-size: 0 !important;
    overflow: visible !important;
    display: inline-block;
    content: ""
    }
a.author:before {
    font-size: 0 !important;
    overflow: hidden;
    display: inline-block;
    content: ""
    }
a.author:after {
    font-size: 10px !important;
    visibility: visible !important;
    content: "Esplin" !important;
    color: rgb(51, 102, 153) !important;
    }
.flair:after {
    content: "";
    display: inline-block;
    width: 0px !important;
    height: 0px !important;
    }
.flair {
    content: "";
    display: inline-block;
    width: 0px !important;
    height: 0px !important;
    }
.author{
    display: inline-block;
    border: 0;
    border-radius: 0;
    background: transparent;
    font-weight: 700;     
    margin-left: -3px !important;
    margin-right: 5px !important;
    }
.author:before {
    background-image: url(http://i.imgur.com/3UogL.png);
    background-position: 0px -2430px !important;
    content: "";
    display: inline-block;
    width: 35px;
    height: 30px;
    background-repeat: no-repeat; 
    margin-left: 0px !important;
    }
/*fixes the stupid fag flair on r/mlas1 i guess */
.tagline a.author[href="http://www.reddit.com/user/RainbowCrash"]:before, .tagline a.author[href="http://www.reddit.com/user/qwert426"]:before, .tagline a.author[href="http://www.reddit.com/user/Orschmann"]:before, .tagline a.author[href="http://www.reddit.com/user/derram"]:after, .tagline a.author[href="http://www.reddit.com/user/EvilHom3r"]:after {
    content: "";
    background: none;
    color: black;
    border: 0;
    border-radius: 0;
    background-image: url(http://d.thumbs.redditmedia.com/pbotzChiVRXp4RXW.png) !important;
    background-repeat: no-repeat;
    display: inline-block;
    width: 100px;
    height: 100px;
    margin-bottom: -18px !important;
    overflow: visible !important;
    }
}
