css:
/* Created  by Grom
@namespace url(http://www.w3.org/1999/xhtml); /* namespace rule, can be omitted */

@-moz-document domain("minus.com") { /* confine the style to minus.com domain, start of style */

/* change every element, an asterisk "*" is a wild-card that applies to everything */
* {
color: #999 !important;
background-color: #111 !important;
border-color: #333 !important;
text-shadow: none !important;
}

/* change links and all their children */
a, a * {
color: #f60 !important;
text-decoration: none !important;
}

/* change visited links and all their children */
a:visited, a:visited * {
color: #f80 !important;
}

/* change hovered links and all their children */
a:hover, a:hover * {
color: #f00 !important;
}


/* darkening buttons */
input, select, textarea, button {
border-color: #999 !important;
/* removing button styling from the Operating System */
-webkit-appearance: none !important; -moz-appearance: none !important; appearance: none !important;
/* we don't need to change the backgrounds and color into dark again;
since we removed the OS styling, they can and will be inherited from the general rule with the asterisk above */
}

/* fixing a Chrome bug from removing OS styling from buttons */
input[type="radio"] { -webkit-appearance: radio !important; }
input[type="checkbox"] { -webkit-appearance: checkbox !important; }

/* end of style */ }
