css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   { 

body{
background:#4c4b4b url(http://i50.tinypic.com/2visheb.jpg) top repeat;
color:#4c4b4b;}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background:rgba(76,75,75,0.5)!important;}

img.content_top,img#content_top,img#content_bottom{
display:block;
margin:0 0;
width:0px;}

#footer{
color:#d3d3d3;}

#footer a{
color:#d3d3d3;}

#footer #change_language a.active{
color:#d3d3d3;}

#logo {
height: 0 !important; 
width: 0 !important; 
padding-left: 165px !important; 
padding-top: 50px !important; 
background: url(http://i45.tinypic.com/15zgnqe.jpg) no-repeat !important;}

#return_to_top .return_to_top_icon{
background: url(http://i50.tinypic.com/4tvw4z.jpg) no-repeat transparent!important;}

#left_column #posts .post .post_avatar{
-moz-border-radius: 38px !important;
-webkit-border-radius: 60px !important;
width: 64px !important;
height: 64px !important;
margin: 0px !important;}

#left_column #posts .post .sub_avatar_inner {
-moz-border-radius: 38px !important;
-webkit-border-radius: 60px !important;
width: 32px !important;
height: 32px !important;
margin: 0px !important;}

#posts li.notification.alt {
background-color: #4c4b4b !important;
border-bottom: 1px solid #272727 !important;}
 
#posts li.notification.first_notification {  
background-color: #818181 !important;  
border-bottom: 1px solid #272727 !important;}
   
#posts li.notification {  
background-color: #818181 !important;  
border-bottom: 1px solid #272727 !important;}
 
#posts li.notification.last_notification {  
border-bottom: 1px solid #272727 !important;}

#posts .notification a:not(.avatar_frame){
border-bottom:1px solid #272727 !important;}

#posts .notification .nipple{
width:0;
height:0;
border-top:6px solid transparent;
border-bottom:6px solid transparent;
border-right:6px solid #272727;}

#posts .notification.alt .nipple{
border-right:6px solid #272727;}

#posts .notification .nipple.border{
border-top:7px solid transparent;
border-bottom:7px solid transparent;
border-right:7px solid #272727;}

#posts .notification{
color:#d3d3d3;
border-left:solid 1px #272727;
border-right:solid 1px #272727;
border-bottom:solid 1px #272727;}

#auto_pagination_loader {
background: rgba(0,0,0,0.0) !important;}

#right_column:after{
background:-moz-linear-gradient(top,rgba(44,71,98,0) 0,rgba(44,71,98,0) 0%);
background:-webkit-gradient(linear,left top,left bottom,color-stop(0%,rgba(44,71,98,0)),color-stop(0%,rgba(44,71,98,0)));
background:-webkit-linear-gradient(top,rgba(44,71,98,0) 0,rgba(44,71,98,0) 0%);
background:-o-linear-gradient(top,rgba(44,71,98,0) 0,rgba(44,71,98,0) 0%);
background:-ms-linear-gradient(top,rgba(44,71,98,0) 0,rgba(44,71,98,0) 0%);
background:linear-gradient(top,rgba(44,71,98,0) 0,rgba(44,71,98,0) 0%);}

}
