/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);

/* ::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::: 
 * :::                Google Video - Dark Carbon Theme                     :::
 * :::                                                                     :::
 * :::  Author        :  storm119                                          :::
 * :::  Updated       :  26 May 2008                                       :::
 * :::                :  http://userstyles.org/users/7491                  :::
 * :::                :                                                    :::
 * :::                :  Their code's or artwork's are included in         :::
 * :::                :  this style:-                                      :::
 * :::  Contributors  :  Based on Valacar's                                :::
 * :::  Credits &     :  Google Video- dark gray redesign                  :::
 * :::  Thanks        :  http://userstyles.org/styles/845                  :::
 * :::                :                                                    :::
 * ::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::: 
 */

@-moz-document url-prefix(http://video.google.com/),
               url-prefix(http://video.google.co.uk/),
               url-prefix(http://video.google.com.au/),
               url-prefix(http://video.google.ca/),
               url-prefix(http://video.google.de/),
               url-prefix(http://video.google.es/),
               url-prefix(http://video.google.fr/),
               url-prefix(http://video.google.it/),
               url-prefix(http://video.google.nl/),
               url-prefix(http://video.google.pl/),
               url-prefix(http://video.google.com/videoadvancedsearch) {

			   
/* Dark Grey Carbon */
body, .lpaf_framefull, table[bgcolor="white"], .nowplaying-info, .nowplaying, .playlist-section, .playlist-footer, .hotvideos-section, .tr-shaded  {
	background: #333 url("data:image/jpg;base64,") repeat !important;
	color: #ddd !important;
}

/* defaults for all links */
body,a,font,div { font-family: Tahoma, sans-serif !important; font-size: 11px !important; }

a { text-decoration: none !important; }
a:link { color: #82BDFF !important; text-decoration: none !important; }
a:link:hover { color: #ff6600 !important; }
a:visited { color: #33FFFF !important; }
a:active { color: #92CDFF !important; }
a:hover, a:visited:hover { color: #1689FF !important; }
:focus { outline: none; }

/* change color of lots of text */
font, div, td { color: #fff !important; }
a.q:visited, a.toplink:visited { color: #82BDFF !important;}

/* make sure web forms (and google suggest) don't get their colors changed  - storm119 */
textarea *, .aAutoComplete { background-color: -moz-Field !important; color: -moz-fieldtext !important; }
input *{color: -moz-fieldtext !important; }

/* Input Box */
input { font: 11px Tahoma,sans-serif; padding: .2em .4em; }
input[name=q] { background: #fff7df; border: 1px solid #cccccc; color: #008000; }
input[type="submit"], input[type="button"] {background: #f4c100;border: none;color: Highlight;cursor: pointer;padding: .2em .6em;}
input[type="submit"]:hover,input[type="button"]:hover{color: #ff6600;}

/* Select input Box */
select {font: 11px Tahoma,sans-serif;padding: .2em .4em;height: 19px;background: #fff7df;border: 1px solid #cccccc;color: #008000;}
select:hover { color: #ff6600; }

/* Main text color */
.nowplaying-description, .Snippet, #commentlist font, #description font {color: #ccc !important;}

/* Dimmed text color */
.list-item-host, .playlist-item-host, .Url, .durationetc {color: #888 !important;}

/* Alt text color */
.nowplaying-durationanddate, .playlist-item-rating span, .Details, .Details span, .communityRating span, #durationetc, #commentlist font[color="green"] {color: #ffc !important;}

/* Drop down menu */
#gbi, .gb2 { background: #000 !important; width: 6.8em !important; }
#gbar .gb2 a:hover { background: #444 !important; width: 7em !important; }
#gbi { border: 1px solid #444 !important;}
.gb2 div { border-top: 1px solid #444 !important;}

/* Border fixes */
.nowplaying, .gbh, .playlist-section, .div-section-header, img[border="1"] { border: none !important;}
.lpaf_framefull, #upnextmenu { border-color: #444 !important; }

/* Active tab */
.category-tab-selected { color: #fff !important; 
}
.category-tab-selected, .playlist-container, .playlist-item, .playlist,
.framemenu_active, .framemenu_content { background: #353535 !important; border: none !important;
}
.framemenu_content { border: 1px solid #444!important;border-top: none !important;
}
.framemenu_tabspacer, .framemenu_flex_tabspacer { border-color: #444 !important;
}
.framemenu_active { border: 1px solid #444!important; border-bottom: none !important;
}
.playlist-container, .category-tab-selected { border: 1px solid #444!important;
}
.category-tab-selected { border-bottom: none !important;
}

/* Inactive tabs */
a.category-tab-unselected-link { color: #999 !important;
}
.category-tab-unselected, .framemenu_inactive, .framemenu_inactive a { background: #2d2d2d !important; border: none !important;
}
.framemenu_inactive { border: 1px solid #353535 !important; border-bottom: 1px solid #444 !important;
}
.category-tab-unselected { border: 1px solid #353535 !important; border-bottom: none !important;
}

/* Buttons */
.Tab, .div-hotstuff-tab, .menu-normalsb, .menu-button {
	background: #656565 !important;
	border: 1px solid !important;
	border-color: #848484 #565656 #565656 #848484 !important;
}

/* Section header bars */
.mod_title, .hot_videos_title_bar, tr[bgcolor="#e5ecf9"], .section-header, #resultsheadertable, .TitleBar, .table-section-header, .headline {
	background: #606060 url("data:image/gif;base64,R0lGODlhAQAKAJEAAG5ubnJycoSEhAAAACH5BAAAAAAALAAAAAABAAoAAAIEhIAJUQA7") top left repeat-x !important;
	color: #fff !important;
	border: none !important;
}

/* Thumbnail Images */
.grid-item-thumbnail, .playlist-item-thumbnail, [style*="border: 1px solid blue;"], .detailsimage {
	border: 1px solid #000 !important;
}

/* Playlist tweaks */
/* (note: background-color set in active tab) */
.playlist-item, [class="playlist-item"][style*="background: rgb(238, 237, 241)"] {
	background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAwCAYAAADQMxCBAAAABGdBTUEAAK/INwWK6QAAABl0RVh0U29mdHdhcmUAQWRvYmUgSW1hZ2VSZWFkeXHJZTwAAAAtSURBVHjaYlJXV2dkYmBgYAIRCBYTXjEwwYyLi5OFRuCUABMsJMuyEMEFCDAAz9oBV1rTbjUAAAAASUVORK5CYII=") !important;
	background-position: bottom left !important;
	background-repeat: repeat-x !important;
	border: 1px solid transparent !important;
}

.playlist-section { padding-left: 5px !important; }

/* active playlist item */
[class="playlist-item"][style*="background: rgb(238, 237, 241)"] {
	background-color: #444 !important;
	border: 1px solid #666 !important;
}

/* move hotvideo section away from header */
.hotvideos-outer-table { margin-top: 10px !important; }

/* Timeline */
.Timeline .Body { border: none !important; text-decoration: none !important; }
.Timeline .Body a { background: #444 !important; border: 1px solid #666 !important;  }
.Timeline .Body .Selected { background: #666 !important; border-color: #999 !important; }

/* Replaced Images */
/* Google Video logo at top  */
#logoimg img, img[src^="/common/logo_video.jpg"], IMG[src="img/logo_video.gif?hl=en"], IMG[src="img/logo119x26.gif?hl=en"] {
	width: 0 !important;
