/* 20080506 */
.prev-enabled { height: 20px !important;padding-left: 21px !important;background-position: -120px 0 !important; }
.prev-disabled { height: 20px !important;padding-left: 21px !important;background-position: -120px -21px !important; }
.next-enabled { height: 20px !important;padding-left: 21px !important;background-position: -142px 0 !important; }
.next-disabled { height: 20px !important;padding-left: 21px !important;background-position: -142px -21px !important; }

/* Search results, Movers & Shakers */
.SearchResultItem .Title a:link { color: #82BDFF !important; font-weight: bold !important; }
.SearchResultItem .Title a:visited { color: #33FFFF !important; font-weight: bold !important; }
.SearchResultItem .Title a:hover, .list-item-title a:hover { color: #1689FF !important; background: none !important; }
.SearchResultItem .Title a:link b { color: orange !important; background: none !important; font-style: italic !important; font-weight: bold !important; }
.SearchResultItem .Snippet { color: #ccc !important; }
.SearchResultItem .Url {color: #DACA7D !important; }
.SearchResultItem .Details { color: #c7c5ad !important; }

/*:::::::::::: Tweaks and Adjusments :::::::::::*/

/* button text */
#upload-text a { color: GrayText !important; }
#upload-text a:hover { color: #ff6600 !important; }

/* hotstuff thingy + expand  */
.expanded-column, #hotstuff .embedded_player_container { background-color: #282828 !important; border-color: #444 !important; }
#hotstuff .embed_container { border-bottom-color: #444 !important; }
a.google-share-b { background-color: #444 !important; border: #555 !important; }
#hotstuff .thumbnail_grid { background: #282828 !important;border-bottom-color: #444 !important; }
#hotstuff .video { border-color: transparent !important; }
.video-thumbnail { border: 1px solid #ff6600 !important; }
#hotstuff .video-selected.video { background: #4F4F4F !important;border-color: #ff6600 !important; }
div.mod_content { background: #282828 !important;border-color: #444 !important; padding-bottom: 0pt !important; }

/* video search - result page */
/* [+] */
.goog-zippy-collapsed { background: transparent url(data:image/png;base64,) no-repeat 1px !important; }
/* [-] */
.goog-zippy-expanded { background: transparent url(data:image/png;base64,) no-repeat 1px !important; }

#search-results, #tv-player { background-color: #282828 !important; border-color: #444 !important; }
#tv-related, .tv-res .rl-res { border-color: transparent !important; }
.tv-res .result-highlighted { background-color: #4F4F4F !important;border-color: #ff6600 !important; }

#video-data, .zzAggregateRating, .zzRatingEdit, .zzAggregateRating, .zzAggregateRatingStat, .zzAggregateRatingStatDescription { background-color: #282828 !important; }
.related-header { background-color: #181818 !important; }
#video-data-spacer { border: 1px dotted #4f4f4f !important; }

/* vertical splitter */ 
#expander, .collapsed-column { background-color: #7f7f7f !important; border-color: #444 !important; }

/* search video */
#video-tab-container { background-color: #282828 !important; border-top-color: #666 !important; }
#comment-widget > DIV, .zzCommentDisplay, .zzCommentList { background-color: #282828 !important; border-color: #444 !important; }
.zzCommentThreadSeparator { border-color: #444 !important; }

/* tab */
.selected-tab { background-color: #282828 !important; border-color: #666 !important; }
.unselected-tab { border: none !important; }
.unselected-tab span { text-decoration: none !important; }

/* hide gooooooogle + spacer (wtf!) */
#line_spacers, .imglink, IMG[src="img/nav_previous.gif"], IMG[src="img/nav_first.gif"],IMG[src="img/nav_page.gif"], IMG[src="img/nav_next.gif"] { visibility: hidden !important; }

.rl-thumbnail-inner img, .rl-thumbnail-rollover { border: 1px solid #444 !important; }
.rl-thumbnail-inner { border: transparent !important; }

.toggle-button { opacity: 0.5 !important; }

/* mouse over */
.grid-res .rl-item-rollover, .grid-res .rl-item-rollover-no-grid { background: #4f4f4f !important; border: 1px solid #ff6600 !important; }

.Notice { border: 1px solid #666 !important; background: #EE6363 !important; /*indianred*/ color: #444 !important; }

/* Bottom "Result Page" block */
table.gooooogle td >font { display: none; }
#pagenavigatortable img { visibility: hidden !important; }

/*  Bottom "Result Page" - Selected */
#pagenavigatortable .currentpage, .currentpage { background: #666 !important; display: block !important; width: 1.7em !important; border: 1px solid #999 !important; color: #000 !important; font-size: 1.5em !important; }

/* modify the navbar links a bit */
#pagenavigatortable td, .gooooogle td { vertical-align: bottom !important; padding: .1em !important; }

/* footer search bar*/
#searchagain { background: #444 !important; border: none !important; }

}

/*::::: Google Video Advance Search :::::*/
@-moz-document url-prefix(http://video.google.com/videoadvancedsearch) {

/* font style */
td > b  { color: #fff !important; }
font b { color: #ffc !important; } 
.light-gray  { color: #fff !important; opacity: 0.5 !important; }
tr > td > b > font { color: #daca7d !important; }

/* top bars */
td { border: none !important; }

/* Tweaked frame + table - bc + border */
tr { background: #282828 !important; }

#headerdiv > TABLE > TBODY > TR { background: transparent !important; }
#headerdiv > TABLE { border: transparent !important; }

tr[bgcolor="#cbdced"] { background: #4f4f4f !important; }
table { border: 1px solid #4f4f4f !important; }

}
